/* ------- Outlook y!Glass by daskdt | daskdt.com ------- */
/*---Remix of: https://userstyles.org/styles/70654/darker-outlook-com---*/




@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.live.com"), domain("people.live.com"), domain("onedrive.live.com"), domain("account.live.com") { 





/* General Border */



* { 

border-color: #282828 !important; 

/* border-width: 1px !important; */

}



/* Header and Footer */


/* Header blue, not dropdown menus */
.t_hdbg {

  background: none !important;  

 -moz-border-bottom-colors: #282828 #000000 !important;  

 }

 

 /* 
  Under Header
  This is for the border below the header bar
  */

 

 .mlRule {

   border-bottom: 0px !important; 

   margin-bottom: -1px !important;
   
   

 }

 

 /* User Name Button up/right */

 

 .Firefox .c_nootl, .c_ic_bar  {

  border: none !important; 

  }

 

 #uxp_ftr_left {

 background: -moz-linear-gradient(#181818, #282828) !important; 

 border-bottom: 2px solid !important;  

 -moz-border-bottom-colors: #282828 #000000 !important;  

 border-top: 2px solid !important;  

 -moz-border-top-colors: #000000 #282828 !important;  

 }

 

 

 

 /* Line above Footer */

 

 #uxp_ftr_control{

 clear:both;

 border-top:0px solid #ccc !important;

 width:100%;

 font-size:86%;

 color:#FFF;

 background-color:#181818 !important; 

 }



/* Body */



body
  {
    background-image: url("http://i.imgur.com/VJitv5V.jpg") !important;
   background-repeat: no-repeat;
    background-position: left top;
       background-attachment: fixed;
    background-size: 100% auto;
  }
  
  #contentLeft, .PeoplePane, .ContentFrom,  .Attribution, #SkyscraperContent, #selMsgPane, #RightRailContainer, #messageListContainerWrapper, .SortFilterItems, .SortFilterContainer.t_mbgc, .ReadMsgBody, #readingPaneContainer0, .CL_SB_Container, .CL_SearchBox, li.CL_Row, .CP_EditActions, .ContactsContent, .CP_EditActionsContainer, #ComposeContent, .RTE, .RteToolbar, .RteEditor, .messageListContainer, .leftnav, .c-PageFooter .uxp_ftr_control,   .t_body .t_sbgc {

 background: none !important;
    

 }

  

.t_mbgc, #c_h_theme_m {

   background-color: transparent !important;

}



/* Text Colors */



.InboxTable .Fm a,.InboxTable .Ct,.InboxTable .Cg a,.InboxTable .Dt, p, .ICName, .ReadMsgSubject.TextSizeLarge, #mpf0_MsgContainer > PRE, .RichText, #mpf0_MsgContainer TD, FONT[color="white"], .LiveViewBranding, B, #mps0_htbl TD, #mps0_MsgContainer SPAN, .Title.TitleWithUpper, TD, .SenderName.TextSizeLargeLoc, .InboxTable .mlRd .Sb a, #selCount, #selAll, .ReadMsgBody, .TextLight, H3, #uxp_ftr_control ul li span, .ExternalClass, .ExternalClass div, .ExternalClass p, .UnsafeSenderEmail, LABEL, .CP_EditHeader, .cntPrfE_grpAddFldLbl, .CP_Service_Name, .cntPrfV_fldTtl, .c-ReadMessage .rmSubject  {

  color:#000 !important

  }

 

.InboxTable .mlUnrd .Fm a, .t_lnksi a {

 color:#ffffff !important; 

  transition: all .4s;

}

  

.InboxTable .mlUnrd .Fm a:hover, .t_lnksi a:hover {

 color:#fff !important; 

  text-shadow: 0 0 5px #fff;

}



/* Some Left Panel lables */

.FolderLabel, .editableLabel.readonly, .count, .ToolbarItem A {

 color:#FFFFFF !important; 
 

 }



/* Blue Text Originally */



.lnav_itemLnk, .TextSemiBold, #NewFldrLnk > SPAN, #NewCategoryLnk > SPAN, a {

 color:#FFFFFF !important;

 }



/* Backgrounds */



BODY:not(.IE) .InboxTable .mlUnsel:hover TD,BODY:not(.IE) .InboxTable .mlUnsel:hover .ia_h, .lnav_item:hover, .Caption:hover, .lnav_itemLnk:hover, .Caption:active, .lnav_itemLnk.t_sel, .MenuTop.c_m, .t_sel, div.ia_h.t_seli, ul.c_m, .t_s_hov.ia_hc.mlUnrd.mlUnsel:hover, .InboxTableBody > LI:hover, .t_sbgc .t_s_hov:hover, .t_s_hov:hover, .t_s_hov:hover .t_s_hovh, .t_s_sel, .t_s_h, .c_hb .c_c.t_hdbg .c_cc .c_mf li { 
  
  background-color: #181818 ! important;
}



#mlcm_sender > DIV > UL > LI > A:hover, .MenuTop.c_m a:hover, .c_c ul.t_hovl a:hover {

background:#333333 !important;

}



#MainContent {

 border-right: 2px solid !important; 

 -moz-border-right-colors: #FFFFFF #FFFFFF !important; 

 }

 

 .PaginationContainer , .FooterContainer {

  border-left: 1px solid #282828 !important; 

 }



/* Right Click Menu on Messages */



.MenuTop.c_m  {

 box-shadow: 0 0 15px rgba(0,0,0,.8) !important; 

 }



/* Compose */



.ComposeHeader, .cp_inputArea, .cp_ctBtn, .cp_remBtn.cp_p, 

.CL_Related, .CL_Contact_Tbl, .TextLightI.WatermarkedInput, .Name { 

background: none !important;

color: #FFFFFF !important; 

}



.cp_primaryInput.cp_anyInput, .CL_Row .Letter, .CL_User_Tile, .cntPrfE_grpCntnr input {

-moz-appearance: none !important;

 background: none !important;
  

  border: 2px solid !important; 

 -moz-border-bottom-colors: #000000 #333333 !important; 

 -moz-border-right-colors: #000000 #333333 !important; 

 -moz-border-top-colors: #000000 #282828 !important; 

 -moz-border-left-colors: #000000 #333333 !important; 

 color: #cccccc !important; 

outline: none !important; 

box-shadow: none !important; 

}





button {

-moz-appearance: none !important;

 background: none !important; 

  border: 2px solid !important; 

 -moz-border-bottom-colors: #000000 #333333 !important; 

 -moz-border-right-colors: #000000 #333333 !important; 

 -moz-border-top-colors: #000000 #282828 !important; 

 -moz-border-left-colors: #000000 #333333 !important; 

 color: #FFFFFF !important; 

outline: none !important; 

box-shadow: none !important; 

}



button:hover {

-moz-appearance: none !important;

 background: none !important; 

  border: 2px solid !important; 

 -moz-border-bottom-colors: #000000 #333333 !important; 

 -moz-border-right-colors: #000000 #333333 !important; 

 -moz-border-top-colors: #000000 #282828 !important; 

 -moz-border-left-colors: #000000 #333333 !important; 

 color: #cccccc !important; 

outline: none !important; 

box-shadow: none !important; 

}





.CL_Contact_Tbl:hover {

background-color:#181818 !important;

color: #999999 !important; 
  

}





.RteToolbar, .ReadMsgContainer, .c-PageFooter .uxp_ftr_control {

 border-top: 0px solid !important;  

 -moz-border-top-colors: #FFFFFF #FFFFFF !important; 

 }



.c_ic_tile {

padding-top: 4px !important; 

}



/* Actions Menu */



#mp0_act > DIV > UL, ul.c_m {

  background: #181818 !important;
  opacity: 1;
  

  border: 0px solid #181818 !important; 

  box-shadow: 0 0 0px rgba(0,0,0,0) !important; 

  }



#mp0_act > DIV > UL > LI > A:hover {

  background: none !important; 
  opacity: 0;
  color: #FFFFFF !important;
  }







/* Search */



.c_search_mc {

 background: none !important; 
   
  border: 2px solid !important; 

 -moz-border-bottom-colors: #FFFFFF #FFFFFF !important; 

 -moz-border-right-colors: #FFFFFF #FFFFFF !important; 

 -moz-border-top-colors: #FFFFFF #FFFFFF !important; 

 -moz-border-left-colors: #FFFFFF #FFFFFF !important; 

 color: #FFFFFF !important; 

 }

 

 .c_search_box.c_ml.c_search_a {

   background: none !important;
   color:#FFFFFF !important; 

 }

 

 .c_search_go, .PaginationContainer, .c_search_box {

  background-color: transparent !important; 
   color: #FFFFFF !important;

  }



/* Actions Menu on top - Remove Ugly Frame */

 

#c_cb0 > UL > LI > IFRAME {

 display: none !important; 

 }

/*
  Custom test area
  */
  
  /* white text color */

span.FM, span.Date, div.DtB a, div.recip_to, span.UnsafeSenderEmail, td.t_lnksi ul li a, td.t_lnksi ul li span, div.PageNavigation ul li, div#mlPinTrayMessage a, span.c_ddtxt, span.c_mcp a{color: #FFFFFF !important;}

span, div#mlRange, h1.lnav_topItemLabel, span.t_atc, span.MenuTitle
  {
    color: #ffffff;
  }

  
  input#fSubject, div.CL_Related, div.CL_User_Tile, div.CL_Display_Name, div.CL_Email, div.Name, div.Address, div#c_headerBar, div.cp_inputArea, div.CL_Related, div.cp_clist
  {
    background: none !important;
    color: #FFFFFF !important;
    border: 0px !important;
  }
 
div.c-MessageListNavigation
  {
    background-color: #181818 !important;
    
  }

  
 span.cp_name
  {
    color: #181818 !important;
  }

  div
  {
    border: none !important;
  }
  
  #MainContent { width:100% !important; }

#RightRailContainer { display: none !important; }

.WithRightRail { right: 0px !important; }
/* ----------------------------------------------- */



}



/* ----------------------------------------------- */
