url-prefix("http://profile.myspace.com/Modules/Applications/Pages/"), url-prefix("http://profile.myspace.com/index.cfm?fuseaction=music")  {

/*---------------------------- COLORS AND TABLE PROPERTIES ----------------------------*/


body {background-color: #0C0C0C !important; color:#ffffff !important;

}

table.cols, #wrap, #home_indicators, #home_bulletins, #home_messages, #StatusBox, 
#CMS_ProfileHome_Gads_AB, #home_bulletins,#section, #cols 
{background-color: #000000 !important; color: #ffffff !important;}

#wrap, #layout_home, #pagecontent 
{ border: 1px solid #222222 !important; background-color: #000000 !important;}


table, tr, td, div , #home_infoBar span, .cols, .section, .content, .buttons, h1,h2,h3,h4,h5, 
.daySection, .blacktextnb10, #betterb th, #bulletin_inbox th, #messages th, #footerCell th, 
.fieldTitle, .messageTable, .row, th, .blacktext10, #confirm p, .popup_title, #subnav
{color:#ffffff !important; background-color: transparent !important;border: 0px !important;}


 .messageMediumFont, input, textarea  {
	font-size: 11px !important;
	color: #eeeeee !important;
	border: 1px solid #222222 !important;
        BACKGROUND-COLOR: #0D0D0D !IMPORTANT;
/*---	background-image: url(http://i34.tinypic.com/4snn5v.jpg) !important; ----*/
	background-repeat: repeat !important;
	font-family:Verdana, Arial, Helvetica, sans-serif !important;
        -moz-appearance: none !important;
}


.profileimagelink {border:1px solid #222222 !important;
padding: 4px !important;}




a:link, a:active, a:visited
{color:#cccccc !important;}

a:hover
{color:#333333 !important; text-decoration:none !important;}


#home_bulletins th, #home_bulletins .cols, #home_bulletins tr, td, table, tr, th, div, span, 
form, input, textarea, .blogContent, .blogContentInfo, p 
{background: transparent !important; border: 0px !important; color: #696969 !important;}


/*---------------------------- DISPLAY NONE AND ADD REMOVAL ---------------------------*/


#advert, #SponsorIcon, #squareAd, #googlead, #ctl00_cpMain_MarketingBox_userHomeTabs_userHomeTabs, 
#marketingcontent, #grayboxrounded, #featuredprofilerounded, #adspacer, #ctl00_cpMain_GoogleAd_CMS_ProfileHome_Gads, 
#ad-wrap, #ad-hdr, #adunit, .middleAds, #gafc, .adcount1, #tkn_newhomepageadspecial1, #tkn_leaderboardDiv, 
#tkn_skyscraper, #tkn_medrec, .sideFeatured, #flipvideo_ad, #medreccol, #channel_ad, #tkn_leaderboard, #splash_mainLeft, 
#splash_mainBottom, #ctl00_ctl00_Main_cpMain_SplashDisplay_PromoMember_ABI Container, #splash_coolNewPeople, 
#ctl00_ctl00_Main_cpMain_SplashDisplay_featuredVideos_CMS_videos, #ad300x100, #home_coolNewVideos, #home_greybox, 
#home_featured_filmmaker, #abipromo, #splash_profile, #home_setHomePage, #ctl00_Main_ctl00_UserNetwork1_CMS_GeneralAnnouncement, 
