#home_featured_music, #PageThemeModule, #home_infoBar, #footer, #leaderboard 
{display: none !important;}



/*---------------------------- REMOVE MENU FRAME IMAGES ----------------------------*/

.roundTop, .roundBtm, .messagingCornerTopLeft, .messagingCornerTopRight, .messagingCornerBtmLeft, 
.messagingCornerBtmRight, #topSideNav, #topSideNav a, .eeSignUpRounded, .eeSignUpCornerTopLeft, 
.eeSignUpCornerTopRight, .eeSignUpCornerBtmLeft, .eeSignUpCornerBtmRight, .whosOnlineCornerTopLeft, 
.whosOnlineCornerTopRight, .whosOnlineCornerBtmLeft, .whosOnlineCornerBtmRight
{
	background-image:none !important; 
	background-color:#000000 !important;

}



/*---------------------------- REMOVE BACKGROUND COLOR FOR MESSAGE PAGE WHOS ONLINE NOW -----------------------*/


.woProfileLinkDiv, .woProfileWrap, .woProfile, .woProfileImageDiv, .whosOnlineScroller, 
.woProfileLinkDiv, .txtOrange, .whosOnlineTitle, .olnClear, .whosOnlineBottom, .whosOnlineTop 
{background-color: transparent!important;}



/*----------------------------- WHOS ONLINE MODIFICATION FOR MESSAGING PAGES -----------------------------*/


#blueBox a:hover 
{
	color:#FFFFFF !important;
        background-color: #222222 !important;
        display: block !important;
        padding: 4px !important;
        border: 1px solid #cccccc !important;
}


#blueBox a
{
        display: block !important;
        padding: 4px !important;
} 


#blueBox
{
	background-color: #000000 !important;
        display: block !important;

}

input {
	background-color: #222222 !important;
	border: 1px solid #000000 !important;
	color: #999999 !important;
        background-image: none !important;
}




/*---------------------------- NAVIGATION START ----------------------------*/

#topnav
{
    height: 29px !important;
    background: #2358b8 url('http://i36.tinypic.com/2h6uiwx.jpg') 0 0 repeat-x !important;
    z-index:200 !important;
    padding:0 !important;
    margin:0 !important;
    border: 1px solid #222222 !important;
}

/*---------------------------- TOP LEVEL START ----------------------------*/

#topnav ul
{
    border-right: 1px #222222 solid !important;
}

		       
#topnav ul li
{
    border-left: 1px #222222 solid !important;
    border-bottom: 1px #222222 solid !important;
}     
   
#topnav ul li a:hover
{
    background-color: #222222 !important;
    color: #FFFFFF !important;
    background: #2358b8 url('http://i36.tinypic.com/2iuqqzd.jpg') 0 0 repeat-x !important;
}   


#topnav ul li a.open small{color: #222222 !important;} 

      

#topnav ul li a.open
{
    background: #222222 !important;
    color: #ffffff !important;
    background: #2358b8 url('http://i36.tinypic.com/2iuqqzd.jpg') 0 0 repeat-x !important;
}


#topnav ul li a.open:hover
{
    background: #222222 !important;
    color: #ffffff !important;
    background: #2358b8 url('http://i36.tinypic.com/2iuqqzd.jpg') 0 0 repeat-x !important;


}


/*---------------------------- DROP DOWNS START ----------------------------*/ 
#subnav *
{
    text-transform: none !important;
    color: #ffffff !important;
    text-decoration: none !important;
    background: #2358b8 url

('http://i38.tinypic.com/2vb8y0m.jpg') 0 0 repeat-x !important;
}        
#subnav ul
{
    border: 1px #222222 solid !important;
    background: #000000 !important;
}    
        
#subnav  ul  li
{
    border-bottom: 1px #222222 solid !important;

}   
         
#subnav  ul  li.divider
{
    border-bottom-color: #222222 !important;
}			

           
       
#subnav ul li a:hover
{
    background: none #222222 !important;
    color: #ffffff !important;
    background: #2358b8 url('http://i36.tinypic.com/34gv7gp.jpg') 0 0 repeat-x !important;
}


#framer{border:none;display:none;position:absolute;}   

               






/*---------------------------- SMALLER FRIENDS AND OTHER MISC ----------------------------*/


.w100 {width:70px !important; padding:15px !important; height: 70px !important; overflow: hidden !important;}

.w100 a {font-size:9px !important; visibility: visible !important; display:inline !important; margin:-100px !important; text-align:center !important;} 

.w100 a img {clip:rect(0 70px 70px 0) !important; display:inline !important; margin:0px !important; overflow: hidden !important; padding:0px !important; border: 1px solid #222222 !important; }

#home_friends {margin-top:-13px !important; padding:0 !important;}




/*---------------------------- FRIENDS PAGES --------

--------------------*/ 


div#friends_tabs a {
	background: none !important;
    }

div#friends_tabs ul {
	margin-left: 0px !important;
	}

div#friends_tabs li {
	background: none !important;
	margin-right: 0px !important;
	}

div#friends_tabs #active {
        background-image: none !important;
    }
div#friends_tabs #active a {
    background-image: none !important;
    background-color: #222222 !important;
    }


button.submitBtn { 
	background: none !important; 
        background-color: #060606 !important;
        border: 1px solid #222222 !important;
        color: #FFFFFF !important;
	} 


.friendHelperBox {width:90px !important; padding:5px !important; height: 70px !important; overflow: hidden !important;}

.friendHelperBox a {font-size:9px !important; visibility: visible !important; display:inline !important; margin:-100px !important; text-align:left !important;} 

.friendHelperBox a img {clip:rect(0 90px 70px 0) !important; display:inline !important; margin:0px !important; overflow: hidden !important;}


}

