css:
@-moz-document domain("facebook.com"), domain("www.facebook.com"), domain("ilike.com") {

body {
    background: white !important;
    background-image: url('http://i859.photobucket.com/albums/ab158/Florencia_Salvatierra/tumblr_mewcjntIFu1rc7pm9o1_500_zpse7ae4a4a.jpg') !important;
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
    background-position: center bottom !important;
    background-size: 100% !important;
    font-size: 11px !important;
    font-family: 'arial',lucida grande,tahoma,verdana,sans-serif !important;
    color: #333 !important;
    line-height: 1.28 !important;
    margin: 0 !important;
    padding: 0 !important;
    text-align: left !important;
    direction: ltr !important;
    unicode-bidi: embed !important;
}

#contentCol {
    background-color: transparent !important;
    background-color: rgba(255, 255, 255, 0.5) !important;  
    padding-top: 15px !important;
}

.uiStream .uiStreamHeader .uiStreamHeaderChronologicalForm, .uiStream .uiStreamHeader .uiStreamHeaderText {
    background: none !important;
    padding-right: 2px !important;
    position: absolute !important;
}

.hasSmurfbar #blueBarHolder #blueBar {
    background-image: none !important;
    background-color: transparent !important;
    background-color: rgba(0, 0, 255, 0.4) !important;  
    border-bottom: 1px solid #2B60DE !important;
    min-width: 981px !important;
    position: relative;
    z-index: 300 !important;
}

._5bfg #pageNav .navLink, ._5bfg #pageNav .navItem .navLink {
  	color: white !important;
}

#pageNav._5bfw #navAccount #navAccountLink .menuPulldown {
    background-image: url(http://i.imgur.com/fR40sw7.png) !important;
    background-repeat: no-repeat !important;
    background-size: auto !important;
    background-position: -3px -20px !important;
}

.hasSmurfbar ._59fb {
    background-image: url(http://facebook.com/rsrc.php/v2/yh/r/9nS3k2yE7I2.png) !important;
    background-position: -65px -325px !important;
}

.fbChatSidebar {
    -webkit-background-clip: padding-box;
    background-color: transparent !important;
    background-color: rgba(255, 255, 255, 0.3) !important;  
    border-left: 1px solid rgba(0, 0, 0, .4);
    -webkit-box-shadow: inset 2px 0 2px -2px #B2B9C9;
    display: none;
    height: 100%;
    position: fixed;
    right: 0;
    top: 0;
    width: 205px;
    z-index: 300;
}

.fbChatSidebar .fbFeedTicker .fbFeedTickerStory {
    background-color: white !important;
    border-bottom-color: transparent;
    border-top-color: #D8DFEA;
    line-height: 13px;
    padding-right: 5px;
}

.uiSideNav .selectedItem .item, .uiSideNav .selectedItem .item:hover, .uiSideNav ul .selectedItem .subitem, .uiSideNav ul .selectedItem .subitem:hover {
    background-color: transparent !important;
    background-color: rgba(0, 0, 255, 0.1) !important;  
    font-weight: bold;
}
.uiSideNav .item, .uiSideNav .subitem {
    color: #333;
    display: block;
    border-bottom: none !important;
    min-height: 17px;
    line-height: 13px;
    padding: 3px 8px 0 28px;
    text-decoration: none;
}

.uiSideNav .item:hover {
    background-color: transparent !important;
    background-color: rgba(0, 0, 255, 0.1) !important;  
}

.fbxWelcomeBoxName {
    color: #461B7E;
    background-position: top !important;
    background-repeat: repeat-x !important;
    display: block;
    font-weight: bold;
    width: 112px;
    word-wrap: break-word;
}


.hasLeftCol #pageFooter {
	background-color: none !important;
}

#contentCurve {
    background-color: transparent !important;
    background-color: rgba(255, 255, 255, 0.5) !important;  
    border-bottom: 1px solid #CCC;
    font-size: 1px;
    height: 8px;
    margin-bottom: 8px;
}

.hasVoiceToggle #pageNav .voiceBarToggle {
	display: none !important;
}

.pagesTimelineButtonPagelet {
    background-color: transparent !important;
    border-bottom: none !important;
    min-width: 122px;
    padding: 13px 0 12px 1px;
    top: 38px;
    z-index: 4;
}

.fbTimelineScrubber {
    background-color: transparent !important;
    padding: 8px 0 8px 1px;
    top: 38px;
    width: 122px;
    z-index: 1;
}

div.fbChatSidebarDropdown .uiCloseButton {
    background-image: url(http://static.ak.fbcdn.net/rsrc.php/v2/yo/x/okE0Xwkxo7X.png);
    background-repeat: no-repeat;
    background-size: auto;
    background-color: white !important;
    background-position: -365px -20px;
    border: 0;
    height: 24px;
    width: 26px;
}

._4oes .button {
    background-color: white !important;
    border-left: 1px solid #C9D0DA;
    -webkit-box-shadow: inset 0 1px white;
    display: block;
    height: 24px;
    width: 26px;
}

.UFIRow {
    background-color: transparent !important;
    background-color: rgba(0, 0, 255, 0.1) !important;  
    margin-top: 1px;
    padding: 4px;
}

.uiHeaderSection, .uiSideHeader {
    background-color: transparent !important;
    background-color: rgba(0, 0, 255, 0.1) !important;  
    padding: 4px 6px 5px;
}

.ego_unit + .ego_unit, .ego_unit + .ego_appended_units .ego_unit {
    padding-top: 8px;
    border-top: none !important;
}

.shareRedesignText, .shareRedesignContainer, .shareRedesign .shareMediaVideo {
    background-color: transparent !important;
    background-color: rgba(0, 0, 255, 0.1) !important;  
}

#fbTimelineHeadline h2 {
    background-image: url('http://i1042.photobucket.com/albums/b430/RapsGraphics/forum/glitter-anim-001.gif') !important;
    background-position: top !important;
    bottom: 50px;
    color: #1C2A47;
    font-size: 20px;
    overflow: hidden;
    margin: 16px 0 0 206px;
}

.uiBoxGray {
    background-color: transparent !important;
    background-color: rgba(0, 0, 255, 0.1) !important;  
    border: 1px solid #CCC;
}

.fbSettingsList a.fbSettingsListLink:hover {
    background-color: transparent !important;
    background-color: rgba(0, 0, 255, 0.1) !important;  
    display: block;
    text-decoration: none;
}

.fbReminders .fbRemindersStory:hover {
    background-color: transparent !important;
    background-color: rgba(0, 0, 255, 0.1) !important;  
    padding: 3px 5px;
}

.fbxPhoto .fbPhotoImageStage .stageContainer {
    background-color: transparent !important;
    height: 453px;
}

.uiBlingBox {
    border-bottom: none !important;
    display: inline-block;
    padding: 3px 4px 3px 2px;
}

.uiBlingBox:hover {
    background-color: transparent !important;
    background-color: rgba(0, 0, 255, 0.1) !important;  
}

.groupsJumpBarTop .groupsCleanLinksBorder {
    display: none !important;
    background-color: white;
    height: 4px;
    margin-bottom: -4px;
    margin-right: 0;
    position: relative;
}

#fbProfileCover .coverNoImage {
    background: transparent !important;
    border-bottom: 1px solid #C4CDE0 !important;
    border-top: 1px solid #C4CDE0;
    height: 129px;
    position: relative;
}

}
