css:
@-moz-document domain(mint.intuit.com) {

/* LOGIN PAGE */

/* Hide everything but login */
main div.content, section.mobile-app, footer.site-footer, div.auth-bills-signup-slider, nav.main-menu, button.js-auth-slider-close-x--login {
    display: none !important;    
}

/* Make the login button more legible, give it space */
button.btn-simple-login {
 	border-radius: 0px;
    background-color: #5F5954;
    height: 60px;
    margin-top: 20px;
    font-weight: 400;
    font-size: 16px;
}

h3.auth-form-title {
    margin-bottom: 50px;    
}


form.signup-form-column:after {
    width: 0;   
}

div.auth-login-wrapper {
display: block; 
}

div.auth-signup-slider {
 	display: block;
    margin-left: 180px !important;    
    margin-right: 180px !important;    
}

form:after {
 	height :0px !important;    
}

div.signup-form-column.left {
 	border-right: none;   
}

/* Color navbar green */
div.navbar-fixed-top {
    background: #31BB31!important;  
}




/* PAGE GLOBAL STYLE */

/* Make titles Segoe caps */
.module h1 {
    text-transform: uppercase;
    font: 700 15px 'Segoe UI', sans-serif !important;
    border: 0 !important;
    height: auto !important;
}

/* Make everything Segoe */
html, body, div, span, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, code, del, dfn, em, img, q, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, var {
    font-family: Segoe UI, sans-serif !important;
}

/* TOP MENU */

/* Remove Ways to Save item from top menu */
#save {
    display: none !important;
}

/* Remove the menu from the black bar on top */
div.navbar-right ul {
    display: none !important;
}

/* Make the black bar on top green */
div.headerlinks {
    background: #31BB31!important;  
}

/* Move the total assets, debts and net worth to the top of the left column */
ul:first-child {
    margin-top: 5px;
}

.totals {
    position: absolute;
    top: 20px;
    width: 83%;
}

/* Remove tax ads from the top */
div.HypothesisTestingView, div.WelcomeMatView {
    display: none !important;
}

/* Reduce the gap between the top menu and cards/blocks */
.OverviewPageView {
	padding-top: 0px !important;
}

/* Hide unwanted blocks: Suggested offers, Investments, Ways to Save, Help sticky, Feedback, Goals  */
div.CardView.adviceWidget, div.CardView.investmentsWidget.withAFooter, div.CardView.w2sWidget.withAFooter, div.CardView.feedbackWidget, div.CardView.goalsWidget.withAFooter, div#nanoRepProxyContainer.nanorep_loadingData.nr-side-widget {
    display: none !important;
}

/* Make net worth information prominent and easy to read */

/* Hide the words Net Worth */
.net-worth {
    visibility: hidden;
}

/* Make each type of Cash account, Credit card account, etc. have a wider border */
#module-accounts ul li.moduleAccount ul li {
    padding: 8px !important;
    margin: 0 12px 0 12px !important;
}

/* Make the link to transactions for each account take up the whole rectangle for easier clicking */
#module-accounts ul li.moduleAccount ul li a.status {
    margin-right: 20px;
    margin-top: -16px;
    width: 280px;
    height: 31px;
}

/* Move Accounts down to accommodate main scores */
#module-accounts > h1 {
    margin-top: 60px;
}

/* Move the yellow exclamation in Account Type closer to the right edge */
#module-accounts ul li a.status {
    right: 7px !important;
}

/* Remove the blue question mark icons */
ul.accounts-list li.error a.status {
    background: none !important;
}

/* Space the accounts in the left column evenly vertically */
#module-accounts ul li .accounts-list {
    margin-top: 10px;
    margin-bottom: 10px;
}

/* Remove ads from under ACCOUNTS HEADERS */
a.accounts-adv {
    display: none !important;
}

/* Collapse main table border */
.overviewPage table.overview {
    border-collapse: collapse !important;
}

#module-accounts ul.totals {
    margin-top: -205px !important;
}
  
#module-accounts ul.totals li {
    padding-bottom: 8px !important;
    font-size: 18px !important;
}

/* Move the net worth amount to the top left in big and green */
#module-accounts ul.totals li.net-worth .balance {
    visibility: visible;
    position: absolute;
    top: -70px;
    font: 100 38px 'Segoe UI', sans-serif !important;
}

#module-accounts ul.totals li.net-worth {
    color: rgb(104, 188, 14) !important;
}

/* Move the entire left column down to accommodate net worth above it */
div#overview-left-column {
    margin-top: 195px !important;
}

/* Move the name Accounts down to leave room for Net Worth on the top left */
/* Padding instead of margin to keep the box shadow on the right */
#module-accounts h1 {
    padding: 120px 0 0 25px !important;
}
.module-accounts-header-buttons {
    display: none !important;
}

/* Increase the fontsize of the debt and assets amount */
.asset-total span, .debt-total span {
    font-size: 18px !important;
}

/* Change the font of all the account balances to Segoe */
span.balance:first-child {
    font-weight: 400;
    font-size: 15px;
    opacity: 0.75;
}

/* Change the font of all the top-level headings (Cash, Credit Cards, etc) to Segoe */
#module-accounts ul li h3 {
    font-family: 'Segoe UI', Segoe, Arial, sans-serif !important;
}
#module-accounts h3 > .balance {
    font-weight: 400 !important;
}

/* Remove rounded edges from Cash/Debt/Net bar chart */
ul.horizontal-bar li {
    border-radius: 0px !important;
}

/* Remove rounded edges from Monthly Net Income bar chart */
ul.vertical-bar li a {
    border-radius: 0px !important;
}

/* Remove advice */
div.advice {
    display: none !important;
}

/* BUDGETS */

/* Make vertical date line taller */
div#month-line {
    height: 525px !important;
    margin-top: -50px;
}

#module-budget-table tbody {
    margin-top: -100px;
}

/* Make current spent amount on bar more readable */
#module-budget table div.bar {
    font: 300 12px Segoe UI, 'Helvetica Neue', 'Helvetica', sans-serif !important;
}

/* Remove rounded edges from Budgets bar chart */
#module-budget table div.bar span {
    border-radius: 0px !important;
}

/* Remove rounded edges from Budgets bar chart background */
#module-budget table div.bar {
    border-radius: 0px !important;
    background: none;
    background-color: #EDEDED;
}

/* Remove Budgets table header for simplicity */
#module-budget table thead tr th.bar span, #module-budget table thead tr th.num {
    display:none !important;
}

/* Pad & Segoe budget categories */
#module-budget table tbody th a {
    font-family: Segoe UI, sans-serif;
    font-size: 15px !important;
    padding: 4px;
}

/* Increase category bar height */
#module-budget table div.bar span {
    height: 16px !important;
}


/* Remove ads */
.module-advice, .last-updated {
  display:none !important;
  }

.module-alert {
  display:none !important;
  }
  
#module-offers, #module-investments {
  display:none !important;
  }
 
  .hero, .feature, .divider, .press, .mobile-apps, .footer, #nav, #signup_button, .disclosure-triangle, #trust, .copyright, .share-link {
  display:none !important;
  }

div#body-header ul.links {
  display:none !important;
}

div#body-header {
  padding-top: 14px;
}
  
#user_auth {
      margin-top: 180px;
      width: 60%;
      margin-left: auto;
      margin-right: auto;   
      border: 0;
    }
    
#logo {
    }
    

#login_button {
      transform: scale(1.8,1.8);
    }
    
    
#header {
      margin-top: 100px;
      transform: scale(1.4,1.4);
    }
    
#credentials {
      position: relative;
      right: 30px;
      border: 0;
      transform: scale(1.3,1.3);
      }    
    
#username {
     padding:10px; 
    }

h4 .balance {
    font-size: 1.2em;
}

.account-header-wrapper h3 .balance {
    font-size: 1.1em;
    margin-left: 10px;
}

.account-header-wrapper h3 {
    padding: 0 25px 0 65px !important;
}

div#body-header ul.tabs {
    margin-bottom: 0px !important;
    text-transform: uppercase;
}


/* Make top menu headers larger */
nav.o2 .navbar-header #tabs-o2 a {
    font: 400 15px/22px 'Segoe UI', Segoe, Arial, sans-serif !important;
}

/* Make active top menu header item bold */
nav.o2 .navbar-header #tabs-o2 a.selected {
    font-weight: bold !important;
}

/* Remove space between topics on right column on Overview page */

.overviewPage .column-main > div > .module {
    margin-bottom: 15px !important;
}

/* OVERVIEW RIGHT COLUMN */

/* CREDIT SCORE */

/* Remove the word Free from Free Credit Score */
span.free {
    display: none !important;
}

/* Remove 'Next 4 Weeks' text from Upcoming Bills topic on right column on Overview page */
.supplementary {
    display: none !important;
}

/* Remove Credit Score rating */
div.rating {
    display: none !important;
}

/* Make Show Details button less prominent */
a.btn-primary {
    background: rgb(218, 218, 218) !important;
    border: 1px solid #656565;
    border-radius: 0px;
}
a.show-details {
    color: #414141 !important;
}

/* Remove dotted border */
.module-credit-score .section {
    border: 0px !important;   
}

/* Leave some room above the Credit Score section */
div.CreditScoreView {
    margin-top: 30px !important;   
}

/* Adjust the position of the credit score */
.module-credit-score .section.score .score-number {
    top: 0px !important;   
}

/* FOOTER */

/* Remove the footer on the Overview page */
.main-pages, .secondary-links {
    display: none !important;
}

/* Remove the account name, and use nickname only instead on the Overview page left column
Note: Mint has reversed account and nicknames, so now hiding nickname instead */
.nickname {
    display: none !important;
}

/* Make the nickname more readable on the Overview page left column */
.nickname {
    margin-top: -20px;
}
#module-accounts .nickname, #module-accounts a.accountName {
    font: 400 15px 'Segoe UI', sans-serif !important;
    color: #555555 !important;
}
#module-accounts ul h6 {
    color: #555555 !important;
}

/* Remove the refreshing gifs next to each individual account. TODO-BUG: This hides all accounts temporarily */
.refreshing {
    display: none !important;
}

/* Hide help widget & bottom status bar */
div.nano_sidewidget, div.AccountStatusBarView {
    display: none !important;
}


/* BILLS */

/* Match amount due with bills header size */
span.billsAmountDue {
 	color: #333 !important;  
    font-size: 1em !important;  
}

/* Remove the Upcoming Bills header */
header.dateHeader {
    display: none !important;
}

div.BillsModuleView {
 	margin-top: 35px !important;   
}


/* TRANSACTIONS PAGE */

/* Make Total Cash and Total Debt look cleaner */

table.account td.large, table.account td.money {
    font: 200 22px Segoe UI, sans-serif !important;    
}

/* Remove advice from the top */
#advice-top-container {
    display: none !important;
}

/* Increase account name size */
div#column-accounts.column ul li li small {
    font: 400 12px 'Segoe UI', sans-serif !important;
}

/* Hide Bank name, account type filter, account header from left column */
div#column-accounts.column ul a, #localnav-type {
    display: none !important;
}

/* Hide Account Header from Right column */
div#account-table div.account-header {
    display: none !important;
}

/* Make Account Summary (Cash & Debt) front and center */
div#account-table div#account-summary {
    position: relative;
    top: -50px;
    left: 65px;
    transform: scale(1.2,1.2);
}

/* Make accounts header larger */
#localnav h5 {
    font: 600 16px 'Segoe UI', sans-serif !important;
    margin-bottom: 10px;
}

/* Make transaction table font larger */
table, td, th {
    font: 400 14px 'Segoe UI', sans-serif !important;
}

/* Remove the top border from the account table */
table.account {
    border-top: none !important;
}

}
