css:
@-moz-document domain('shanbay.com') {
@font-face {
  font-family: "Avenir";
  src: local("Avenir"), url(https://s3-us-west-2.amazonaws.com/s.cdpn.io/273445/Avenir.woff) format("woff");
  font-weight: 400;
  font-style: normal; }
body { font-family: Avenir, Helvetica Neue, Hiragino Sans GB, Microsoft Yahei, sans-serif; }
#review #learning-box #notes-box ol li .content, #review .cndf, .simple-definition { white-space: pre-line; line-height: 1.5; }
body .main-body .tasks-outer-container .checkin-container .checkin-note { line-height: 1.5; }
.nav.nav-tabs.nav-stacked li a { line-height: 1.5; padding-top: 10px; padding-bottom: 10px; }
.main-menu[href="/market/store/"] { display: none; }
#review #learning_word .word h1.content, .popover-title .word, input, button, select, textarea { font-family: inherit; }
.popover { z-index: 1051; }
#review-definitions .well, #affix .well { padding: 10px; }
#review #learning-box #learning-examples-box ol li { margin-bottom: 10px; }
/* wider review */
#summary-resolved-learning-box .span9, #summary-box .span9 { width: 765px; }
body .main-body .tasks-outer-container .tasks-container .task .task-stats .task-num .task-num-total, body .main-body .tasks-outer-container .tasks-container .task .task-stats .task-num .task-num-finished { font-size: 32px; }
.btn-mini.hide[style]{ display: inline-block !important; }
}

@-moz-document url('http://www.shanbay.com/bdc/review/'), url('http://www.shanbay.com/sentence/review/') {
.navbar-fixed-top { position: fixed; background: none; pointer-events: none; }
.navbar-fixed-top .brand, .navbar-fixed-top .nav { display: none; }
.navbar-search { float: right; }
.navbar-search .input-prepend { margin-bottom: 0; margin-top: 17px; pointer-events: auto; }
.navbar-search .search-input { width: 3em; border-right: none; -webkit-transition: width 0.4s ease; opacity: .3; }
.navbar-search .search-input:focus { width: 7em; opacity: 1; }
}
