css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bugs.eclipse.org") {
  .bz_alias_short_desc_container, #titles, #header .links {
    border-radius: 0px;
  }
  #titles {
    background: url("https://projects.eclipse.org/sites/all/themes/solstice/public/images/template/breadcrumbs-large-bg.jpg") repeat scroll center center / 100% auto #1B1732;
  }
  .bz_comment_head, .bz_first_comment_head,.bz_alias_short_desc_container  {
    background: #ECECEC;
  }
  #commit_top, #commit {
    border: 0px;
    background: #F7941E;
    color: white;
    font-weight: bold;
    padding: 2px;
    cursor: pointer;
    border-radius: 3px;
  }
  #commit_top:hover, #commit:hover {
    background: #DA7A08;
  }
  body, html {
    margin: 0px;
    padding: 0px;
  }
  #header .thin-header {
    margin: 5px 5px 0px 5px;
  }

  #bugzilla-body {
    margin: 0px 5px;
  }
  
  #footer {
    background: url("https://eclipse.org/eclipse.org-common/themes/solstice/public/images/template/footer-bg.jpg") no-repeat scroll center center / cover  #242426;
    font-size: 14px;
    color: white;
    border: 0px solid;
    border-bottom: 0px;
    padding: 10px 0px;
  }
  
  #header .links {
    background: linear-gradient(#FFF, #F0EFEF 70%, #DBDBDB);
    border: 0px;
  }

  #footer a {
    color: #EEE;
    text-decoration: none;
    font-size: 14px;
   
  }
  #footer a:visited {
    color: #aaa;
  }
  #footer a:hover {
    color: #F7941E;
  }
}
