css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.digitec.ch") {
  
  #product-details .details-element { min-height: 0px; }
  #product-details .details-element .detail-element-title span,
  #product-details .details-element > .detail-element-content > ul:first-child { display: none !important; }
  #product-details .details-element > .detail-element-content > .more { display: block !important; opacity: 1 !important; }
  #product-details .detail-element-content,
  #product-details .details-information p,
  #product-details .detail-element-content .more > p:last-child { padding-bottom: 0px !important; margin-bottom: 0px !important; }
  /*#product-details br { display: none !important; }*/
  #product-details h2 { margin-top: 0px !important; padding: 8px 0 !important; }
  #product-details table.keyvalue tr.title td { padding: 4px 4px 2px !important; }
  #product-details td { padding: 0 4px !important; }
  #product-details .detail-element-content .more p { font-size: 10px !important; line-height: 12px !important; padding-bottom: 2px !important; }
  
  #product-details .details-showroom h3 { display: none !important; }
  #product-details .details-showroom ul { margin: 0 !important; }
  
  #product-details .details-used .product { height: auto !important; }
  #product-details .details-used .product-image,
  #product-details .details-used .refurbished-tag { display: none !important; }
  #product-details .details-used .product-text-box { top: 0 !important;  padding-top: 5px !important; padding: 5px !important; }
  #product-details .details-used .product-text-box .product-description { width: auto !important; }
  #product-details .details-used .product:before { left: 0 !important; }
  
  #product-details .details-variations .product:hover,
  #product-details .details-variations .product:hover .product-name strong { color: red !important; }
  #product-details .details-variations .product { width: auto !important; height: auto !important; margin: 0 !important; float: none !important; display: list-item !important; margin-left: 20px !important; overflow: visible !important; }
  #product-details .details-variations .product-image { display: none !important; }
  #product-details .details-variations .product-name { padding: 0 !important; width: auto !important; }
  #product-details .details-variations .text-only { margin-top: 0 !important; }
  
  #product-details .details-availability .more h2 { display: none !important; }
  #product-details .details-availability .detail-element-content > :first-child,
  #product-details .details-availability .more .item  { display: table-row !important; }
  #product-details .details-availability .detail-element-content > :first-child > *,
  #product-details .details-availability .more .item > * { display: table-cell !important; padding: 0 !important; }
  #product-details .details-availability .detail-element-content > :first-child > *:first-child,
  #product-details .details-availability .more .item > *:first-child {  width: 80px !important;  }
}
