css:
@-moz-document domain("personalitycafe.com") {
    
    .postbitlegacy .postfoot .textcontrols a.multiquote {background: url("http://personalitycafe.com/images/styles/PersonalityCafe/buttons/multiquote-back_40b.png.pagespeed.ce._kCjT0PnaA.png") no-repeat scroll left center rgb(30, 30, 30)}
    .postbitlegacy .postfoot .textcontrols a.report {background: url("http://personalitycafe.com/images/styles/PersonalityCafe/buttons/report-40b.png.pagespeed.ce.S0BDN2spWV.png") no-repeat scroll left center rgb(30, 30, 30)}
    .cke_skin_kama .cke_wrapper.cke_ltr {background: url("http://personalitycafe.com/clientscript/ckeditor/skins/kama/images/sprites.png") repeat-x scroll 0px -1950px rgb(70,70,70)}
    .actionbuttons .group .button {background: url("http://personalitycafe.com/images/gradients/generic_button.png.pagespeed.ce.T_11cynXtW.png") repeat-x scroll left top rgb(70,70,70); color: rgb(1, 1, 1);}
    .polloptionsblock {background: url("http://personalitycafe.com/images/misc/xpoll_question_mark.png.pagespeed.ic.dS7n8JoM-F.png") no-repeat scroll 30px 10px rgb(40, 40,40)}
    
    #navbar, #navbar2
    {
        background: rgb(24, 24, 24)!important;
    }
    
    html, body, .doc_header
    {
        background: rgb(25, 25, 25)!important;
        color: rgb(1, 1, 1);
    }
    
    #contentMain, .vbseo_liked, .textcontrols, .quickreply, .newreply, .blog, .editpost,
    .blockfoot, .blocksubhead, .popupbody, .contentBR,
    #notices .restore
    {
        background: rgb(30, 30, 30)!important;
    }
    
    .breadcrumb, ul#sidebar, .bcLeft, .bcRight,
    .foruminfo, .forumactionlinks, .forumstats, .forumlastpost, .forumrow, .bottomBarLeft, .bottomBarRight,
    .threadinfo, .threadlisthead, div.inner, .threadstats, .threadlastpost, .threadnotification, .threadpostedin, .sticky, .nonsticky, .tcatLeft,
    .forum_info_block, .options_input_block,
    .userinfo, .postdetails, .posthead,
    .blockbody, .blockrow, .navlinks, .theadrow, #pollinfo .blockfoot
    {
        background: rgb(40, 40, 40)!important;
    }
    
    .postbody, .postrow, .info
    {
        background: rgb(50, 50, 50)!important;
        color: rgb(1, 1, 1)!important
    }
    
    .quote_container {background: rgb(60, 60, 60)!important;}
    
    .socialLinks, #df2a0749b6, .pure_facebook2, #google_ads_div_PersonalityCafe_com_728x90_Top_OH_Forum, .link_unit_top, .body_wrap > center:nth-child(1), .body_wrap > div:nth-child(25) > center:nth-child(1), #pageWrapper > center:nth-child(4), .searchButton, .submitButton 
    {display: none;}
    
    #newthreadlink_top, .forumrow, #newthreadlink_bottom {border: none; box-shadow: none;}
    
    .threadbit .sticky, .threadbit, #threadlist .threadlisthead, .threadbit .nonsticky, .forumbit_post .forumrow, .thead_bar .theadrow,
    .forum_info .blockhead, .forum_info .blockbody, .footer,
    #sidebar_container > ul,
    .newcontent_textcontrol, #vbseo-likes
    {
        border-color: rgb(70, 70, 70) rgb(70, 70, 70) rgb(70, 70, 70);
        box-shadow: none;
        color: rgb(1, 1, 1);
    }
    
    .loginInput, .passInput, .searchinput,
    .pagination span a, .pagination span a.popupctrl, .popupbody li.formsubmit, #vbseo-likes:hover,
    .vbseo-likes-container, ul.vbseo-likes-list, vbseo-likes-active, .vbseo-likes-count
    {
        background: rgb(70, 70, 70);
    }
}
