css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.cielotv.it/streaming.html") {
    .col-sm-8 { width: 100%; }
    .vSkyContainer, .vSkyVideo, .vSkyIframe { width: 1000px; height:670px; }
    .col-sm-4 { display:none; }
    html body #page.streaming .premium { padding-bottom: 0px; }
    body { background-color: #222; }
    html body #page, #topbar > .topbarwrapper, #navbar, #footer .pagewidth {background-color: #333C41; box-shadow: none;}
    html body #page.streaming .maincontent, .nav > li > a:hover, .nav > li > a:focus { background-color: #38BAD1; color: #FFF; }
    #footer .footertext { border-top: 1px solid #38BAD1; }
    #navbar { margin-bottom:0!important; }
    .pull-right { visibility: hidden; }
    #topbar > .topbarwrapper > .navbar-nav .assistenza > ul > li .dropdown-toggle { padding-top: 11px; padding-bottom: 11px; }
/* v2.0 styled by acolombo @ www.acolombo.eu */
}

