css:
#TabsToolbar {
    background: transparent !important;
    color: #FFFFFF !important; 
    font-weight:normal!important;
    text-shadow: black 0px 0px 5px !important;
    border: none !important;
    border-radius: 2px 0 0 0 !important;
    margin: 0 !important;}

.tabbrowser-tab {
    background: transparent !important;
    color: white !important;
    text-shadow: black 0px 0px 8px !important;}

.tabbrowser-tab:hover {
    background: transparent !important;
    color: white !important;
    -moz-transform: scale(1.03) !important;
    text-shadow: black 5px 5px 8px !important;}

.tabs-newtab-button {
    background: transparent !important;
    box-shadow: inset 0px 0px 0px rgba(255,255,255,.5) !important;}

.tabs-newtab-button:hover {
    background-image: -moz-linear-gradient(rgba(0,0,0,0), rgba(255,255,255,.05) 95%),
    -moz-linear-gradient(rgba(255,255,255,0.0), rgba(255,255,255,.5) 95%)  !important;}

.tabbrowser-tab[pinned], .tabbrowser-tab[pinned]:not([selected]){
    margin-top: 2px !important;
    margin-bottom: 1px !important;
    margin-right: 0px !important;
    background: transparent!important;}

#browser-bottombox, #status-bar, #status-bar > *, #nav-bar, #PersonalToolbar, #addon-bar, #main-window[tabsontop="false"] #TabsToolbar:nth-last-child(1n+2),
    #main-window[tabsontop="false"] #PersonalToolbar[collapsed="false"] + #TabsToolbar,
    #main-window[tabsontop="false"] [id^="__customToolbar"]:not([collapsed="true"]) ~ #TabsToolbar {
	 background: transparent !important;
	 color: #FFFFFF !important;
	-moz-appearance: none !important;}

.chromeclass-directories *, #bookmarksPanel * {
    color: white !important;
    text-shadow: #5a5a5a 1px 1px 2px !important;}
#main-window[inFullscreen="true"]{
    -moz-appearance: -moz-win-borderless-glass !important;
    background: none !important;}
#main-window[stylish-url="about:blank"]:not([onclose^="PrintUtils"]):not([title^="View Source Chart"]) #content browser:not([src]){ 
    opacity: 0 !important;}

#urlbar {
    background: transparent !important;
    color: #FFFFFF !important;
    font-weight:normal!important;
    text-shadow: grey 1px 1px 5px !important;
    border: none !important;
    border-radius: 2px 0 0 0 !important;
    margin: 0 !important;
    box-shadow: inset 0px 0px 3em 0px rgba(255,255,255,.5) !important;}

#urlbar:hover {
    background: transparent !important;
    color: #FFFFFF !important;
    text-shadow: #000000 1px 1px 5px !important;
    font-weight:bold!important;
    box-shadow: inset 0px 0px 5em 0px rgba(255,255,255,.5) !important;}

#searchbar > * {
    background: transparent !important;
    color: #FFFFFF !important;
    font-weight:normal!important;
    text-shadow: grey 1px 1px 5px !important;
    border: none !important;
    border-radius: 2px 0 0 0 !important;
    margin: 0 !important;
    box-shadow: inset 0px 0px 3em 0px rgba(255,255,255,.5) !important;}
    
#searchbar:hover > *, .findbar-textbox:hover {
    background: transparent !important;
    color: #FFFFFF !important;
    text-shadow: black 1px 1px 5px !important;
    font-weight:bold!important;
    box-shadow: inset 0px 0px 5em 0px rgba(255,255,255,.5) !important;}

toolbarbutton.bookmark-item:hover {
  -moz-appearance: none !important;
  box-shadow: black 1px 5px 5px !important;
  -moz-transform: scale(1.05) !important;
  border: none !important;
  margin: 0px 4px 3px 4px !important;
  background: transparent !important;}

tooltip {
  -moz-appearance: none !important;
  border: none !important;
  color: #ffffff !important;
  text-shadow: black 1px 1px 5px !important;
  background-color: rgba(0,0,0,0.20) !important;}

popup:not(#autoscroller), menupopup, context-menu {
    -moz-appearance: -moz-win-glass !important; 
    background: white !important;
    padding: 0 0 6px 0 !important;}

menubar > menu, menupopup > menu, popup > menu, menuitem, toolbarbutton {
    color: #FFFFFF !important;
    text-shadow: black 1px 1px 5px !important;}

menuseparator {
    -moz-appearance: none !important; 
    padding: 0 1px 1px 1px !important;
    opacity: 0.3 !important;
    background: black !important;}

#identity-popup {
    background-color: transparent !important;
    text-shadow: black 3px 3px 5px !important;}

#identity-popup-container {
    border: 1px solid threedshadow !important;
    color: black !important;
    background-color: white !important;}

#identity-popup:hover {
    background-color: transparent !important;}

#bookmarks-view :-moz-tree-row(hover){
    background: black !important;}

/*Removes Background-Color From Chrome*/
#appcontent,
#appcontent > #content,
#appcontent > #content > tabbox > tabpanels {
  background-color: transparent !important;}

/*Fixes Aero Border*/
#main-window[stylish-url="about:blank"]:not([onclose^="PrintUtils"]):not([title^="Source Chart"]):not([chromehidden="menubar toolbar directories extrachrome "]) #appcontent { 
  -moz-appearance: none !important;}

/*For Transparency In Fullscreen*/
#main-window[inFullscreen="true"]{
  -moz-appearance: -moz-win-borderless-glass !important;
  background: none !important;}

/*Sets Content Transparent*/
#main-window[stylish-url="about:blank"]:not([onclose^="PrintUtils"]):not([title^="Source Chart"]):not([chromehidden="menubar toolbar directories extrachrome "]) #content browser:not([src]){ 
   opacity: 0 !important;}
