css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document

regexp("https?://kickass.torrents.rocks/.*"),
domain("kickasstorrents.hd-download.net"),
regexp("https?://kickasstorrent.link/.*"),
domain("kickasstorrent.proxyindex.co"),
domain("kickass-torrent.ytsre.eu"),
domain("kickasstorrents.come.in"),
domain("kickass.proxyindex.net"),
domain("kickass.unblocked.la"),
domain("kickassunblock.info"),
domain("kickasstorrents.com"),
domain("kickasstorrents.video"),
domain("kickasstorrentsan.com"),
domain("kickasstorrents.ee"),
domain("kickasstorrents.to"),
domain("kasssto.come.in"),
domain("kickass.hid.im"),
domain("kickassto.co"),
domain("katproxy.com"), 
domain("kurs-pnf.eu"), 
domain("katproxy.is"), 
domain("kickass.cd"),
domain("kickass-cr.online"),
domain("kickass.mx"),
domain("kickass.so"),
domain("kickass.to"),
domain("thekat.tv"),
domain("katcr.co"),
domain("katcr.to"),
domain("kat.cr"),
domain("katcr.co"),
domain("kat.ph"),
domain("ka.tt") 

{

html, header, .headmenu, .headmainpart, #navstyle, .iverify,
.ui-menu-item, .darkButton, .icomment, .idownload,
.imagnet, .prevAV, .commentbody, .showmore,
.textareaRecommendation, .qrate,
.bbedit-toolbar, .profileCloud, #fancybox-content,
.forumGroupName:hover, .accentbox,
.messagerow div, .quoteAuthor, .pollbar,
.commentHeadLine, .commentHeadLine:hover, 
.iaconbox a, .ka, .ka:hover, #menu, .menuValue,
.kaGiantButton, .buttonsline a,

.kaGiantButton.siteButton .ka::before
{
background: #2051A0 !important;
}
#fancybox-close span, .ajaxLink,
.imessage span, .ireply span, .redButton span,
.irategood span, .iuserplus span, .iratebad span,
.iuserminus span, nav, .dropdown, .dropdown li a
{
background-color: #2051A0 !important;
}
.commentcontent
{
border: 1px #2051A0 solid !important;
}
.search
{
border-top: 1px #2051A0 solid !important;
border-right: 1px #000000 solid !important;
border-bottom: 1px #2051A0 solid !important;
border-left: 4px #000000 solid !important;
}
.mainpart, .data td, .previewcomment,
.comareajs, .quote, .search,
.commentcontent, textarea, .pollresultsblock,
.pollbarwrapper, .holder, .ui-corner-all,
.dropdown li a:hover, #navigation li a:hover, 
#sidebar #latestForum li:hover,
#sidebar #latestNews li:hover,
#sidebar #blogroll li:hover,
#sidebar #goodies li:hover,
#sidebar #latestSearches li:hover,
#sidebar #friendsLinks li:hover,
#sidebar .advertising .legend, 
.bottom-menu-block, .land-search-input
{
background: #000000 !important;
}
.bbedit-toolbar
{
background: -moz-linear-gradient(#2051A0, #FFFFFF )!important;
}
.land-content
{
background: -moz-linear-gradient(#000000, #2051A0 )!important;
}
.search input
{
border: 1px #000000 solid !important;
}
#searchTool button
{
border: none !important;
}
.seedBlock, .leechBlock
{
color: #FFFFFF !important;
padding: 2px !important;
background: #2051A0 !important;
border-radius: 0 25px 25px 0 !important;
border: 1px #2051A0 solid !important;
}
.cellMainLink strong
{
color: #FFFFFF !important;
padding: 2px !important;
background: #2051A0 !important;
border-radius: 25px !important;
border: 1px #2051A0 solid !important;
}
.communityLayout .cellMainLink strong
{
color: #FFFFFF !important;
padding: 2px !important;
background: #000000 !important;
border: 1px #000000 solid !important;
}
.siteButton, .searchButton, button, .buttonPic,
.postLink, .ireport, .iremfrombook, .feedbackButton,
.commentbody .icon16 span, .sidebarCell .icon16 span, .badge 
.icon16 span, .textButton, h2 .icon16 span, .dataList 
.icon16 span, .kaButton
{
color: #FFFFFF !important;
background: -moz-linear-gradient(#2051A0, #000000)!important;
border: 1px #2051A0 solid !important;
text-shadow: none !important;
}
.folderopen, .folder,
.torrentFileList, .torTree, .torFileIcon, .torFileName, .torFileSize
{
background: #2051A0 !important;
border-top: 1px #000000 solid !important;
border-bottom: 1px #000000 solid !important;
}
.tagcloud
{
background: -moz-linear-gradient(#2051A0, #000000, #2051A0)!important;
}
input, select
{
color: #FFFFFF !important;
background: #000000 !important;
border: 1px #2051A0 solid !important;
}
.quicksubmit
{
outline: none !important;
}
.ui-widget-content a
{
background: #2051A0 !important;
text-shadow: none !important;
}
.ui-widget-content a:hover
{
background: #000000 !important;
text-shadow: none !important;
}
#searchTool .ka
{
background: #000000 !important;
}
#sidebar #latestForum li p:hover,
#sidebar #latestNews li p:hover,
#sidebar #blogroll li p:hover,
#sidebar #goodies li:hover,
#sidebar #latestSearches li p:hover,
#sidebar #friendsLinks li a:hover
{
text-decoration: underline !important;
}
#previewPopupContainer #previewPopupContent, .previewPopupContainer 
.previewPopupContent
{
text-shadow: none !important;
}
h1, h2, h3, h4, h5, h6, b, p, #search_box,
a, .lightgrey a, .lightgrey, .dataList, .dataList li, .data td,
.width100perc, .nopad, button span, input, .textcontent li,
a[href], .advertBanner a, .commentAVRate, .comareajs,
.torrentRating, .torrentRating span, .quote,
.commentText, .botmarg5px, .topmarg5px, form,
.repValue, .positive, strong, .formtable, .accentbox,
.textcontent, .botmarg10px, .data th, .lightgrey,
.commentcontent, .quicksubmit, li, i, p, .explanation,
.aclColor_4, .aclColor_4 a, .aclColor_4.aclColor_verified
{
color: #FFFFFF !important;
}
}
