css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vipracing.tv"), domain("vipracing.info"), domain("vipracing.live"), domain("vipracing.icu"), domain("vipracing.vip") {
  hr {
    border-color: #555 !important;
    margin-top: 60px !important;
    margin-bottom: 60px !important;
    outline: 1px dashed #333;
  }

  a:focus {
    outline: none;
  }

  .input-group .form-control {
    background-color: #555 !important;
    color: #CCC !important;
    border-color: #333 !important;
  }

  .input-group-addon {
    background-color: #333 !important;
    color: #555 !important;
    border-color: #333 !important;
  }

  .col-xs-12 {
    margin: 0px !important;
    padding: 0px !important;
    width: 100%
  }

  .col-sm-9 {
    width: 100% !important;
    padding-top: 60px !important
  }

  .navbar {
    border-radius: 0px !important;
    padding-left: 10px !important;
    padding-right: 10px !important;
  }

  .in-video-adds, .col-sm-3, .rj-publi:before, .text-right {
    display: none !important;
  }

  img {
    display: none;
  }

  /* v1.5 styled by acolombo @ www.acolombo.eu */
}

@-moz-document url("http://vipracing.net/programacion/programacion.html") {
  body {
    background-color: #000;
  }

  table {
    width: 100%;
  }

  input[type="button"] {
    font-family: arial, sans-serif;
    font-size: inherit;
    color: inherit;
    background: none;
    cursor: pointer;
    padding: 25px 80px;
    display: inline-block;
    margin: 8px 0px;
    text-transform: uppercase;
    letter-spacing: 1px;
    font-weight: 700;
    -webkit-transition: all 0.3s;
    -moz-transition: all 0.3s;
    border: 3px solid #fff;
    color: #fff;
    width: 100%;
  }

  input[type="button"]:hover, input[type="button"]:active {
    color: #000;
    background: #fff;
  }

  /* v1.5 stiled by by acolombo @ www.acolombo.eu */
}

@-moz-document domain("byetv.org") {
  #g207 {
    display: none !important;
  }
}

@-moz-document domain("deportesmax.info") {
  #nf37 {
    display: none !important;
  }
}

@-moz-document domain("thezoo101.blogspot.com") {
  #wrap2 {
    border: 0px solid #000;
    color: #555;
  }

  body, #outer-wrapper, #wrap2 {
    background-color: #000;
  }

  /* v1.5 styled by acolombo @ www.acolombo.eu */
}
