css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("habrahabr.ru") {
/*Navigation panel*/
#navbar {
    left: 0;
    border: 1px solid #ccc;
    border-radius: 4px;
    box-shadow: 0px -1px 10px rgba(50, 50, 50, 0.2);
}
#nav-pages {
    position: relative;
    left: 50%;
    margin-top: -33px;
}
.page-nav ul.next-prev li:last-child {
    float: right;
    margin-right: 0;
}
a.tab_user {
    position: absolute;
    bottom: 0;
}
#header .userpanel {
    width: auto;
}
/*Common*/
#layout {
    width: 1350px;
    background: none;
}
#layout .inner {
    width: 1350px !important;
    padding-top: 0;
    margin-left: 0;
    margin-right: 0;
}
table.menu {
    box-shadow: 0px -1px 10px rgba(50, 50, 50, 0.2);
    margin: 0 0;
    padding: 0;
}
table.menu td.item, .content_left.wide.formenuwidth table.menu td.item {
    width: auto;
}
div.submenu {
    box-shadow: 0px 1px 10px rgba(50, 50, 50, 0.2);
    margin: 1px 0;
    padding: 0;
}
.sidebar_right{
    float:right;
    margin-top:6px;
    border: 1px solid #ccc;
    border-radius: 4px;
    box-shadow: 0px -1px 10px rgba(50, 50, 50, 0.2);
    padding: 5px;
}
.footer_panel, .rotated_posts {
    width: 1024px !important;
    left: 0
}
.buttons button:disabled, .buttons a.button:disabled, .buttons input:disabled {
    background-color: #ddd !important;
}
/*Feed & post pages*/
.post {
    background: #fff;
    border: 1px solid #ccc;
    border-radius: 4px;
    box-shadow: 0px -1px 10px rgba(50, 50, 50, 0.2);
    margin: 10px 0 !important;
    padding: 20px;
}
#comments {
    background: #eee;
    border: 1px solid #ccc;
    border-radius: 4px;
    box-shadow: 0px -1px 10px rgba(50, 50, 50, 0.2);
    margin: 10px 0;
    padding: 20px;
}
#comments .title, #comments_form_placeholder .title {
    background-image: url("http://habrastorage.org/files/37e/423/7cc/37e4237cc5814ceebd2caba56653eb72.png")
}
.comments_list .comment_item .message.bad5, .comments_list .comment_item .message.bad4, .comments_list .comment_item .message.bad3, .comments_list .comment_item .message.bad2, .comments_list .comment_item .message.bad1{
    background-color: transparent !important;
}
.comments_list .comment_item .info .folding-dot {
    background: url("http://habrastorage.org/files/479/f00/1ed/479f001ed9a24c33a1e09e0831f4db93.png") repeat-x scroll 5px 0 rgba(0, 0, 0, 0);
}
.comments_list .comment_item .info.is_topic_starter {
    background-color: #ddd
}
.comments_list .comment_item .info.is_author, .conversation_page .messages .message.my .info {
    background-color: #ded;
}
.comments_list .comment_item .info.is_new {
    background-color: #dde;
}
#comments_form_placeholder {
    background: #eee;
    border: 1px solid #ccc;
    border-radius: 4px;
    box-shadow: 0px -1px 10px rgba(50, 50, 50, 0.2);
    margin: 0 0;
    margin-top: 20px;
    padding: 10px;
}
/*profile pages*/
.user_profile, .user_notes, .peoples_list, .conversations, .user_settings {
    background: #fff;
    border: 1px solid #ccc;
    border-radius: 4px;
    box-shadow: 0px -1px 10px rgba(50, 50, 50, 0.2);
    margin: 0 0;
    padding: 15px;
    width: auto;
    
}
.user_profile .companies_list, .user_profile .hubs_list {
    box-shadow: unset !important;
    border: none !important;
    padding: 0 0 !important;
}
.conversation:hover {
    background-color: #eee !important;
}
/*Tracker pages*/
#tracker_feed_form, #tracker_folowers_form {
    background: #fff;
    border: 1px solid #ccc;
    border-radius: 4px;
    box-shadow: 0px -1px 10px rgba(50, 50, 50, 0.2);
    margin: 0 0;
    margin-top: 20px;
    padding: 10px;
}
/*Info pages*/
.ppa, .info_page {
    background: #fff;
    border: 1px solid #ccc;
    border-radius: 4px;
    box-shadow: 0px -1px 10px rgba(50, 50, 50, 0.2);
    margin: 0 0;
    padding: 15px;
    width: auto;
}
.info_page div, .info_page a.river{
    width: auto;
    margin: auto !important;
    margin-bottom: 20px !important;
}
.malyava .form, .malyava .form .item, .malyava .form .item .editor .text-holder {
    background-color: #fff !important;
    width: auto !important;
    margin: auto !important;
}
/*Company blogs and posts*/
#layout .wrapper {
    width: 1064px !important;
    padding: 10px;
    margin-left: 0;
    margin-right: 0;
}
#layout .wrapper .inner {
    width: auto !important;
    
}
.wrapper .content_left {
    width: 692px !important;
}
