css:
@-moz-document domain(myfantasyleague.com) {
body {
	background: none;
}



.eventablerow {
	background-color: #e8e8e8;
}

.eventablerow td {
  background-color: inherit;
  }
  
    

/* header */  
#menu0 {
  background-color: #263e68;
  width: 100%;
  height: 60px;
  }

.milonictable {
  margin: 0 auto ;
  }


.milonictable a {
  padding-top: 20px;
  padding-right: 20px;
  }

#simg0, #simg1, #simg2, #simg3, #simg4, #simg5, #simg6 {
  display: none;
}
  

td.brandlogo {
	display: none;
}
    
td.bannerimage {
	display: none;
}
    
td.welcome {
	position: absolute !important;
	right: 0px;
  z-index: 9999;
  font-size: 9px;

}

.pagetitle h1{
   display: none;
  }

.pageheader {
  margin: 0px;
  }

.leaguelogo {
    display: none;
  }

/* footer */
.pagefooter {
      display: none;
}

/* body */

.pagebody {
    width: 80%;
    margin: -40px auto 0;
  }

#hsubmenu {
    margin-bottom: 30px;
  }

#homepagetabs {
 	margin-bottom: 20px;
}

#homepagetabs #tab1 {
  text-indent: -9999px;
  line-height: 0; /* Collapse the original line */
}

#homepagetabs #tab1::after {
  content: "Matchups";
  text-indent: 0;
  display: block;
  line-height: 1.2em; /* New content takes up original line height */
}


#vsubmenu {
  float: right;
  width: 15%;
  margin-top: 100px;
}

#vsubmenu ul {
  width: 100% !important;
  }

#vsubmenu ul li {
  width: 100% !important;
  }

#vsubmenu ul li a {
  width: 90% !important;
  margin: 0 auto;
  }

#withmenus {
    margin-left: 0px !important;
  	width: 80%;
  	margin-top: 75px;
  	position: relative;
  }


#tabcontent0 > :first-child {
	display: none;
}

#tabcontent0 #homepagecolumn2 {
 	display: none;  
}

.report {
   	padding: 15px 0px;
    border: none;
  }


@media only screen 
and (max-width : 1410px) {
	.report {
    	font-size: 11px;
    }
}
  
}
