css:
@-moz-document domain("urbandictionary.com") {

    * {font-family: calibri !important; padding-top: 0px!important; padding-left: 0px!important;}
    a {color: rgb(150, 150, 150) !important; text-decoration: none !important}
    a:hover, .pagination>a:hover {text-decoration: underline !important; background: rgb(25, 25, 25) !important}
    .container {margin-left: 15px;}
    .row {margin-left: 20px;}
    .small-10 {margin-left: -250px; padding-bottom: 10px; color: rgb(70, 70, 70);}
    .autocomplete {box-shadow: none; border: 1px solid rgb(70, 70, 70); transition: none;}
    .meaning, .example {color: rgb(70, 70, 70)}
    .form-control:focus, .def-panel {box-shadow: none; border-color: rgb(80, 80, 80);}
    .pagination > .current > a{background:none !important;}
    
    div#term
    {
        display: inline !important;
    }
    
    #dark-top, #left-column, #nearby_titles, .col-md-2, .col-md-4, .app-banner a, .modal, div.columns.small-6.large-2.large-push-7.text-right.main-actions,
    .btn, .look_up, .tt-dropdown-menu, .full-width-nav, #logo, .large, .thumb.up, .thumb.down, .right-link, .buy,
    #navbar1,
    #nav2,
    .mission,
    #copyright,
    .share, .author, .contributor, .bar, .footer, .box .ribbon, .box .right-links, .counts,
    .tags, .random_button, .dfp, .twitter-timeline, .text-center, .icon-ud_speaker, .sticky, .three-columns > div:nth-child(2) > div:nth-child(2), .tags-panel, .ad-panel, .def-panel .ribbon,
    #urban-top-bar, .large-4, .def-footer, #dark_top, .def-store.is-test.bb-mug
    {
        display: none !important;
    }
    
    body, .home_controller, .three-columns, .box, .autocomplete, .def-panel
    {
        background: rgb(25, 25, 25) !important;
        color: white;
    }
}
