css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.video2mp3.net") {

  /*BG inner*/
  #hero {
    overflow: auto;
    padding-top: 35px;
    background-color: #000;
}
  
  /*BG outer*/
  
  
  
  /*pre-footer*/
  .reverse-content {
    background-color: #000;
}
  
  /*real-footer*/
  #footer {
    display: none !important; /* ups social media stuff is gone*/
}
 
  /*slide ads begone*/
  div#visadd_slider_window.visadd_layer_window {
    display: none !important;
  }
 
  
  /*swf placeholder*/
  div#dp_swf_engine {
    display: none !important;
  }
  
  /*header*/
  #navigation {
    background-color: #000;
    overflow: auto;
    border-bottom: 1px solid #000;
    padding: 0px 15px;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.1);
}
  
  /*main BG*/
  body {
    font-family: Arial,sans-serif;
    font-size: 14px;
    color: #3F3F3F;
    background-color: #000;
}
  
  /*remove useless text*/
  p {
    /*display: none !important;*/
  }
  
  div#home-intro.mobile-box {
    display: none !important;
  }
  
  p.clear-left {
    display: none !important;
  }
  
  div.content-panel > p {
    display: none !important;
  }
  
  
  ul.float-left.clear-left {
    display: none !important;
  }
  
  /*remove social media junk*/
  div#social-box.mobile-box {
    display: none !important; /*yes*/
  }
  
  /*convert-box*/
  .convert-box {
    padding: 2%;
    background-color: #000;
    border-radius: 10px;
    overflow: auto;
    margin-bottom: 15px;
    margin-right: 330px;
}
  
  /*convert input field*/
  input#convert-input {}
  
  
  
  /*convert button*/
  .button {
    background: -moz-linear-gradient(center bottom , #151515 0%, #252525 100%) repeat scroll 0% 0% transparent;
    border: 1px solid #0f0;
    color: #0F0; /*text*/
    padding: 10px 20px;
    border-radius: 5px;
    cursor: pointer;
    font-size: 14px;
    text-decoration: none;
    display: inline-block;
    text-align: center;
    text-shadow: 1px 1px 8px #0f0;
}
  
  /*social media nav right side floating*/
  #footer #fixed-social {
    position: fixed;
    top: 100px;
    right: 0px;
    background: none repeat scroll 0% 0% rgba(0, 0, 0, 0.8);
    border-radius: 10px 0px 0px 10px;
    padding: 10px;
}
  
  /*after conversion ads*/
  div#news-plug {
    display: none !important;
  }
  
  div#hero.content-panel > h1 {
    display: none !important;
  }
  
  div#hero.content-panel > br {
    display: none !important;
  }
  
  /*buttons in nav*/
  /*removed example URL*/
  .convert-box span.help-text {
    display: none; /*yes*/
  }
  /*removed feature infos/ we know the, already, duh*/
  ul.ul-checks {
    display: none; /*yes*/
  }
  div#home-icons {
    display: none;
  }
  div.content-panel > h2 {
    display: none;
  }
  /*URL box layout*/
  .convert-box input[type="text"] {
    width: 80%;
  }
  .convert-box {
    padding: 2%;
    background-color: #000;
    border-radius: 10px;
    overflow: auto;
    margin-bottom: 15px;
    margin-right: 60px;
}
  a.pill { /*unselected*/
    display: inline-block;
    padding: 6px 15px;
    color: #FFF;
    font-weight: bold;
    text-decoration: none;
    background-color: #010;
    border-radius: 3px;
}
  a.pill-at { /*current tab*/
    background-color: #030;
}
  /*removed text/ads afterwards*/
  #social-share {
    display: none;
  }
  /*border*/
  .convert-box input[type="text"] {
    float: left;
    padding: 0px 12px;
    font-size: 14px;
    border: 1px solid #0f0;
    border-radius: 10px;
    width: 75%;
    margin-right: 1%;
    height: 45px;
  }
}
