css:
@-moz-document regexp("https://(greasy|sleazy)fork\\.org(?!.*?/forum/(plugin|dashboard)).*") {
  /* GLOBAL *******************************************************************/
  #Content a[href$="//*[[nick]]*/"],
  a[href$="/*[[nick]]*/"] {
    color: /*[[nickcolor]]*/;
    font-weight: bold;
  }
  html,
  body,
  .script-list,
  .user-list,
  .text-content {
    border: none;
    box-shadow: none;
    padding: 0;
    margin: 0;
    background-color: /*[[bgcolor]]*/
  }
  :target {
    outline: 1px solid #ffc000;
  }
  #main-header,
  #Head {
    background-color: #123 !important;
    background: linear-gradient(#123 48px, #234 48px) !important;
    border-bottom: 4px solid #506880;
    height: 64px;
    margin-bottom: 64px;
  }
  #site-name {
    position: absolute;
    z-index: 999
  }
  #site-name img {
    border: 2px solid #fff;
    border-radius: 50%;
    box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.5);
  }
  #main-header #site-name a,
  #main-header #site-name h1,
  #Head #site-name a,
  #Head #site-name h1 {
    min-height: 96px;
    margin-bottom: -24px;
    background: -webkit-linear-gradient(-80deg, #A9BED2, #2D3C4E);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    color: #4B5867;
    text-shadow: 1px 1px 10px rgba(0, 0, 0, 0.3);
  }
  #Head .subtitle,
  #main-header .subtitle {
    text-shadow: none;
    color: rgba(0, 30, 50, 0.5);
  }
  .sign-out-link,
  #Head #nav-user-info {
    color: rgba(255, 255, 255, 0.2);
  }
  #Head #nav-user-info li:nth-child(2) a,
  #main-header .sign-out-link a {
    color: rgba(255, 255, 255, 0.4);
  }
  #site-nav nav,
  .SiteMenu {
    display: flex;
    top: 44px;
    height: 24px;
  }
  nav li,
  .SiteMenu li {
    padding: 0 1ex;
    background-color: rgba(255, 255, 255, 0.15);
  }
  li.nav-search {
    padding-right: 0px;
  }
  #Head .SiteMenu li a,
  #main-header #site-nav li a {
    color: rgba(215, 245, 255, 0.8);
  }
  body {
    font-size: 12pt
  }
  body,
  .SiteTitle,
  select,
  input {
    letter-spacing: 0
  }
  a {
    color: #147;
    text-decoration: none
  }
  a:hover:not(.Button),
  #Head #nav-user-info a:hover,
  #Head .SiteMenu a:hover,
  #Head #site-nav a:hover {
    text-decoration: underline;
  }
  a:visited:not(.Button) {
    color: #578
  }
  #nav-user-info {
    font-size: medium
  }
  #language-selector-locale {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    background-color: rgba(0, 0, 0, 0.3);
    color: rgba(215, 245, 255, 0.5);
    border: none;
    min-width: calc(14em + 16px + 1ex);
    padding-left: 1ex;
    outline: none!important;
  }
  #language-selector-locale option {
    background: #123!important
  }
  #script-search input[type="search"] {
    min-width: 13em;
    background-color: transparent;
    border: none;
    height: 24px;
    padding: 0 0.5ex;
    color: white;
    outline: none!important;
  }
  #script-search input[type="search"]:-webkit-input-placeholder {
    color: rgba(215, 245, 255, 0.5);
  }
  #script-search input[type="search"]:-webkit-autofill {
    -webkit-box-shadow: 0 0 0 50px #42515F inset;
    -webkit-text-fill-color: white;
  }
  .expander {
    background-color: #cde;
    color: #123
  }
  .width-constraint > .notice {
    text-align: center;
    display: block;
    margin: 0;
    font-weight: bold;
    background-color: #ffd500;
    border-left: none;
    line-height: 1.1;
  }
  pre,
  code {
    padding: 1px 8px 3px;
    font-size: 9.5pt;
    line-height: 1.2;
    background: #F7F7EF;
    border: 1px solid #D2D2C7;
  }
  pre code {
    white-space: pre-wrap
  }
  /* more menu on top */
  .SiteMenu nav {
    background-color: #234;
    margin-top: 0;
    z-index: 10000;
  }
  .SiteMenu nav li {
    padding: 0;
  }
  #site-nav .with-submenu nav {
    display: none;
  }
  #site-nav .with-submenu nav,
  #site-nav .with-submenu nav li {
    background-color: #234;
  }
  #site-nav .with-submenu nav li:last-child {
    padding-bottom: 1ex;
  }
  #site-nav .with-submenu a:hover + nav,
  #site-nav .with-submenu nav:hover {
    display: block;
    margin-top: -3ex;
  }
  .pagination {
    display: flex;
    padding: 1rem 2rem !important;
    background: linear-gradient(rgba(10,40,80,.5), rgba(10,40,80,.25) 15%, rgba(10,40,80,.15) 50%, rgba(10,40,80,.1) 80%, rgba(10,40,80,.2) 100%);
    border-radius: 0 !important;
    font-weight: bold;
    font-size: 1rem !important;
  }
  .pagination>*, .script-list+.pagination>*, .user-list+.pagination>* {
    background: #dae0e5;
    border: 1px solid #879fb6;
    padding: .25em .75em !important;
    margin-right: .25em !important;
    border-radius: .75em;
  }
  .pagination>a:hover, .pagination>a:focus {
    background-color: #fff;
  }
  .pagination .current {
    background-color: #3979bd;
    color: #fff;
    border-color: #06233f;
  }
  html {
    overflow-y: auto;
  }
}
@-moz-document regexp("https://(greasy|sleazy)fork\\.org/([^/]+/)?search/?.*") {
  h3 {
    font-size: 11pt;
    padding-top: .25rem;
  }
  form {
    position: relative;
    display: block !important;
    margin-bottom: 3rem;
  }
  input[type="search"] {
    color: #123 !important;
    background-color: white !important;
    font-weight: bold;
    border: 1px solid #abc !important;
    width: calc(100% - 12rem);
    top: 0.2rem;
    left: 10rem;
    position: absolute;
    height: 23px;
  }
  input.search-submit {
    top: 0.2rem;
    right: 0;
    position: absolute;
  }
  form p {
    top: 1rem;
    left: 10rem;
    position: absolute;
    font-size: 85%;
    opacity: 0.6;
    transition: opacity .5s .25s;
  }
  form p:hover {
    opacity: 1;
  }
}
@-moz-document regexp("https://(greasy|sleazy)fork\\.org/([^/]+/)?(scripts(|/|(/page|/by-site|/search|/?\\?|/requested_permanent_deletion|/reported|/minified).*)|users/\\d+.*)$") {
  /* SCRIPT LIST (global and inside user profile) *******************************************************************/
  #script-language-filter {
    position: absolute;
    right: 1rem;
    margin-top: .25em;
    width: 18ex;
    text-align: right;
  }
  .list-option-groups ~ ol {
    width: auto!important;
  }
  .width-constraint {
    margin: 0px;
    max-width: 100%;
    min-width: 1000px;
  }
  .width-constraint > p:first-child {
    text-align: center;
    padding-top: 2em;
  }
  body > .width-constraint {
    min-height: 10em;
    text-align: center;
  }
  /*deleted script*/
  .width-constraint > * {
    text-align: left
  }
  #main-header {
    padding-left: 2ex;
    padding-right: 2ex;
    margin-bottom: 0;
  }
  #by-site-list {
    padding: 0 2em;
    font-size: 120%
  }
  .script-type {
    font-size: 0.7rem;
  }
  /******** SORT OPTIONS ********************/
  .list-option-groups {
    float: none;
    width: 100%;
    height: 8ex;
    margin-top: 0;
    margin-left: 0;
    background: linear-gradient(rgba(0, 15, 40, 0.2), rgba(0, 15, 40, 0.3) 90%, rgba(0, 15, 40, 0.5) 100%);
    font-size: 10pt;
  }
  .list-option-groups a:hover {
    text-decoration: underline;
  }
  .list-option-group {
    margin-left: calc(4ex + 96px);
    border: none;
    background: none;
    border-radius: 0;
    padding: 0.5em 0.5em 0.3em;
    margin-bottom: 1em;
  }
  .list-option-group ul {
    margin: 0;
    padding: 0;
    display: inline-block;
    background: none;
    border: none;
    box-shadow: none;
  }
  .list-current {
    border-left: none!important;
    background: none!important;
    box-shadow: none!important;
    padding: 0 0 0 1ex!important;
  }
  .list-option-group a {
    padding: 0;
  }
  .list-option-group a:hover {
    background: none;
    box-shadow: none;
  }
  .list-option-groups li {
    list-style-type: none;
    padding: 0.5ex 0;
    line-height: 1.1;
    letter-spacing: -0.05ex;
    display: inline-block;
  }
  #script-list-sort {
    position: absolute;
    margin: 0;
    display: flex;
    width: 100ex;
    top: calc(63px + 5.5rem);
    right: 1rem;
    box-shadow: none;
    background: none;
    border: none;
    vertical-align: top;
    text-align: right;
    word-wrap: break-word;
  }
  #script-list-sort li,
  #script-list-sort ul {
    visibility: visible;
    margin-top: -.1ex;
  }
  #script-list-sort ul {
    display: flex;
    flex: 1;
  }
  #script-list-sort {
    padding-top: 2ex
  }
  #script-list-sort .list-current {
    padding-top: .4ex!important
  }
  #script-list-sort li {
    text-align: center;
    order: 2;
  }
  #script-list-sort li:nth-child(1) {
    width: 14ex;
    text-align: right;
  }
  #script-list-sort li:nth-child(2) {
    width: 12ex;
  }
  #script-list-sort li:nth-child(3) {
    width: 13ex;
  }
  #script-list-sort li:nth-child(4) {
    width: 13ex;
  }
  #script-list-sort li:nth-child(5) {
    width: 13ex;
  }
  #script-list-sort li:nth-child(6) {
    width: 13ex;
  }
  #script-list-sort li:last-child {
    order: 1;
    flex: 1;
  }
  #script-list-filter {
    position: absolute;
    top: calc(64px + 1ex);
    width: calc(100% - 20ex - 96px);
    word-wrap: break-word;
    visibility: hidden;
  }
  #script-list-filter ul {
    display: flex;
    visibility: visible
  }
  #script-list-filter li {
    flex: auto;
    overflow: hidden;
    height: 1em
  }
  #script-list-set {
    position: absolute;
    top: calc(64px + 5.75rem);
  }
  #script-list-set ul {
    margin-top: 0.3ex
  }
  #script-list-set li {
    padding-left: 1ex
  }
  /****************************/
  .script-list {
    margin-top: 0;
    border-top: 0.2ex solid rgba(0, 0, 0, 0.1)
  }
  .script-list::before {
    height: 2.5rem;
    width: 100%;
    display: block;
    background: white;
    border-bottom: 0.2ex solid rgba(0, 0, 0, 0.1);
    content: " ";
  }
  .script-list li:not(#foo) {
    padding: 0
  }
  .script-list li:nth-child(even) {
    background-color: rgba(0, 20, 40, 0.05);
  }
  .script-list article {
    margin-bottom: 0;
    padding: .1ex 0 .4ex;
    display: flex;
  }
  .script-list article::after {
    clear: left;
    content: "";
    height: 2px;
    display: block;
  }
  .script-list dt[title] > span {
    border-bottom: none;
  }
  .name-description-separator {
    display: none
  }
  .script-list h2 {
    width: calc(100% - 30em);
    display: flex;
    word-wrap: break-word;
    align-items: center;
  }
  .script-list h2 a {
    font-size: 11pt;
    font-weight: 600;
    line-height: 1.2;
    width: calc(45% - 2ex);
    max-width: calc(45% - 2ex);
    padding-left: 2ex;
    text-decoration: none;
  }
  .script-list-author a {
    font-size: 9pt;
    text-decoration: none;
    white-space: normal;
  }
  .script-list a:hover {
    text-decoration: underline;
  }
  .description {
    font-size: 10pt;
    line-height: 1.2;
    max-width: calc(55% - 4ex);
    padding-left: 1ex;
    padding-right: 1ex;
  }
  .script-list dl {
    margin: 1.25ex 0 0;
    display: flex;
    flex: auto;
    font-size: 0.85em;
    line-height: 0.9;
    white-space: nowrap;
    -moz-column-count: 1;
    -webkit-column-count: 1;
    column-count: 1;
    max-width: 38em;
  }
  .script-list dt {
    display: none;
  }
  .script-list dd {
    float: none;
  }
  dd.script-list-author {
    width: 15ex;
    word-wrap: break-word
  }
  dd.script-list-daily-installs {
    width: 5ex;
    text-align: right
  }
  dd.script-list-total-installs {
    width: 9ex;
    text-align: right
  }
  dd.script-list-ratings {
    width: 13ex;
    display: flex;
    margin: -1ex 1ex -0.8ex 2ex
  }
  dd.script-list-ratings > span {
    display: flex;
    flex: auto;
  }
  dd.script-list-ratings > span > span {
    flex: auto;
    padding: 1ex 0.7ex 0.6ex 0;
    border-radius: 0;
    text-align: right;
  }
  span.good-rating-count {
    width: 5.5ex;
    background-color: rgba(0, 255, 0, 0.04);
    border: none;
  }
  .ok-rating-count {
    width: 3.5ex;
    background-color: rgba(255, 255, 0, 0.05);
    border: none;
  }
  .bad-rating-count {
    width: 3.5ex;
    background-color: rgba(255, 0, 0, 0.05);
    border: none;
  }
  dd.script-list-created-date,
  dd.script-list-updated-date {
    width: 13ex;
    text-align: center;
    word-wrap: break-word;
    white-space: normal;
  }
  .script-list + .pagination {
    background-color: transparent
  }
  .script-list + .pagination > * {
    padding-left: 1ex;
    padding-right: 1ex;
    margin-left: -0.5ex
  }
  .script-list + .pagination > a:hover {
    background-color: rgba(0, 20, 40, 0.1);
    transition: background-color 0.5s
  }
  #site-nav > nav {
    right: 10rem;
  }
  .sidebar-search input[type=search] {
    top: -2.2em;
    right: 2rem;
    position: absolute;
    height: 24px;
    width: 8.5rem;
    padding-left: 1ex;
    padding-right: .5ex;
    border: none;
    background-color: transparent;
    color: white;
  }
  .sidebar-search input[type=search]::placeholder {
    color: #a3ccd6;
  }
  .sidebar-search input[type=submit],
  .home-search input[type=submit] {
    top: -2em;
    width: 1.5rem;
    color: white;
    padding-right: 1ex;
  }
  .sidebarred {
    flex-direction: column;
  }
  .sidebarred-main-content {
    order: 1;
  }
  .ad.carbon-ad {
    display: none !important;
  }
  .sidebar {
    width: auto;
    margin: 0;
    padding: 0;
  }
  .sidebarred-main-content > p {
    margin: 0;
    padding: .25em .75em;
    font-size: .8rem;
    text-align: center;
    background: antiquewhite;
  }
}
@-moz-document regexp("https://(greasy|sleazy)fork\\.org/([^/]+/)?scripts/.*?\\?.+") {
  #script-list-sort {
    right: 36rem;
  }
}
@-moz-document regexp("https://(greasy|sleazy)fork\\.org/([^/]+/)?(scripts/\\d+|moderator_actions).*") {
  /* SCRIPT *******************************************************************/
  .width-constraint {
    max-width: 100%;
    text-align: center
  }
  .width-constraint > * {
    text-align: left
  }
  #main-header {
    padding-left: 2ex;
    padding-right: 2ex;
    margin-bottom: 0;
  }
  .width-constraint > table {
    margin-top: 6ex;
    font-size: 10pt;
    border-collapse: collapse
  }
  /*moderator actions*/
  .width-constraint > table tr:nth-child(even) {
    background: rgba(0, 20, 40, 0.05)
  }
  .width-constraint > table td {
    padding: 0.2ex 2ex 0.3ex;
    white-space: nowrap;
    border: none
  }
  .width-constraint > table td:nth-child(3),
  .width-constraint > table td:nth-child(5) {
    white-space: normal
  }
  #script-links,
  #install-area,
  #script-show-info-ad,
  #script-meta,
  #script-feedback-suggestion,
  #script-info header p {
    padding-left: calc(96px + 3ex);
  }
  #script-info {
    padding: 0;
    border: none;
    background: none;
    box-shadow: none;
    margin: 0;
    display: table;
    width: 100%;
  }
  #script-info header {
    display: table-header-group;
    background-color: rgba(0, 15, 40, 0.2);
  }
  #script-info header h2 {
    margin-top: 0;
    padding: 2ex 2ex 0 calc(96px + 1.5ex);
    overflow-wrap: anywhere;
  }
  #script-info header p {
    padding: 0.5ex 2ex 1ex calc(96px + 3ex);
    bottom: 0;
    line-height: 1.1;
  }
  #script-links {
    margin: 0 0 -1px;
    background: linear-gradient(rgba(0, 15, 40, 0.2), rgba(0, 15, 40, 0.3) 90%, rgba(0, 15, 40, 0.5) 100%);
    z-index: 9;
    padding-top: 1em;
    top: auto;
  }
  #script-links .current {
    background: white;
    border: 1px solid black;
    border-bottom-color: white;
    top: auto;
    margin-top: -1px;
    margin-left: 4px;
    box-shadow: none;
  }
  #script-links li:not(.current) {
    background-color: #d0d4d8;
    margin-left: 4px;
    border: 1px solid black;
  }
  #script-links li:not(.current) a {
    margin-top: 0;
  }
  #script-links li > * {
    padding: .25em .75em .25em;
  }
  #script-links .current > * {
    top: auto
  }
  #script-links a:hover,
  #script-links a:focus {
    position: static;
    background: rgba(255, 255, 255, 0.25);
    padding: .25em .75em .25em;
  }
  #script-links a:hover span,
  #script-links a:focus span {
    position: static
  }
  .expander {
    right: auto!important;
    left: 0;
    margin-top: 1ex;
  }
  #script-meta {
    padding-bottom: 1em
  }
  #script-content {
    margin: 0;
    padding: 0;
    background: white;
    /*border-bottom: 0.4ex solid rgba(0,20,40,0.2);*/
  }
  #script-content::before {
    border-top: 1px solid black;
    display: block;
    height: 0;
    content: " ";
  }
  #install-area {
    padding-top: 1em;
  }
  #additional-info {
    background: /*[[bgcolor]]*/;
    padding: 2ex 2ex 2ex calc(96px + 3ex);
    margin-bottom: 0;
    word-wrap: break-word;
    border: none;
  }
  #script-content > p,
  #script-content > form,
  #script-content > ul {
    padding: 2ex 1ex 2ex calc(96px + 2.5ex);
  }
  #script-content > .new_script_version {
    background-color: /*[[bgcolor]]*/;
    border-top: 0.4ex solid rgba(0, 20, 40, 0.2);
  }
  #additional-info > h3 {
    display: none
  }
  #code-container {
    font-size: 9pt;
    line-height: 1.2em;
    overflow: visible!important;
    -moz-tab-size: 4;
    tab-size: 4;
    background: #EEF1F3;
  }
  table.CodeRay {
    width: 100%;
    max-width: 100vw;
    background: #EEF1F3;
    border-top: 0.4ex solid rgba(0, 20, 40, 0.2)!important;
    border-left: none!important;
    border-right: none!important;
    border-bottom: none!important;
  }
  #code-container .CodeRay .line-numbers {
    background: rgba(0, 0, 0, 0.05)!important;
  }
  #code-container .CodeRay .line-numbers a {
    background-color: transparent!important;
    color: #95AFC1!important;
  }
  #code-container .CodeRay .string {
    background-color: transparent
  }
  #code-container .CodeRay .string .content {
    color: #079E81
  }
  #code-container .CodeRay .string .delimiter {
    color: #91BAC2
  }
  #code-container .CodeRay .comment {
    color: #947C46
  }
  @media (min-width: 2500px) {
    #script-content > ul {
      -webkit-column-count: 2;
      -webkit-column-gap: 4ex;
    }
  }
  .script-screenshots {
    background: /*[[bgcolor]]*/
    !important;
    margin-top: 0!important;
    padding-top: 2em!important;
  }
  .script-screenshots img {
    box-shadow: 4px 4px 40px rgba(0, 0, 0, 0.25);
    margin-right: 2em;
  }
  .prettyprint {
    max-height: none;
    padding: .5rem .5rem .5rem calc(96px + 4ex) !important;
    border-style: solid none none none !important;
    background: /*[[bgcolor]]*/
    !important;
  }
}
@-moz-document regexp("https://(greasy|sleazy)fork.org/([^/]+/)?scripts/\\d+.*?/feedback.*") {
  /* SCRIPT FEEDBACK *******************************************************************/
  #script-links {
    z-index: 99;
    position: relative;
  }
  #script-content {
    background: linear-gradient(white 11ex, /*[[bgcolor]]*/
    11ex);
    position: relative
  }
  #script-content > h3:first-child {
    display: none
  }
  #script-content > p {
    padding: 0 2ex 0 calc(96px + 3ex)
  }
  #script-content > p#support-url {
    padding-top: 1rem;
    padding-bottom: 1rem;
  }
  #script-content > h3 {
    padding: 1ex 2ex 0 calc(96px + 2.5ex)
  }
  #support-url {
    background-color: white;
    margin-bottom: -1em !important;
