css:
@-moz-document domain("vaje.uni-mb.si") {
  
  body {
      
  	  margin: 10px;
      background: white !important;  
  }
 
  body, body td, body th, body li {
    font-family: 'Open Sans', sans-serif !important;
  }

  table.navigacija {
    width: 100%;
    margin-top: 5px;
  	background: #eee;
    border-radius: 0;
  	border: none;
    padding: 5px 10px;
  }

  table.navigacija td, table.navigacija th  {
  	background:none;
  }

  .generalboxcontent {
      padding: 20px;
    
  }
  
  .generalboxcontent img {
      max-width: 100%;
      height: auto !important;
  }

  .generalbox {
  	margin-bottom: 10px;
  }

  .generalbox, .generalboxcontent {
  	border-color: transparent;
    background: #efefef;
  }

  table.sideblock {
  	border-color: transparent;
    background: #efefef;
  }

  
  table.sideblock td.sideblockmain { background: white;}
  table.sideblock td.sideblockheading {
  	background: #efefef;
    padding: 10px;
  }
  a:link {color: #0000ee;  }
  a:visited {color: #0000aa; }
  a:hover {  }
  a:active {color: red; }

  a[href="../copyright.jsp"]:after{
    color: black;
  	content: ' Theme by Marko Zabreznik'
  }
}

@-moz-document url("https://vaje.uni-mb.si/vaje/new/stanje.jsp") {


td[valign="top"] td {
 padding: 2px 5px;
}
td[bgcolor="#000000"] {
	background-color: transparent;
}
}

@-moz-document url("https://vaje.uni-mb.si/vaje/new/main.jsp") {
.sideblock {
 margin: 50px auto 0 auto
}
}
