css:
/* 2013-09-04 */


@-moz-document
    domain("tieba.baidu.com") {

/* 网页字体 */
* {
    font-family: 'Microsoft YaHei' !important;
}

/* 网页背景 */
body {
    background: #FCFCFC !important;
}

}


@-moz-document
    url-prefix("http://tieba.baidu.com/f?kw="),
    url-prefix("http://tieba.baidu.com/f?ie=utf-8&kw="),
    url-prefix("http://tieba.baidu.com/f?fr="),
    url-prefix("http://tieba.baidu.com/f/good?kw="),
    url-prefix("http://tieba.baidu.com/f/vote?kw="),
    url-prefix("http://tieba.baidu.com/p/"),
    url-prefix("http://tieba.baidu.com/f?ct="),
    url-prefix("http://tieba.baidu.com/f?kz="),
    url-prefix("http://tieba.baidu.com/i/"),
    url-prefix("http://tieba.baidu.com/shipin/"),
    url-prefix("http://tieba.baidu.com/photo?kw="),
    url-prefix("http://tieba.baidu.com/bakan/view?kw="),
    url-prefix("http://tieba.baidu.com/daquan?kw="),
    url-prefix("http://tieba.baidu.com/daquan/sub?kw=") { /* 贴吧通用开始 */

/* 文字链接 */
a {
    color: #014A8F !important;
    text-decoration: none !important;
}
a:hover {
    color: #3986A6 !important;
}
a:visited {
    color: #4B0F68 !important;
}

/* 贴吧背景 */
.wrap2 {
    background-position: top center !important;
    background-color: #F3EFE3 !important;
    background-image: url("http://static.tieba.baidu.com/tb/cms/frs/bg/bg_203_wrap2.jpg?v=1") !important;
    background-repeat: no-repeat !important;
}
.wrap_bright,
.container_bright {
    background: none !important;
}

/* 搜索框 */
#wd1 {
    border-color: #E5E5E5 !important;
}
/* 搜索按钮 */
#search_submit {
    background: #FCFCFC !important;
}

/* 使用者选单 */
#com_userbar {
    position: fixed !important;
    top: 0px !important;
    right: 5px !important;
    border: 2px rgb(200,200,200) solid;
    border-top: 0 !important;
    border-radius: 0px 0px 6px 6px;
    background: #FCFCFC !important;
}
#com_userbar:hover {
    box-shadow: lightgray 0px 0px 2px 2px !important;
}
#com_userbar a {
    color: #304560 !important;
}
#com_userbar a:hover {
    color: #3986A6 !important;
}
#com_userbar .u_ddl_con {
    box-shadow: 0px 0px 5px 0px #000 !important;
}

/* 贴吧导航条 */
#frs_nav,
#frs_nav_wrap,
#frs_nav_wrap ul,
#frs_nav_wrap ul li,
#frs_nav_wrap ul li p {
    background: #FCFCFC !important;
}
#frs_nav_wrap {
    border-top: 1px solid #D3DAED;
    border-bottom: 1px solid #D3DAED;
}
.nav_left li a {
    font-weight: bold !important;
    color: #161820 !important;
    background: none !important;
}
.nav_left .focus {
    background: none !important;
}
.nav_center li a,
.nav_right li a {
    padding: 0px 0px 0px 8px !important;
    color: #555555 !important;
    background-image: none !important;
}

/* 主题页页数列表 */
.pager > span,
.pager > a {
     padding-left: 8px !important;
     padding-right: 8px !important;
     color: #2C72C7 !important;
}
/* 帖子页页数列表 */
.l_pager > span,
.l_pager > a {
     padding-left: 8px !important;
     padding-right: 8px !important;
     color: #323232 !important;
}

/* 回复框 */
#title1,
.tb-editor-wrapper {
    border-color: #E5E5E5 !important;
}
/* 发表按钮 */
.subbtn_bg,
.subbtn_bg_hover,
.subbtn_bg_active {
    color: #161820 !important;
    background: #FFFFFF !important;
}
/* 回复框提示信息 */
.editor_tip_show {
    display: none !important;
}
} /* 贴吧通用结束 */


@-moz-document
    url-prefix("http://tieba.baidu.com/f?kw="),
    url-prefix("http://tieba.baidu.com/f?ie=utf-8&kw="),
    url-prefix("http://tieba.baidu.com/f?fr="),
    url-prefix("http://tieba.baidu.com/f/good?kw="),
    url-prefix("http://tieba.baidu.com/f/vote?kw=") { /* 贴吧主题页开始 */

/* 精品区 */
#frs_good_nav {
    padding-top: 5px !important;
}
#frs_good_nav span a {
    color: #405065 !important;
}
#frs_good_nav span a:hover {
    font-weight: bold !important;
}

/* 新版贴吧 == BEGIN == */
.search_main_wrap,
.search_main_wrap .search_main {
    background: rgba(255,255,255,0.00) !important;
    border: none !important;
}
.search_main_fixed {
    display: none !important;
}
/* 新版贴吧 ==  END  == */

} /* 贴吧主题页结束 */


@-moz-document
    url-prefix("http://tieba.baidu.com/f?ct="),
    url-prefix("http://tieba.baidu.com/f?kz=") { /* 贴吧帖子页开始 */

/* 隐藏贴吧主题TOP10 */
.top10_wrapper {
    display: none !important;
}


#frs_nav_wrap {
    border-left:0px !important;
    border-right:0px !important;
}

#frs_nav ul.nav_left > li:nth-child(1),
#frs_nav ul.nav_left > li:nth-child(1) > p,
#frs_nav ul.nav_left > li:nth-child(1) > p > a {
    border-left:0px !important;
    border-right:0px !important;
}

#j_head_focus_btn {
    display: none !important;
}

/* 帖子标题 */
.core_title_absolute {
    position: static !important;
    border: none !important;
}

/* 拉宽回复信息 */
.left_section {
    background: #FCFCFC !important;
}
.core {
    width: 100% !important;
}
#j_core_title_wrap {
    width: 100% !important;
    border:none !important;
}
#j_p_postlist {
    width: 100% !important;
    margin-top:0 !important;
}
.l_post {
    width: 100% !important;
    background:none !important;
    border-left: 1px solid #CCCCCC !important;
    border-right: 1px solid #CCCCCC !important;
}
.d_post_content_main {
    width: 82.5% !important;
}
.p_thread {
    width: 100% !important;
}
.p_share_ding {
    width: 100% !important;
}
.core_reply {
    margin-left:180px !important;
}

/* 隐藏一楼顶按钮 */
.p_ding_up {
    display: none !important;
}

}/*贴吧帖子页结束*/


@-moz-document
    url-prefix("http://tieba.baidu.com/i/"){ /* i贴吧开始 */

#top_banner,
#main_header_logo,
#pc2client {
    display: none !important;
}

} /* i贴吧结束 */
