css:
@-moz-document  url-prefix("about:newtab"){
    #newtab-vertical-margin{ 
       background-color: rgb(62,62,62) !important;
       background-position: center center !important;
       background-repeat: no-repeat !important;
    }
    #newtab-margin-bottom{
        background-color: rgb(62,62,62) !important;
    }
    #newtab-search-logo,#newtab-toggle{
        display: none !important;
    }
    #newtab-search-container{
        margin: auto !important;
    }
    #newtab-search-text{
       padding: 0 4px 0 12px !important;
       background-color: transparent !important;
       border: none !important;
       border-radius: 0 !important;
       box-shadow: none !important;
       color: white !important;
    }
    #newtab-search-form{
       width: 320px !important;
       height: 30px !important;
       background-color: rgba(255, 255, 255, 0.1) !important;
       border-radius: 15px !important;
    }
    #newtab-search-submit{
       width: 30px !important;
       height: 30px !important;
       padding: 6px !important;
       background-color: transparent !important;
       background-image: url("data:image/png;base64,") !important;
       background-position:50%50% !important;
       background-repeat: no-repeat !important;
       border: none !important;
       box-shadow: none !important;
       border-radius: 0 !important;
       text-indent:-9999px;
    }
    #newtab-grid > div > div > div > a:hover{
       -moz-transform:scale(1.1);-moz-transition:all .5s ease-in-out,  ease-in;
    }
    #newtab-grid > div > div > div > a{
        -moz-transform:scale(1);-moz-transition:all .5s ease-in-out,  ease-in;
    }
    .newtab-control{
        display: none !important;
    }
    .newtab-title{
        padding: 6px 32px !important;
        bottom: auto !important;
        top:0;
        font-size: 13px !important;
        text-align: center !important;
        height: 150px !important;
        background-color: rgba(255, 255, 255, 0.1) !important;
        border-radius: 15px !important;
        color: rgb(255, 255, 255) !important;
        line-height: 158px !important;
    }
    .newtab-row {
        margin-bottom: 160px !important;
    }
    .newtab-thumbnail{
        display: none !important;
    }
    .newtab-cell{
        background-color: transparent!important;
        max-height: 38px;
        border-width: 0px !important;
        display: block !important;
    }
}
