css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

  body, .fullname a, .fullname:not(:hover), .ProfileTweet-text, .ProfileTweet-fullname, .ProfileNav-value:not(:hover), .tweet-text, .profile-field, .ProfileHeaderCard-locationText, .is-active, .ProfileHeaderCard-joinDateText, .ProfileNameTruncated-link { 
 color: rgba(185, 189, 192, 1) !important;
}
  .dm-message p{
    color: black !important;
  }
   .global-nav-inner, .DashboardProfileCard, #tweet-box-template, #tweet-box-mini-home-profile, .modal-header, .import-prompt, #tweet-box-global, .header-inner, .content-header, .dropdown-menu, #user_name, #user_location, #user_url, #user_description, #device_country_code, #country_code_display, #device_address, #device_carrier, #user_screen_name, #user_email, #user_lang, #user_time_zone, #user_country, #current_password, #user_password, #user_password_confirmation, #tweet-box-dm-conversation, .caret-inner {
  background: rgb(50,50,50) !important;
  border: rgb(50,50,50) !important;
}
  .tweet, .stream-item, .module, .ProfileTweet, .ProfileCanopy-navBar, .ProfileNav-item, .ProfileHeader, .trends-container, .flex-module:not(.profile-header-inner), .WhoToFollow, .ProfileCard, .dm-thread, .list-link, .content-inner, .thumbnail-container, .dm-convo, .TwitterCard-container, .QuoteTweet {
    background: rgb(87, 85, 90) !important;
    border-color: rgb(78, 75, 87) !important;
    transition-property: background-color;
    transition-duration: 0.3s;
  }
  .new-tweets-bar, .stream-end-inner, .go-to-profile, .ProfileHeading-toggle, .ProfileHeading-content, hr, .profile-social-proof, .view-more-container, .logged-in, .three-col, .AppContent:not(.black):not(.white), #page-outer {
    background: rgb(62, 60, 64) !important;
    border: 1px solid rgb(62, 60, 64) !important;
    transition-property: background-color;
    transition-duration: 0.3s;    
  }
  .tweet:hover, .dm-thread:hover {
    background: rgb(92,88,97) !important;
    transition-property: background-color;
    transition-duration: 0.3s;
  }
  .new-tweets-bar:hover {
    background: rgb(78,73,82) !important;
    transition-property: background-color;
    transition-duration: 0.3s;
  }
  #tweet-box-mini-home-profile:focus, #tweet-box-global:focus, #user_name:focus, #user_location:focus, #user_url:focus, #user_description:focus, #device_address:focus, input:focus, #tweet-box-dm-conversation:focus {
    color: rgb(185, 189, 192) !important;
  }
  .stream-item-activity-me, .DashboardProfileCard-bg, .dropdown-divider {
    border-bottom: 1px solid rgb(62, 60, 64)
  }
  .modal-tweet-form-container{
    background: rgba(117, 81, 230, 0.8)
  }
}
