css:
@-moz-document domain('hootsuite.com'), url-prefix('https://hootsuite.com/dashboard') {

.trim {background-image: none !important;}

p { font-size:13px; }

.stream .message, .stream .noMessage, .originalMessage .message {
	border-bottom: 1px solid rgba(0,0,0,0.1) !important;
	border-top: none !important;
	background: #ffffff !important;
	padding: 10px 10px 10px 60px !important;
	background-color: #ffffff !important;
	background-image: none !important;
}

.message .networkName {
	color: #444 !important;
	font-weight: 900 !important;
	font-family: Verdana,Arial,Helvetica,Sans-Serif !important;
	cursor: pointer !important;
	float: left !important;
	line-height: 15px !important;
}
._twitterStream .message .networkName:before {
	content: "@";
	font-weight:100 !important;	
}

/* comments */
.message .comment { padding: 5px 20px 5px 55px !important;  }
.message .comment .networkAvatar { top:5px !important; left:5px !important;  }
.message .comment .networkName { float:none !important; display:block !important; }
.message .previousMessages .comment p.commentContent { margin:0px !important; }

.message .postTime { clear:none !important; margin-left:5px !important; margin-top:-2px !important; color:#bbb !important; position:absolute; right:10px; max-width:40px; max-height:15px; overflow:hidden; }
.message .retweet,.message .retweetedBy { display:none !important; }

.stream .header {
	background-color: #1793e0  !important;
	cursor: move !important;
	width: 100% !important;
	height: 43px !important;
	padding-top: 7px !important;
	position: absolute !important;
}

.stream .header .streamName {
	color: #fff !important;
	padding-top: 6px !important;
	margin-right: 75px !important;
	font-weight: 700 !important;
	font-size: 14px !important;
	overflow: hidden !important;
	line-height: 12px !important;
	white-space: nowrap !important;
	text-shadow: 0 0 0 !important;
}

.stream .header .streamName .subTitle {
	display: block !important;
	color: #ffffff !important;
	padding-top: 1px !important;
	font-size: 11px !important;
	font-weight: 400 !important;
	text-shadow: 0 0 0 !important;
}

.stream.update .header {
	background-color: #82ac59 !important;
	background-image: none !important;
	position: relative !important;
}

#streamsContainer {
	background-color: #ececec !important;
	background-image: none !important;
}

.stream .stream-scroll {
	border-right: none !important;
	margin-top: 50px !important;
	background-color: #ececec !important;
}

.stream.update .stream-scroll {
	margin-top: 0 !important;
	background-color: #fff !important;
}

.message .networkAvatarLink, .message .networkAvatar {
	width: 40px !important;
	height: 40px !important;
	position: absolute !important;
	top: 10px !important;
	left: 10px !important;
	z-index: 2 !important;
}

.stream .stream-scroll .messageOptions {
	background: #fff !important;
	height: 23px !important;
	padding: 0px 4px 0px 8px !important;
	position: absolute !important;
	right: 60px !important;
	z-index: 1 !important;
	margin-top:-1px;
}
.stream .stream-scroll .messageOptions a { opacity:0.6 !important; }

.btn-lite-sta {
	border: 1px solid #eeeeee !important;
	background-color: #F3F3F3 !important;
	background-image: linear-gradient(bottom,#f0f0f0 0,#f8f8f8 100%) !important;
	background-image: -moz-linear-gradient(bottom,#f0f0f0 0,#f8f8f8 100%) !important;
	background-image: -webkit-linear-gradient(bottom,#f0f0f0 0,#f8f8f8 100%) !important;
	color: #333!important;
	text-shadow: 0 0 0 !important;
	box-shadow: 0 0 0 !important;
}

.btn-lite-sta:hover,.btn-lite-sta:focus{border:1px solid #eee;background-color:#f3f3f3 !important;background-image:linear-gradient(bottom,#e9e9e9 0,#f7f7f7 100%) !important;background-image:-moz-linear-gradient(bottom,#e9e9e9 0,#f7f7f7 100%) !important;background-image:-webkit-linear-gradient(bottom,#e9e9e9 0,#f7f7f7 100%);color:#333!important;text-shadow:0 0 0;box-shadow:0 0 0 !important;background-image:linear-gradient(bottom,#dedede 0,#e9e9e9 100%) !important;background-image:-moz-linear-gradient(bottom,#dedede 0,#e9e9e9 100%) !important;background-image:-webkit-linear-gradient(bottom,#dedede 0,#e9e9e9 100%) !important;color:#333!important;text-shadow:0 0 0;box-shadow:0 0 0 !important;}

.btn-lite-sta.active.toggle,.btn-lite-sta:active.toggle{border: 1px solid #eee;background-color:#82ac59;background-image:none;color:#fff!important;text-shadow:0 0 0 !important;box-shadow:0 0 0 !important;}

.btn-lite-sta.inactive{border:1px solid #eee !important;background-color:#EEE !important;background-image:linear-gradient(bottom,#eee 0,#eee 100%) !important;background-image:-moz-linear-gradient(bottom,#eee 0,#eee 100%) !important;background-image:-webkit-linear-gradient(bottom,#eee 0,#eee 100%) !important;color:#B0B0B0!important;text-shadow:0 0 0 !important;cursor:default;box-shadow:0 0 0 !important}

.btn-lite-sta.btn-brite{border:1px solid #eee !important;background-image:linear-gradient(bottom,#ffcd03 0,#fee47c 100%) !important;background-image:-moz-linear-gradient(bottom,#ffcd03 0,#fee47c 100%) !important;background-image:-webkit-linear-gradient(bottom,#ffcd03 0,#fee47c 100%) !important;color:#000 !important;text-shadow:0 0 0 !important;box-shadow:0 0 0 !important;}

.sizeSlider {
	border-color: #b4b6b9 #b4b6b9 #f2f3f3 !important;
	background-color: #ebebeb !important;
}
.sizeSlider {
	border-top: none !important;
}

#dashboardTabs {
	background-color: #AACBE7 !important;
	background-image: none !important;
	-moz-box-shadow: 0 0 0 !important;
	-webkit-box-shadow: 0 0 0 !important;
	box-shadow: 0 0 0 !important;
}

#dashboardTabs .tab .trim {
	background-color: #E7E7E7 !important;
}

#dashboardTabs .tab a.text {
	color: #6D6D6D !important;
}

#dashboardTabs .active {
	text-shadow: 0 0 0 !important;
}

#dashboardTabs .tab.active a.text {
	color: rgb(34, 34, 34) !important;
	font-weight: 700 !important;
}

#dashboardTabs .tab {
	font-weight: 400 !important;
	text-shadow: 0 0 0 !important;
}

#dashboardTabs .tab.active a.text {
	color: #222 !important;
	font-weight: 700 !important;
}

#dashboardTabs .tab.active .trim, #dashboardTabs .tab.active .trim:hover {
	/*background-color: #f9f9f9 !important;*/
}

#streamTabInfo {
	border-color: rgba(228, 228, 228, 0) !important;
	background-color: #f9f9f9 !important;
	background-image: none !important;
}

#streamTabInfo .controls {
	border-color: #f9f9f9 !important;
}


#header {
	background-color: rgb(109, 109, 109) !important;
}

.stream ::-webkit-scrollbar {
	width: 6px !important;
}

.stream ::-webkit-scrollbar-thumb {
	width: 6px !important;
}

.message p {
	color: #32363f !important;
}

.messageBoxContainer .messageTools {
	border-top: none !important;
	background-color: #F0F0F0 !important;
}

.messageBoxContainer .messageSection {
	background-color: rgb(198, 224, 250) !important;
}

.messageBoxContainer .messageBox {
	border: none !important;
}

.messageBoxContainer.collapsed .messageSection {
	background-color: rgb(109, 109, 109) !important;
}

.globalNav {
	background-color: rgba(68, 76, 87, 0.85) !important;
	background-image: none !important;
}

.globalNav .sidebarNavMenu a:hover .trim {
	border-color: rgba(44, 49, 56, 0) !important;
	background-color: rgba(64, 72, 86, 0.74) !important;
	background-image: none !important;
}

.streamMessage .content {
	background-color: #C5ECEB !important;
	border-bottom: 1px solid #B6F6FC !important;
	color: #616161 !important;
}

.globalNav .sidebarNavMenu li .navSubMenu {
	background-color: rgba(39, 44, 50, 0.81) !important;
	background-image: none !important;
}

.globalNav .sidebarNavMenu li .navSubMenu ul a {
	border-width: 0 !important;
}

.globalNav .sidebarNavMenu a:hover .trim {
	background-image: none !important;
}

.rb-a-3 {
	border-radius: 0px !important;
}

.sidebarNavMenu a:hover .trim .text {text-shadow: 0 0 0 !important;}

.globalNav .sidebarNavMenu a {padding:0 0 0 0px !important;}

.globalNav .sidebarNavMenu a.active .trim {
	background-image: none !important;
	background-color: rgba(234, 249, 255, 0.81) !important;
}

.globalNav .sidebarNavMenu a .trim{display:block;background-image:none !important;padding:10px 7px 10px 9px !important;position:relative}.globalNav .sidebarNavMenu a:hover .trim,.globalNav .sidebarNavMenu a:active .trim,.globalNav .sidebarNavMenu a.active .trim{background-image:none !important;}.globalNav .sidebarNavMenu a:hover .trim,.globalNav .sidebarNavMenu a.hover .trim{border:none !important;background-color:#404856 !important;background-position:0 -2700px !important;padding:10px 7px 10px 9px !important;-moz-box-shadow:0 0 0 !important;-webkit-box-shadow:inset 0 0 0 !important;box-shadow:0 0 0 !important;}.globalNav .sidebarNavMenu a:active .trim,.globalNav .sidebarNavMenu a.active .trim{border:0 !important;background-position:0 -2750px !important;padding:10px 7px 10px 9px !important;-moz-box-shadow:inset 0 0 0 !important;-webkit-box-shadow:0 0 0 !important;box-shadow:inset 0 0 0 !important;}.globalNav .sidebarNavMenu a .trim .text{color:#fff !important;margin-left:6px !important;font-size:14px !important;line-height:18px !important;vertical-align:middle !important;text-shadow:0 0 0 !important;}

.globalNav .sidebarNavMenu a.active .trim {background-color: rgba(234, 249, 255, 0.81) !important;}
.globalNav .sidebarNavMenu a:active .trim {background-color: rgba(234, 249, 255, 0.81) !important;}

.globalNav .sidebarNavMenu li .navSubMenu ul a {
	text-shadow: 0 0 0 !important;
	-moz-box-shadow: 0 0 0 !important;
	-webkit-box-shadow: 0 0 0 !important;
	box-shadow: 0 0 0 !important;
	padding-left: 20px !important;
}

.globalNav .sidebarNavMenu a.active .trim .icon-13.selected {
	display: none !important;
}

.profileSelectorWidget .picker .footer {
	color: #aaa !important;
	background-color: #C6E0FA !important;
	-moz-box-shadow: 0 0 0 !important;
	-webkit-box-shadow: 0 0 0 !important;
	box-shadow: 0 0 0 !important;
}

.profileSelectorWidget .btn-lite-cmt {
	border-color: rgba(213, 213, 213, 0) !important;
}

.icon-19.account {
	background-position: -51px -210px !important;
}

.profileSelectorWidget .selectedList {
	min-height: 26px !important;
	padding: 2px 26px 0 0 !important;
}

.profileSelectorWidget .selectedList .item {
	margin:0 0 2px 3px !important;
	border:1px solid #FFF !important;
	-moz-border-radius:0;-webkit-border-radius:0;border-radius:0 !important;
	-moz-box-shadow:0 0 0 !important;
	-webkit-box-shadow:0 0 0 !important;
	box-shadow:0 0 0 !important;
	background-color:#FFFFFF !important;
}
.profileSelectorWidget .selectedList .item:hover{
	background-color:#EFEFEF !important;
}
.profileSelectorWidget .picker .snList .row.active {
	border:1px solid #FFFFFF !important;
	background-color:#EFEFEF !important;
}

.profileSelectorWidget .picker .snList .row .details strong {
	font-weight:300 !important;
}

.profileSelectorWidget .selectedList .profileSelect {
	border-width: 0 1px 0 0 !important;
	padding: 0 3px !important;
	margin-top: 0 !important;
	line-height: 26px !important;
}

.sidebarNavMenu a.active .trim .text {
	color: #333 !important;
	text-shadow: 0 0 0 !important;
}

#dashboard .resultFilter {
	border-color: rgba(228, 228, 228, 0) !important;
	background-color: #f9f9f9 !important;
	background-image: none !important;
	color: #000 !important;
}

#analyticsContent .page .section-title {
	border-width: 1px !important;
	border-style: solid !important;
	border-color: #fff !important;
	background-color: #fff !important;
	color: #333 !important;
	min-height: 8px !important;
	margin-bottom: 2px !important;
	padding: 0 !important;
	-moz-box-shadow: inset 0 1px #fbfbfb,0 1px #ececec !important;
	-webkit-box-shadow: inset 0 1px #fbfbfb,0 1px rgb(189, 206, 206) !important;
	box-shadow: inset 0 1px #fbfbfb,0 1px rgb(189, 206, 206) !important;
}

#analyticsContent .page .header h2 {
	color: #333333 !important;
	text-shadow: 0 1px #AACBE7 !important;
}

#analyticsContent .page .header {
	border-color: #AACBE7 !important;
	background-color: #AACBE7 !important;
	background-image: none !important;
}

#analyticsSection #analyticsContent {
	background-color: #ffffff !important;
}

#contactsManage .profile {
	border-color: #fff #fff #C5E3F5 !important;
	background-color: #fff !important;
	background-image: none !important;
	padding: 12px 7px 12px 42px !important;
}

#contactsManage .profile .networkAvatarLink{
	top: 13px !important
}

#contactsManage .profile .actions {
	top: 44px !important;
}

#contactsManage .profile .networkAvatarLink, #contactsManage .profile .networkAvatar {
	top: 13px !important;
}

#dashboard .sidebarTabs {
	background-color: #F0F2F5 !important;
	background-image: none !important;
}

.sidebarTabs .tab .trim {
	color: #444 !important;
	padding-left: 9px !important;
	font-size: 12px !important;
	font-weight: 400 !important;
	line-height: 24px !important;
	outline: 0 !important;
	white-space: nowrap !important;
	text-shadow: 0 0 0 !important;
	position: relative !important;
}

.sidebarTabs .trim:hover, .sidebarTabs .trim.hover {
	text-decoration: none !important;
	background-color: rgba(220, 231, 235, 0.65) !important;
}

.sidebarTabs .active .trim, .sidebarTabs .tab .trim:active {
	border-color: rgb(174, 204, 216) !important;
	background-color: rgb(174, 204, 216) !important;
	color: #fff !important;
	text-shadow: 0 0 0 !important;
}

.sidebarTabs h5.section-title {
	border-color: #fcfcfc transparent #9ea1a5 !important;
	background-color: #d6dade !important;
	color: #4d4e52 !important;
}

#contactsSection {
	border-color: #464e58 !important;
	background-color: #E8F1F1 !important;
	background-image: none !important;
}

#assignmentsContentSection {
	border-color: #464e58 !important;
	background-color: #E8F1F1 !important;
	background-image: none !important;
}

.ui-dialog-content {
	border-color: #f4f5f6 !important;
	background-color: #C8DAEC !important;
	background-image: none !important;
}

.ui-dialog-section {
	border-color: #b2b3b3 !important;
	background-color: #ffffff !important;
	-moz-box-shadow: 0 0 0 !important;
	-webkit-box-shadow: 0 0 0 !important;
	box-shadow: 0 0 0 !important;
}

.userInfoBox .networkInfo table td {
	border-bottom: none !important;
	border-top: 1px solid #efefef !important;
}

.topTabs {
	background-color: #E1E4E7 !important;
}

.topTabs .tab.active, .topTabs .tab.active:hover, .topTabs .tab.active.hilite {
	background-color: #fff !important;
	color: #151719 !important;
	text-shadow: 0 0 0 !important;
}

#twitterUserInfoPopup .socialAvatar {
	-moz-box-shadow: 0 0 0 !important;
	-webkit-box-shadow: 0 0 0 !important;
	box-shadow: 0 0 0 !important;
}

.ui-dialog-titlebar {
	border-color: #9b9fa2 !important;
	background-color: rgb(155, 191, 255) !important;
	color: rgb(255, 255, 255) !important;
	text-shadow: 0 0 0 !important;
	background-image: none !important;
}

.btn-cmt {
	border: 1px solid #ddd !important;
	background-color: #F3F3F3 !important;
	background-image: linear-gradient(bottom,#f0f0f0 0,#f8f8f8 100%) !important;
	background-image: -moz-linear-gradient(bottom,#f0f0f0 0,#f8f8f8 100%) !important;
	background-image: -webkit-linear-gradient(bottom,#f0f0f0 0,#f8f8f8 100%) !important;
	color: #333 !important;
	text-shadow: 0 0 0 !important;
	-moz-box-shadow: 0 0 0 !important;
	-webkit-box-shadow: 0 0 0 !important;
	box-shadow: 0 0 0 !important;
}

.btn-cmt:hover, .btn-cmt.hover, .btn-cmt:active{
	border:1px solid #eee !important;
	background-color:#e9e9e9 !important;
	background-image:linear-gradient(bottom,#dedede 0,#e9e9e9 100%) !important;
	background-image:-moz-linear-gradient(bottom,#dedede 0,#e9e9e9 100%) !important;
	background-image:-webkit-linear-gradient(bottom,#dedede 0,#e9e9e9 100%) !important;
	color:#333!important;
	text-shadow: 0 0 0 !important;
	-moz-box-shadow: 0 0 0 !important;
	-webkit-box-shadow: 0 0 0 !important;
	box-shadow: 0 0 0 !important;
}

-webkit-scrollbar-thumb {
	border-radius: 4px !important;
	background-color: #BDBDBD !important;
	-webkit-box-shadow: 0 0 1px rgba(255,255,255,.5) !important;
}

#dashboard .noContentMessage .title {
	color: #333 !important;
	text-shadow: 0 0 0 !important;
}

#dashboard .noContentMessage .description {
