}

.sidebarTabs p {
	color: #333 !important;
}

.message p a {
	color: #376299 !important;
}

.filterContainer {
	border: none !important;
	background-color: rgb(232, 239, 245) !important;
	padding: 4px 5px !important;
	box-shadow: 0 0 2px #d8d8d8 !important;
}

#memberProfileSection .filterContainer {
	min-height: 100px !important;
	padding: 14px !important;
	position: relative !important;
}

.documentView.next .message-banner {
	background: #C8E9F3 !important;
