css:
/* @namespace url(http://www.w3.org/1999/xhtml); */

/* @-moz-document domain("xgn.nl") { */
@-moz-document regexp("http://www.xgn.nl/(?!film|tech|kids)(.*)") {
* {
    text-decoration: none !important;
    box-shadow: none !important; 
    border-radius: 0 !important;
}

body {
    background: url("http://www.hdwallpapers.in/walls/harley_quinn-wide.jpg") fixed no-repeat 40% top / 1920px auto white;
    padding-right: 0px;
}

html, #search-bar .search-form-group, #nav-meer, #top-game .well, #nav .nav.navbar-nav > .forum > a:not(:hover), #popular .game .overlay {
    background: none !important;
}

/*BG Transparent*/ #content, .page_heading, .background-videos, #featured, .list-group-item, .comment .replies, #login, #latest-user-reviews .average, .panel, .panel-default > .panel-heading, #popular, .breadcrumb, .table-striped > tbody > tr:nth-child(2n+1) > td, .img-thumbnail, #profile-top .overlay.profile, .padd .thumbnail, #popular-games .game .overlay, #grid .grid-item > .overlay {
    background-color: transparent !important;
}

/*No Border*/.news-feed .media, #latest-user-reviews .average, #featured-articles .top, #featured-articles .media, #featured .article, .list-group-item, .nav-tabs > li > a, .nav-tabs, #footer, .panel, .panel-heading, .dropdown-menu, .pushy a, .row.article-info, #popular-games .game, .news-feed .media img, .table > tbody > tr > td, .padd .thumbnail, #user-collections .row.top, #game-info .top, .img-thumbnail, #search-bar button.search-active, .pages ul a, .game-review-box, .game-review-box .top, #top-game, #latest-screenshots, #latest-videos, #game-review-and-info, div.pages > ul, #related, #related > div > div > a > div, .center-block, .well, #search-bar .search-form-group > input.form-control, #popular .game {
    border: none;
/*     border-color: transparent; */
}

/*Header*/#nav {
    background: linear-gradient(rgba(213, 68, 17, .75) 45%, rgba(162, 33, 0, .75)) !important;
    position: absolute;
}
    
#nav .nav.navbar-nav > li > a {
    font-size: 20px;
}
/*Header*/
    
/*Hover Button red*/ 
.news-feed .media:hover, a.list-group-item:hover, a.list-group-item:focus, .row .forum-item:hover, .nav-tabs > li.active > a, #featured .info .title:hover, .nav-tabs > li:hover * {
    background: rgba(160, 40, 0, .75) !important;
    text-shadow: none !important;
    border: 0 !important;
}

.nav > li > a:hover {
    background: rgb(140, 30, 0) !important;
}

.row .forum-item, .row .forum-item:hover {
    margin-left: 0;
    margin-right: 0;
}

/*Display*/.ad-rectangle, #share-buttons, .hidden-sm, #cookie-notification, #footer-leaderboard, .cc_container {
    display: none !important;
}

/*Display sidebar zit ook Ads*/.content-sidebar {
    padding-top: 0;
}

/*Frontpage images Boven aan*/#grid img, .top-image:not(:hover) {
    opacity: .3;
}

#popular-games .game:not(:hover), #featured .article .img img:not(:hover), #video div.col-lg-6:not(:hover), #video div.col-md-4:not(:hover), .news-feed .media img, div.top-video, #nieuws_page .article-text img.img-responsive:not(:hover), .thumbview:not(:hover), a.listview.selected:not(:hover), #popular-videos > div:not(:hover), [id="game-splash"]:not(:hover), [class="img-thumbnail boxart-large"]:not(:hover),.media > .pull-left:not(:hover), .container > #popular:not(:hover), #user-collections img:not(:hover), #latest-user-reviews .media-object:not(:hover), .alert.alert-info:not(:hover) {
    opacity: .6;
}

/*Frontpage images*/ #video img, .img-responsive:hover, .news-feed .media:hover *, div.top-video:hover, #grid .grid-item:hover *, #featured .article:hover *  {
    opacity: 1 !important;
}

/*Bars/panels*/
    /*Transparent white*/.tab-content > .active .well {
        background-color: rgba(255, 255, 255, .75)
    }


    /*Bars Blue*/.panel-primary > .panel-heading, .news-feed .list-group-item-info, [class="tab-pane active"] > p > a > .center-block {
        background-color: rgba(66, 139, 202, .75) !important;
    } 

    /*Bars Black*/#forum-posts .heading, #popular-games .heading, #exp-bar, #game-info .heading, #user-collections .heading, #user-collections .row.top, #forum-top-users > div, #forum-top-users .heading, #game-prices .heading, #latest-user-reviews .heading {
        background-color: rgba(15, 15, 15, .75);
    }
/*Bars/panels*/

/*Hidden sidebar*/.pushy {
    background: none repeat scroll 0% 0% rgba(51, 51, 50, .75); 
}

/*Grid*/
#grid .grid-item .overlay div {
    background: rgba(0, 0, 0, .6);
    width: 100%;
}

/*Width Aanpassen*/ .container {
    min-width: 100% !important;
}

/*Kleinscherm*/#grid-slider {
    opacity: .5;
    padding-bottom: 230px ;
}

/*Button Avatar veranderen*/ 
#nav-content #user-button .avatar {
/*     height:0 !important; width:0 !important; padding-left: 36px !important ; padding-top: 36px !important; 
    background: url("file:///C:/Users/Say%20Wa%20Chuong/Pictures/Firefox/XGN_no-avatar.png") no-repeat center / cover!important; */
}

#nav .nav.navbar-nav > li > a, #forum-posts .list-group-item-heading {
    font-weight: bold;
}

/*Footer*/#footer {
    background: rgba(206, 53, 0, .75) !important; 
    padding: 0;
}

}


@-moz-document regexp("http://www.xgn.nl/(?!forum|film|tech|kids)(.*)") {

* {
    font-weight: bold;
    font-size-adjust: .6;
}

/*Text white + shadow*/a, p, h1, h2, span, strong, em, li, .date, td, .btn-link, .text-muted, .form > div > label, .checkbox > label, #game-info dt, #game-info dd, .media-heading, #comments-wrapper small, #search-form > form label, div.author-info-box, #related .related-header, #game-details, article > div.row > div > h3, #news.col-lg-4 a, 
#news.col-lg-4 .news-feed .date, #nieuws_page .article-text > div > div, #game-title small {
    color: white !important;
    text-shadow: 1px 1px 3px black, 1px 1px 3px black, 1px 1px 3px black;
}

/*No-shadow*/#nav *, #grid .grid-item .overlay div span, #grid .grid-item .overlay div h2, .pushy > .nav > li > a, .navbar-nav > li > a *, #video .item .overlay .holder-cell p, .pushy a, .pushy .dropdown-menu > li > a, #footer *, a.list-group-item:hover *, a.list-group-item:hover, #popular-games .game .overlay span.title, #grid-slider h2, #grid-slider span.label, #popular-games .heading h2, #nieuws_page .label-article-type, .news-feed .media:hover *, .pages .next, .pages ul a.current, .pages ul a:hover, .content-wrapper > ul > li > a > span, #featured .info .title:hover *, #related .related-post-header h3, .btn-block, .btn-primary, p#rank.text-center, .to-page-button {
    text-shadow: none !important;
    box-shadow: none !important;
}

#nav .dropdown-menu a, .youtube-video *, .alert-info *, .autoPagerS *, .well > h1#game-heading, .modal.in .modal-dialog td a, .modal.in .modal-dialog td span, .modal.in .modal-dialog td, .modal.in .modal-dialog .nav > li:not(.active) > a {
    color: black !important;
    text-shadow: none !important;
}

}


/*Alleen Forum*/ @-moz-document url-prefix("http://www.xgn.nl/forum") {

body {
    background: url("http://arstyle.org/uploads/posts/2010-01/1264346310_1252171602_1.jpg") fixed no-repeat 65% 15% / 2180px auto transparent !important;
}
    
#forum * {
    font-size-adjust: .75 !important;
}

/*No BG*/.js-editor, .pagination > .disabled > span, .pagination > li > a {
    background: none !important;
}

/*No border*/.pagination > .disabled > span, .pagination > li > a, #forum .post *, .page-header, #forum-top-users .media, #content .form-control, #forum #thread-list, .pagination > li > span, .wysibb *, .wysibb, #forum #thread-list .btn-group .btn, .panel-footer {
    border: 0 !important;
}

/*Display None*/.forum-post .forum-post-signature, .topic-list .avatar { 
    display: none !important;
}

/*Kader wit*/ #forum .post, #forum .post .post-content blockquote, #content .form-control, .panel-primary > .panel-footer, .wysibb, .panel-footer {
    background: rgba(255, 255, 255, .6) !important;
}

/*Light blue*/.forum-post, .alert-info {
    background-color: rgba(217, 237, 247, .75) !important;
    border: none !important;
}
/*Bar Red*/#channel-tabbar, #forum-posts .list-group-item:hover {
    background: rgba(160, 40, 0, .75) !important;
}

/*Bar Blue + active pagenumber*/.pagination > .active > a, .forum-post-heading, .pagination > .active > span {
    background-color: rgba(66, 139, 202, .75) !important;
}

/*Bar White / hover*/#forum .forum-category-header, #mark-read, .table-hover > tbody > tr:hover > td {
    background: rgba(255, 255, 255, .75);
    border: 0;
}

/*Text*/ .row .forum-item *, ul.nav-tabs > li > a:nth-child(1), #forum-posts .list-group-item-heading, #forum-posts .list-group-item, #forum-posts .list-group-item *, .media-body, .media-body .text-muted, .media-body a, .media-list > li, .media-list > li a, #forum #thread-list .thread-info, #forum #thread-list .thread-info a, #forum #thread-list .thread-post-count, #forum > div > h1, #forum .breadcrumb *, .pagination-lg > li > a, .pagination > .disabled > span, .pagination-sm > li > a, .panel-poll .panel-body .radio label {
    color: white;
    text-shadow: 1px 1px 3px black, 1px 1px 3px black, 1px 1px 3px black;
}

li.active > a, #forum .post .post-toolbar .btn.btn-primary.post-quote, .pagination-sm > li > a, .pagination-lg > li > a, .pagination > .disabled > span {
    color: white !important;
}

div.well, div.forum-category-header > h2 {
    color: black !important; 
}

#content .form-control, #forum .post * {
    color: black !important;
    font-size-adjust: .7; 
}

.row .forum-item:hover *, #forum-posts .list-group-item:hover *, .wysibb .wysibb-toolbar .wysibb-toolbar-container .wysibb-toolbar-btn .fonticon {
    text-shadow: none !important; 
}

#thread-list > tbody >tr:hover * {
    text-shadow: none !important; 
    color: black !important;
}

}


/*News Sidebar*/@-moz-document url("http://www.xgn.nl/"), url("http://www.xgn.nl/#") {

#grid {
    padding-left: 433px;
}

#news {
    top: -236px;
}

}
