@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("vk.com") {
    
/* VK.com - Золотая шапка профилей | Made by Diamond00744  */
#header, #header span, #header b, #header h1 {
    color: #000000 !important;
}
#header {
    height: 12px;
    background: #f5ecbc !important; /* Old browsers */
    background: -moz-linear-gradient(bottom, #ede8b1 0%, #fff1ca 95%, #e3ddab 100%) !important; /* Firefox 3.6 */
    background: linear-gradient(to top, #ede8b1 0%, #fff1ca 95%, #e3ddab 100%) !important;
    border-bottom: 1px solid #d2ceab !important;
    border-top: 1px solid #cdc999 !important;
}
#header h1 {
    margin: -2px !important;
    vertical-align: middle !important;
    font-family: Tahoma, Arial, Verdana, sans-serif, Lucida Sans !important;
}
.page_verified { background: url(data:image/gif;base64,R0lGODlhDQALAIABAAAAAP///yH5BAEAAAEALAAAAAANAAsAAAIZjI8AyKHazINtLnfxk9seOzFgloSRdn5nAQA7) no-repeat scroll 0 0 rgba(0, 0, 0, 0) !important; }
.mob_onl {
    background: url(data:image/gif;base64,R0lGODlhBwAMAIABAAAAAP///yH5BAEAAAEALAAAAAAHAAwAAAIRjI8GCr0O24HT1UhfzkHGUAAAOw==) no-repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
}
    
/*--------Старая шапка и лого [в]контакте----------*/
#pageHeader .header_left,
#pageHeader .header_right,
#pageHeader .header_back,
#page_header .left,
#page_header .right,
#page_header .back,
#top_logo_down {background-image: url("http://i.imgur.com/kq45NIU.gif") !important}
/*--------ЗОЛОТАЯ! ПОЛОСКА!! РЕЙТИНГА!!!----------*/
#profile .rate_line .rate_left {background-color: #c9b468;}
#profile .rate_line .rate_right {background-color: #e1cb7f;}
#profile .rate_line .rate_text {color: #998232}
/*--------Cтарые хедеры блоков----------*/
.module_header {
border-top: 1px solid;
border-color: #B1BDD6;
}
.wide_column .module_header .header_top {padding: 3px 22px;}
.module_header .header_top {
padding: 2px 23px;
background: url("http://vkontakte.ru/images/flex_arrow_open.gif") no-repeat 
/*http://i.imgur.com/tb2xnIi.gif - if shit happens*/
}
#gifts_module_header {padding: 0px 16px;}
.counts_module div a {
padding: 0px 0px;
border-bottom: 1px solid silver;
}     
.module_header .header_top {border-bottom:1px solid #d3d7d8}
.module_header .header_top {background-color: #dae1e9}
.p_header_bottom {background: none repeat scroll 0% 0% #eeeeee}
  
/*-------Старые кнопки (из 2011)-------*/
.flat_button, .button_wide, .button_blue, .button_wide, .subscribe_button, .button_big.flat_button, .button_blue button {
  border-radius: 2px;
  border: 1px solid;
  border-top-color: #789ABF;
  border-bottom-color: #5980A9;
  border-left-color: #5980A9;
  border-right-color: #5980A9;
  text-shadow:0 1px 0 #45688E;
  *border: 0;
  background: linear-gradient(#6a8db2, #50769e)
}
.flat_button:hover, .button_wide:hover, .button_blue:hover, .button_wide:hover, .subscribe_button:hover, .button_big.flat_button:hover {
  background:linear-gradient(#7a9bbd, #50769e)
}
    .flat_button.secondary, .button_gray button {
    font-weight: normal;
    background-color: #DFE5ED;
    color: #FFF;
}
.mv_share_button, .mv_add_button, .mv_edit_button {color: #FFF}
.flat_button, .button_gray button, .button_blue button {transition: none}
/*--------Cтена (N записей)----------*/
b#page_wall_posts_count:before{content: "Стена ("}
b#page_wall_posts_count:after{content: ")"}
/*--------добавить в друзья вместо подписаться----------*/
    #friend_status .profile_action_btn button{font-size: 0px}
    #friend_status .profile_action_btn button:before {
     content:"Добавить в друзья";
     font-size: 11px;
     line-height: 1.45em;}

/* */
#pv_author_img {
    display: none;
}
