@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gamekings.nl"), domain("gamekings.tv") {

* {
    box-shadow: none !important;
}

body {
    background: url(http://www.gamekings.nl/wp-content/uploads/20150119_Brievenmaandag_Splash.jpg) no-repeat fixed 38% top / 120%;
/*     background-position: center; */
/*     background-attachment: fixed; */
/*     background-repeat: no-repeat; */
    background-color: rgb(10, 10, 10);
}

/*No BG*/.slider, .tabs__content--dark, .content--bgdark {
    background: none;
}

/*No border*/.tabs__navigation, .followus, .content--page {
    border: 0;
}

/*Display none*/.dda-billboard, .bottommenu {
    display: none;
}

/*Header Footer*/
    /*Blue Bar*/.header {
        background-color: rgba(6, 114, 238, .7);
    }

    /*Footer grey*/.followus, .filters {
        background-color: rgba(17, 17, 17, .7);
    }

    /*Black Bar*/ .copyright {
        background-color: rgba(0, 0, 0, .7);
    }
/*Header Footer*/

/*Getpremium*/
    .getpremium, .getpremium::before {
        border-radius: 0;
    }

    .getpremium {
        opacity: .3;
    }
/*Getpremium*/

/*Slider*/
    .header {
        margin-bottom: 0;
    }
    
    .slider {
        opacity: .57;
/*         height: calc(709px * 2 / 3) !important; */
        height: 490px !important;
    }

    .slider:hover {
        opacity: .85;
/*         height: 709px !important; */
    }

    .post-slider__items {
        opacity: .5 !important;
    }

    .slider__item:not(:hover) {
/*         height: 50px; */
    }

    .slider__gradient {
/*     background: unset; */
    }

    .slider__image, .slider__items--grey .slider__thumb {
        filter: unset;
    }
    .slider__item-overlay {
    background-color: rgba(5, 103, 216, .5);
    }
/*Slider*/

/*Image Hover*/
    .post:hover .post__imageâ€‹ {
/*         transform: scale(1) translate(0,0); */
        transform: unset;
    }
    
    .post__image {
        transition: unset;
    }
    
    .post:hover .post__image {
    transform: scale(1) translate(-50%, -50%);
}
/*Image Hover*/

.meta__item {
    border-radius: 20px;
}

/*Reading content*/
.content--bglight {
    background-color: rgba(255, 255, 255, .7);
}

.tabs__morebutton {
    border: 0;
    border-radius: 0;
    background-color: rgba(34, 34, 34, .5);
}

/*Tabs*/
    .tabs__arrow {
        top: 55px;
        border-left: 134px solid transparent;
        border-right: 134px solid transparent;
        border-top: 20px solid rgb(27, 27, 27);
        opacity: .7;
    }

    .tabs__item:not(.tabs__item--blue):not(.tabs__item--yellow) {
        background-color: rgba(27, 27, 27, .5); 
    }

    .tabs__item--blue {
        background-color: rgba(5, 103, 216, .5);
    }


    .tabs__item--yellow {
        background-color: rgba(236, 197, 16, .5);
    }
/*Tabs*/

    /*Social*/ .followus__item:not(:hover) {
        opacity: .3;
    }
    
    
/*Text*/
* {
    font-size-adjust: .57 !important;
    /*     font-weight: bold !important; */
    --text-shadow-line: 1px 1px 3px black;
    --text-shadow: var(--text-shadow-line), var(--text-shadow-line), var(--text-shadow-line), var(--text-shadow-line), var(--text-shadow-line);
}

test {
    color: #0672EE !important;
}

test {
    color: white !important;
}

test {
    text-shadow: var(--text-shadow) !important;
}

test {
    text-shadow: none !important;
}

}


/*Post probleem*/@-moz-document url("https://www.gamekings.tv/"), url("https://www.gamekings.tv/category/videos/tv-afleveringen/"), url("https://www.gamekings.tv/category/nieuws/"), url("https://www.gamekings.tv/category/videos/"), url("https://www.gamekings.tv/meer-alles/") {

/*Post*/
    .post__thumb:not(:hover) {
        opacity: .5;
    }

    .post--horizontal:hover .post__thumb, .postcontainer > div:not(.post--horizontal):hover .post__thumb {
        opacity: 1;
    }

   .post--horizontal {
        background: linear-gradient(to right, transparent 230px, rgba(27, 27, 27, .5) 230px);
    }

   .post--horizontal:hover {
        background: linear-gradient(to right, transparent 230px, rgba(6, 114, 238, .5) 230px);
    }

/*     .post.post--horizontal {
        margin-bottom: 10px;
    } */
    
    
   .postcontainer > div:not(.post--horizontal) {
        background: linear-gradient(transparent 130px, rgba(27, 27, 27, .5) 130px);
    }

   .postcontainer > div:not(.post--horizontal):hover {
        background: linear-gradient(transparent 130px, rgba(6, 114, 238, .5) 130px);
    }

    .post--horizontal:hover a:not(.post__titlelink--yellow), .postcontainer > div:not(.post--horizontal):hover a {
        color: white;
    }
/*Post*/
}


