css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("budgetgaming.nl") {

* {
    box-shadow: none !important;
    border-radius: 0 !important;
}

body, html {
    background: url("http://www.gameranx.com/images/wallpapers/dmc/devil-may-cry-wallpapers-1-hd-1080p.jpg") fixed no-repeat center top / auto 810px !important;
}

/*Transparent + no border*/ #content, #container, #header, #footer, .filter, .list ul li.itemrow, .page-content, .table-shop tr td, #consolemenu{
    background: none !important;
    border: 0 !important;
}

/*No border*/ .table-search tr td, th.game, .prijs, .goedkoopstbij, #menu ul li, #consolemenu ul li, #consolemenu ul li a, .gamereactie-meta, #xgn .news, #xgn .score, #xgn .media  {
    border: none !important;
}

h1.pagetitle {
    margin: 0;
    padding-left: 10px;
}

/*Passend maken*/
li.console {
    margin: 0 -5px;
}

#consolemenu > ul > #prijslijst {
    background-image: url("http://www.budgetgaming.nl/images/menubg.jpg") !important;
    height: 48px;
}

/*header*/
#consolemenu ul {
    background: linear-gradient(rgba(255, 255, 255, .6), rgba(100, 100, 100, .8)) !important;
}

#menu {
    background: linear-gradient(rgba(100, 100, 100, .8) 30%, rgba(50, 50, 50, .9)) !important;
}
/*/header*/

/*Kader white*/ .table-search thead, .table-search tbody tr, .table-shop tbody tr, .list ul, .gamereactie, #xgn .news, #game-info, h1.pagetitle, div.gamereactie-content > div > div, .page-content {
    background: rgba(255, 255, 255, 0.3) !important;
}

/*Search*/#searchfield input {
    background-color: rgba(255, 255, 255, 0.3) !important;
    border: 2px solid rgba(255, 0, 0, 1) !important;
}


/*All Font black*/ a, h1, h2, h3, span, div, li {
    color: black !important;
    /*text-shadow: 0px 0px 3px white, 0px 0px 3px white, 0px 0px 3px white !important;]*/
    font-weight: bold !important;
}

.titel h2 span, .tabtitel ul li a.current span {
text-shadow: none !important
}

