css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("imdb.com") {

#wrapper, #pagecontent, #nb20,
.recently-viewed, #footer,
input, .poster, .message,
.moreResults, select, .datepicker a,
.header .nav, .winner, 
#filmography .filmo-row, 
#filmography .head, .faq div
{
background: #000000 !important;
}
.cast_list tr, .boards tr,
.rec-jaw-upper, .list_item,
.news_item, .list-preview
{
background: transparent !important;
}
.rating, .rating-list, .minPosterWithPlotSummaryHeight
{
background:#000489 !important;
}
#titleMediaStrip, #navbar,
#titleRecs, #titleCast, #titleStoryLine,
#titleDetails, #titleDidYouKnow, #navMenu3,
#titleFAQ, #titleUserReviewsTeaser, #boardsTeaser, .article,
#navTitleMenu, #navNameMenu, #navNewsMenu, #navWatchlistMenu,
.subNavListContainer, #latestHeadlines,
.aux-content-widget-2, .aux-content-widget-3,
.newsTabs li a, .list_tabs li a,
.results td, .plot_summary 
{
background: -moz-linear-gradient(#000489, #000123)!important;
border: 1px #000489 solid !important;
}
#sidebar
{
background: -moz-linear-gradient(#000489, #000123)!important;
}
.even, .oddm, .titleReviewBar 
{
background-color: #000000 !important;
border: 1px #000000 solid !important;
}
.heroic-overview .plot_summary_wrapper
{
border-top: 2px #000000 solid !important;
}
#content-2-wide::before
{
border-left: 2px #000000 solid !important;
}
.list_item, .results .odd
{
border-top: none !important;
border-left: none !important;
border-right: none !important;
border-bottom: 1px #FFFFFF solid !important;
}
table.cast_list td
{
border-top: 1px #000000 solid !important;
border-bottom: 1px #000000 solid !important;
border-left: 1px #000000 solid !important;
border-right: 1px #000000 solid !important;
}
a, p, strong, small, input, select, option,
h1, h2, h3, h4, h5, .time, th, .subText, .popularityDown,
.comment-meta, .txt-block, .text-block, .odd div, .even div,
.rec-cert-genre, .rec-elipsis, .nobr, .star-box-details,
.star-box-rating-label, .itemprop, .yn, .infobar, .absmiddle,
.play_quiz_widget span, .us_tv_pg, .titlePageSprite,
.photo_strip, .article, .desc, .mellow, #left, .outline,
.head, .spoilers, .summary_text, .titleReviewBarItem 
{
color: #FFFFFF !important;
text-shadow: none !important;
}
a:hover
{
color: #FFFFFF !important;
text-shadow: none !important;
text-decoration: underline !important;
}
}
