css:
@-moz-document url-prefix("http://www.ohmydollz.com/forum") {
#brdmenu {
    background-image: url("http://image.noelshack.com/fichiers/2014/25/1403074141-main-menu.png");
    background-repeat: no-repeat;
    height: 30px;
}

#brdwelcome {
    background-image: url("http://image.noelshack.com/fichiers/2014/25/1403073290-brd-visit.png");
    background-repeat: no-repeat;
    color: #000000;
    font-weight: bold;
    height: 60px;
    padding: 10px;
    width: 950px;
}

#contenu {
    background: url("http://sournoishack.com/uploads/793291810fond2.png") no-repeat fixed 50% 0 rgba(0, 0, 0, 0);
    background-size : cover;
    height: 100%;
    margin: 0;
    padding: 3px;
    width: 100%;
}

#footer_box {
    background-image: url("http://image.noelshack.com/fichiers/2014/25/1403082624-brd-about.png");
    background-repeat: no-repeat;
    height: 100px;
    width: 950px;
}

#head_forum {
    background-image: url("http://sournoishack.com/uploads/725206445BAN4.png");
    background-repeat: no-repeat;
    height: 300px;
    position: relative;
    width: 950px;
}

#head_links {
    bottom: 13px;
    left: 508px;
    position: absolute;
}

#navlogout a {
    border-color: #ffffff;
    color: #8061d6;
}

.box {
    border: 1px solid #7d23da;
}

.pun H2, #brdmenu {
    background-image: url("http://image.noelshack.com/fichiers/2014/25/1403075703-main-head.png");
    background-repeat: no-repeat;
    color: #FFFFFF;
    height: 22px;
    width: 100%;
}

.pun TABLE, .pun DIV, .pun FORM, .pun P, .pun H1, .pun H2, .pun H3, .pun H4, .pun PRE, .pun BLOCKQUOTE, .pun UL, .pun OL, .pun LI, .pun DL, .pun DT, .pun DD, .pun TH, .pun TD, .pun FIELDSET, .pun IMG {
    list-style: none outside none;
    margin: 0;
    padding: 0;
}

.pun TH {
    background-image: url("http://image.noelshack.com/fichiers/2014/25/1403081672-fondtitres.png");
    background-repeat: no-repeat;
    color: #000000;
    font-size: 13px;
    height: 100%;
    text-align: center;
}

DIV.box, .pun BLOCKQUOTE, DIV.codebox, #adminconsole FIELDSET TH, .rowodd, .roweven {
    background-color: #ffa0da;
}

DIV.icon {
    background-image: url("http://sournoishack.com/uploads/134157119soda.png");
    background-repeat: no-repeat;
    float: left;
    height: 49px;
    width: 40px;
}

DIV.inew {
    background-image: url("http://sournoishack.com/uploads/161485928soda2.png");
    background-repeat: no-repeat;
    height: 49px;
    width: 40px;
}

DIV.postright, DIV.postfootright {
    border-color: #B059E0;
    border-left: 18em solid #FABEE2;
}

dd.postavatar {
    background-color: #ffbdfd;
}

dd.usertitle {
    background-color: #ffbdfd;
}

div.box {
    border-color: #ac0ecc;
}

div.inbox {
    color: #ffffff;
}

div.inbox div a {
    color: #8b33ff;
}

div.incqbox p {
    background-color: #c371e3 ;
    color: #ffc2ff ;
}

div.infldset.txtarea label textarea {
    margin-left: 5px;
}

div.inform table {
    margin-left: 5px;
    margin-top: 5px;
}

div.postfootright ul {
    background-color: #fa99fa;
}

div.postfootright ul hr {
    background-color: #ffbdfd;
}

div.postleft dl dd {
    background-color: #ffbdfd;
    color: #9268d1;
}

div.postleft dl dt {
    background-color: #ffbdfd;
}

div.postmsg p {
    background-color: #ff9cff;
    color: #750175;
}

div.postsignature {
    background-color: #fa9bfa;
}

div.tclcon {
    color: #470447;
}

dt strong a {
    color: #580eb8;
}

element.style {
}

span.byuser {
    color: #78056b;
}

td.tc2 {
    background-color: #fa9efa;
    color: #ffffff;
}

td.tc3 {
    background-color: #fa9efa;
}

td.tcr a {
    color: #f03096;
}

th.tc2 {
    background-color: #ffffff;
}

th.tcl {
    background-color: #ffffff;
    border-color: #7d007d;
    color: #000000;
}

th.tcr {
    color: #000000;
}

tr td fieldset {
    margin-right: 5px;
}

ul li a {
    color: #8e59ff;
}

ul.conl li {
    color: #efcfff;
}

ul.conr li a {
    background-color: #791ac7;
    color: #ffffff;
    text-decoration: none;
}

user agent stylesheetdiv {
    display: block;
}

div.postleft dl {
    background-color: #ffbdfd ;
}

div.postfootleft {
    background-color: #ffbdfd;
}

div.inbox {
    background-color: #ff9cff;
}

div.postfootright {
    background-color: #ff9cff;
}



}
