css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("(.*?club\.tgfcer\.com\/thread.*?)|(.*?club\.tgfcer\.com\/viewthread.php.*?)") {
    #foruminfo{
        position: relative;
        top:-30px;
    }
    .mainbox.viewthread>h1 {
        display: block;
        height:120px;
        width:120%;
        position: fixed;
        top:0px;
        left:0%;
        z-index:50;
        background:#3F51B5;
        font-size: 30px;
        padding: 15px;
        border-radius: 3px;
        box-shadow: 1px 1px 3px #999;
    }
    .mainbox.viewthread{
        width:960px;
        margin:0 auto 15px auto;
        position: relative;
        border:none;
        background: #fff;
    }
        .mainbox.viewthread *
        {
            border:none;
            text-decoration: none;
        }
    .mainbox.viewthread span.headactions
    {
        position: fixed;
        display: block;
        top:0px;
        right:0%;
        z-index: 60;
    }
    .mainbox.viewthread span.headactions a{
        background-size:30% 30%;
        background-image:none;
        display:inline-block;
        padding:5px 10px;
    }
    .mainbox.viewthread span.headactions a:hover{
	      background: -webkit-radial-gradient(center center, circle, #f00 30%, #0f0 60%, #00f 80%) center center no-repeat;
        background: -moz-radial-gradient(center center, circle, #3F51B5 35%, #fff 60%, #3F51B5 90%) center center no-repeat;
	      background-size:300% 300%;
	      transition:background-size 0.5s linear;
        display:inline-block;
        padding:5px 10px;
        box-shadow: 0px 0px 3px #fff;
    }
    .mainbox.viewthread>table{
        position: relative;
        z-index: 60;
    }
    .mainbox>table>tbody>tr:last-child{
        display: block;
        height: 0px !important;
        overflow:hidden;
    }
    .mainbox>table>tbody>tr:first-child td.postauthor a.dropmenu
        {
            color:#7986CB;
        }
    .mainbox>table>tbody>tr:first-child td.postauthor {
        background:#fff !important;
        height:180px;
        width:200px;
        display: block;
        overflow: hidden;
        transition: 0.5s;
        box-shadow: 0px 0px 6px #fff
    }
    .mainbox>table>tbody>tr:first-child td.postauthor:hover {
        height:520px;
        transition: 0.5s;
        background:#fff !important;
        box-shadow: 0px 0px 3px #999;
    }
    
    .mainbox>table>tbody>tr:first-child td.postcontent{
        width:750px;
        padding:15px;
        background:none transparent;
    }
    .mainbox .postmessage.defaultpost
    {
        margin: 60px 0 0 0;
        box-shadow: 1px 1px 6px #999;
        background:#f9f9f9;
    }
    .mainbox .postmessage .t_msgfont
    {
        font-size: 16px;
        color:#333;
        word-spacing: 3px;
        letter-spacing: 2px;
    }
    .mainbox .postmessage .t_msgfont a
    {
        color:#7986CB;
        background:#fff;
        display: inline-block;
        padding:0 3px;
        border-radius: 3px;
        box-shadow: 1px 1px 3px #999;
        transition: 0.3s;
    }
    .mainbox .postmessage .t_msgfont a:hover
    {
        box-shadow: 1px 1px 9px #999;
    }
    .mainbox.viewthread .postinfo
    {
        position: absolute;
        right:-100px;
        height:54px;
        line-height: 54px;
        width:220px;
        overflow: hidden;
        background: rgba(255,255,255,0.8) !important;
        margin:0px;
        padding:0px 0px 0px 15px;
        background:none transparent;
        border-radius: 27px;
        transition: 0.5s;
    }
    .mainbox.viewthread .postinfo em,.mainbox.viewthread .postinfo a
    {
        display: inline-block;
        padding: 5px 10px;
        margin: 5px;
        line-height: 30px !important;
        text-align: center;
    }
    .mainbox.viewthread .postinfo em
    {
        position: relative;
        top:-60px;
        display: none;
    }
    .mainbox.viewthread .postinfo:hover
    {
        background: rgba(255,255,255,1) !important;
        transition:0.5s;
        width:850px;
    }
    .mainbox.viewthread .postinfo:hover em
    {
        display: inline-block;
        top:0px;
    }
    .mainbox.viewthread .postinfo strong
    {
        display: block;
        width:50px;
        height:50px;
        border:2px solid #3F51B5;
        line-height: 50px !important;
        margin:0px;
        padding:0px;
        text-align:center;
        border-radius: 27px;
        background:#fff;
    }
    .postauthor .avatar {
        background: #fff;
        border-radius: 50%;
        box-shadow: 1px 1px 6px #999;
        height: 120px;
        margin: 0 auto 10px;
        max-width: 120px;
        vertical-align: middle;
        position: relative;
    }
    .postauthor .avatar img   {
        display: block;
        margin:auto;
        position: absolute;
        top:0px;
        bottom:0px;
        left:0px;
        right:0px;
    }
    .userinfopanel{
        top:5px !important;
        left:180px !important;
    }
    #postform
    {
        display: block;
        width:60px;
        height:60px;
        position: fixed;
        bottom:50px;
        right:50px;
        overflow: hidden;
        box-shadow: 1px 1px 3px #999;
        transition:width 0.6s,height 0.6s;
        z-index: 90;
        border:none;
        background:transparent;
    }
    #postform:hover
    {
        width:80%;
        height:415px;
        position: fixed;
    }
    #postform #quickpost>h4
    {
        height:60px !important;
        width:60px;
        display: inline-block;
        background:#fff;
        position: absolute;
        top:0px;
        left:0px;
        margin:0px;
        padding:0px;
        border-radius: 50%;
    }
}
