@-moz-document url("https://facebook.com"), url-prefix("https://recure.facebook.com"), domain("apps.facebook.com"), regexp("https?://www\\.facebook\\.com/(?!plugins/).*") {

/* Bluebar, top bar */
	/* The bar itself */
		#blueBar
		{
			box-shadow:
				inset 0 -10px 15px -5px rgba(0, 0, 0, 0.3),
				inset 0 10px 15px -5px rgba(0, 0, 0, 0.3),
				inset 0 22px 10px rgba(255, 255, 255, 0.0)
					!important;
				
			border-bottom:
				1px solid #000
					!important;
		}

		#blueBar:after /* Removes that 1px underline above the bluebar */
		{
			background-image:
				none
					!important;
		}

		._56lq, ._55bh:after /* Small spacing between the nav buttons, small border at the left of the menu-pulldown */
		{
			display:
				none
					!important;
		}

	/* Friend requests | Messages | Notifications objects effects on hover */
	.objectListItem, .messagesContent, .anchorContainer a
	{
		transition:
			0.3s;
	}

	.objectListItem:hover, .messagesContent:hover, .anchorContainer a:hover
	{
		box-shadow:
			inset 0 0 0 1px rgba(0, 0, 255, 0.5),
			inset 0 0 5px rgba(0, 0, 0, 0.3)
				!important;
		
		background:
			rgba(0, 0, 0, 0.04);
	}
		
	/* Bar nav buttons */
		/* Nav links (home, post, menu-pulldown) */

		.headerTinymanPhoto, .fbxWelcomeBoxBlock .fbxWelcomeBoxImg /* The picture in the "Name" button, the picture in the left bar */
		{
			border-radius:
				100px
					!important;
		
			box-shadow:
				0 0 0 1px #000;
		}
		

		._5bfg #pageNav._5bfj .tinyman .navLink:after, ._5bfg #pageNav._5bfj .tinyman .navLink:hover:after, .litestandClassicWelcomeBox .fbxWelcomeBoxBlock:after /* A border around the profile image icon, this removes it */
		{
			background:
				none
					!important;

			border:
				none
					!important;
		}

		#pageNav .navLink
		{
			height:
				30px
					!important;
			
			padding-right:
				8px
					!important;
			
			margin-top:
				-12px
					!important;
			
			transition:
				0.2s;
			
			padding-top:
				10px
					!important;
			
			background:
				#526da4
					!important;
			
			box-shadow:
				0 0 0 1px #000,
				inset 0 20px 15px -15px rgba(0, 0, 0, 0.6),
				inset 0 -20px 15px -15px rgba(0, 0, 0, 0.4),
				inset 0 0 0 1px rgba(255, 255, 255, 0.3);
			
			border-radius:
				0 0 8px 8px
					!important;
			
			z-index:
				1;
			
			margin-right: /* This stops the nav buttons from moving left when hovered */
				0px
					!important;
		}

		._59fc /* Privacy button div, for some reason .navLink doesn't apply the width. This forces it to be applied */
		{
			width:
				30px
					!important;
		}

		#privacyFlyoutLabel /* Privacy button icon, this moves it a bit to the right, so it gets centered into the button */
		{
			margin-left:
				10px
					!important;
		}

		#pageLogo /* F button effects */
		{
			height:
				36px
					!important;

			width:
				36px
					!important;
			
			margin-top:
				-8px
					!important;
			
			transition:
				0.2s;
			
			padding-top:
				10px
					!important;
			
			background:
				#526da4
					!important;
			
			box-shadow:
				0 0 0 1px #000,
				inset 0 25px 15px -15px rgba(0, 0, 0, 0.6),
				inset 0 -20px 15px -15px rgba(0, 0, 0, 0.4),
				inset 0 0 0 1px rgba(255, 255, 255, 0.3);
			
			border-radius:
				0 0 8px 8px
					!important;
		}

		
		/* Jewel Buttons, this sets their initial position */
		#fbRequestsJewel, #fbMessagesJewel, #fbNotificationsJewel
		{
			transition:
				0.2s;
			
			margin-top:
				-7px
					!important;
			
			padding-right:
				3px;
			
			padding-left:
				3px;
			
			box-shadow:
				0 0 0 1px #000;
			
			height:
				30px 
					!important;
			
			padding-top:
				10px
					!important;
			
			background:
				#526da4
					!important;
			
			box-shadow:
				0 0 0 1px #000,
				inset 0 20px 15px -15px rgba(0, 0, 0, 0.6),
				inset 0 -20px 15px -15px rgba(0, 0, 0, 0.4),
				inset 0 0 0 1px rgba(255, 255, 255, 0.3);
			
			border-radius:
				0 0 8px 8px;
			
			margin-bottom:
				10px
					!important;
			
			margin-left:
				5px;
			
			margin-right:
				5px;
		}
		
		/* This changes the position of the menu that pops up when you click a jewel, so it fits the button when it is pulled down, a quick-fix */
		.jewelFlyout
		{
			margin-top:
				15px;
		}

		#navAccount .menuPulldown /* This to fix the location of the button icon */
		{
			margin-top:
				15px
					!important;
			
			margin-left:
				5px
					!important;
			
			margin-right:
				4px
					!important;
		}

		#navAccountLink /* This to fix the height of the button to match the other nav buttons' alignment, brazilian "gambiarra" */
		{
			top:
				-4px;
				
			box-shadow:
				0 0 0 1px #000,
				inset 0 25px 15px -15px rgba(0, 0, 0, 0.6),
				inset 0 -20px 15px -15px rgba(0, 0, 0, 0.4),
				inset 0 0 0 1px rgba(255, 255, 255, 0.3)
					!important;
		}

		#navAccountLink /* There's a border in the nav account button, this removes it */
		{
			border:
				none
					!important;
		}

		#pageNav .navLink:hover, #fbRequestsJewel:hover, #fbMessagesJewel:hover, #fbNotificationsJewel:hover, #pageLogo:hover /* This makes the nav buttons go down when hovered */
		{
			padding-top:
				20px
					!important;
					
			background:
				#526dc4
					!important;
		}

		#pageNav .navLink:active, #fbRequestsJewel:active, #fbMessagesJewel:active, #fbNotificationsJewel:active, #pageLogo:active /* This adds an active effect to the nav buttons */
		{
			background:
				#203ba2
					!important;
		}
/* Here ends the bar itself */






/* News feed stuff */
		.uiTypeahead .uiTextareaAutogrow, #composerCityTagger, ._1dsr, ._9lb, #pagelet_composer .uiSelectorButton /* "Post" screen transitions */
