css:
@-moz-document domain("pikabu.ru") {
/*pikabu.ru DanillaTech style. More styles and cool stuff in my publick vk.com/klavomania */
.personals {
    width: 250px !important;
    background: none !important;
}

.personals ul li a, .personals ul li span {
    width: 250px !important;
}

li.active2 a, li.active1 a, li.active3 a, li.active4 a {
    width: auto !important;
}


ul.header_links li a {
    font-size: 14px  !important;
}


div.text_story_div {
    font-size: 18px  !important;
    width: 800px  !important;
}



input.submit_button {
    height: 40px !important;
}

div.comment_msg.comment_desc {
    font-size: 16px  !important;
}

div.comment_desc {
    font-size: 17px !important;
}

textarea.b-textarea.b-textarea_width_100 {
    font-size: 17px !important;
    width: 700px !important;
}

button.b-button.b-button_type_default.b-comment-reply__submit {
    font-size: 17px !important;
    height: 40px !important;
    width: 400px !important;
}

td.info.info_c noindex {
    font-size: 16px !important;
}

button.b-button.b-button_type_link.b-comment__show-reply {
    font-size: 16px !important;
}

td.info.info_c noindex a {
    font-size: 16px !important;
}

input.search_block {
    font-size: 16px !important;
}

input.submit_button_freshitems {
    font-size: 16px !important;
}

input {
    font-size: 16px !important;
}

button {
    font-size: 16px !important;
}

div.main_comm {
    width: 800px !important;
}

div.short {
    font-size: 15px !important;
}

input.add_story_cn_textarea {
    height: 40px !important;
    font-size: 20px !important;
    width: 600px !important;
}

textarea {
    font-size: 16px !important;
}
A {text-decoration: none !important; color: #170}
A:hover {color: #A60}

div {font-family: tahoma, sans-serif !important; font-size: 17px !important}
* {line-height:136% !important; font-size: 16px !important}
h1 {font-size: 18px !important} 
}
