@namespace url(http://www.w3.org/1999/xhtml);

/*Main*/ @-moz-document domain("www.metronieuws.nl") {

* {
    box-shadow: none !important;
/*     border-radius: 0 !important; */}

body {
    background: url('http://i.imgur.com/Cl9gKcU.jpg') fixed no-repeat right 25% / cover;
}

/*Border none*/ test {
    border: 0;
}
    
.css-p4d99j, .css-1u61t7o , .css-ag6mj8 {
    border-radius: 0;
}

/*Display none*/ test {
    display: none; 
}

/*Header*/ 
    .css-fc66n7 {
        position: absolute;
        background-color: rgba(150, 150, 150,.5);
    }
    
    .css-1gk2oin {
/*         max-width:  85%; */
    }
    
    /*Button*/ li.css-1l1nddq:not(:hover) > a {
        opacity:.5;
    }
    
    /*Button*/ li.css-1l1nddq:nth-child(1) > a {
        opacity: 1 !important;
    }
/*Header*/

/*Content*/
    /*Overlay*/.css-18pnsyk  {
        background-color: rgba(243, 243, 243, .45) !important;;
    }

    .css-1u61t7o > picture > img:not(:hover), div.css-fre7fl > a > div > div > picture > img {
        opacity:.5;
    }
    
    div.css-fre7fl > a > div:hover img {
        opacity: 1 !important;
    }

    .css-p4d99j {
        background: linear-gradient(to top, rgba(255, 255, 255,.5) 48.5%, transparent 48%);
    }
/*Content*/

}


/*Front-pages*/ @-moz-document url("https://www.metronieuws.nl/"), regexp("https://www.metronieuws.nl/(video|werkgeld|relatieseks|Columnisten|nieuws/dossier|food|techgadgets|showbizz|tfravel|sport|horoscoop|commercie/winacties|lezerscolumn|nachtboek-van-een-eerstejaars|nieuws)") {

    .region-center {
        transform: scale(var(--scale),1.7);
        transform-origin: center 0;
        margin-top: 10px;
    }

}


/*Nieuws Item*/ @-moz-document url-prefix("https://www.metronieuws.nl/nieuws/") {
    .css-s6kj4u {
        background: linear-gradient(transparent 440px, rgba(255, 255, 255,.5) 440px) !important;
    }
 
    img.lazyloaded:nth-child(5):not(:hover) {
        opacity:.5 !important;
    }
    
    .css-148rk96, .css-1msbwu4, .css-1jsoxct, .css-pk4k7f, .css-s6kj4u {
        border-radius: 0;
    }
    
    .region-sidebar_left, .region-top {
        display: none;
    }
    
    .css-6bxyg2 {
        transform: scale(var(--scale),1.7);
        transform-origin: center 0;
        margin-top: 20px;
        display: block;
        width: 648px;
    }
    
