css:
@-moz-document domain("rsdn.ru") {
/* colors: #BCB891, 7C7A60, 3D3B2F, 333227, 302F25 */

/*----------------------------------------------------------------------
 *
 * wheat on the clay
 * ^^^^^^^^^^^^^^^^^
 * by kris kaspersky
 *--------------------------------------------------------------------*/

a	{ color:wheat; border-bottom: none }
hr	{ border-color:wheat;} 
body { color: #BCB891; background-color: #302F25!important; }
table { color: #BCB891!important; background-color: #302F25!important; border-collapse: collapse!important; border-color: #3D3B2F!important;}
td, tr, td {border-color: #7C7A60!important; color: #BCB891!important; background-color: #302F25!important;} 

li {color:wheat!important;}
a:link, a.m {color:#BCB891;text-decoration: none;}
a:visited {color:#7C7A60!important;text-decoration: none;}
a:hover {color:wheat!important; text-decoration:none;}

input
{
  color: wheat!important;
  border-color:#BCB891!important;
  background-color: #7C7A60!important;
  outline: none; /* remove the glowing blue border around input that some browsers put when it is in focus */
}

select, textarea
{
	outline: none; /* remove the glowing blue border around a textarea that some browsers put when it is in focus */
	color: #BCB891!important;
	background-color: #302F25!important;
	border-color: #7C7A60!important;
}

div.form { border-bottom-color: wheat!important; }


::selection {
    background-color: #BCB891;
    color:  wheat;
}


frameset
{
  border-top-color:#7C7A60!important;
  border-left-color:#7C7A60!important;
  border-right-color:#7C7A60!important;
  border-bottom-color:#7C7A60!important;
}

div#left-block {background-color:wheat!important;}
div#tree-toolbar {color:wheat!important; background-color:#7C7A60!important; }
span#tree-toolbar-title {color:wheat!important; background-color:#7C7A60!important; }


/* ---- main page ---- */
/* span.site-name {color:wheat!important;} */
span.subtitle { color:wheat!important;}
div.info-box {color:wheat!important; border-color:#BCB891!important;}
div.box-title { color:wheat!important; background-color: #7C7A60!important;} 
ul.refs, li:before { color:wheat!important; background-color: #302F25!important;} 
/* div.news, */ span.item-subj {color:#BCB891!important;}
div#account-name {text-shadow:none!important;color:#302F25!important;}

span.item-date {color:wheat!important;}


/* ------  forums: table header -------- */
div.hdr {color:wheat!important;} /* = Messages = */
th {background-color: #7C7A60; color:wheat!important;} 

/* msg list, # of replies */
form table#messageList span[style="color:Black;font-weight:normal;"]
{
	color:wheat!important; border-color: #BCB891;
}


/* msg flat */
td.s {color:wheat!important;} /* list header */
tr.odd * {background-color: #3D3B2F!important;}

/* div.hdr {color:wheat;} */

/* --- subject header line ---- */
div.msg-hdr, div.right-tb {background-color: #3D3B2F!important;} 
a.hdr {color:wheat!important;} /* msg from */
font[color="#000000"] { color:wheat!important;} /* admin */
span.anonym {color:wheat!important;} 
font[color="#808080"] { color:#7C7A60!important;} /* anonym */

a.rate-list-ref {color:wheat!important;} /* msg rate */
div.subj {color:#BCB891!important; background-color: #3D3B2F!important;}

/* msg body */
div.m {color:#BCB891; background-color: #302F25!important}
div.o, div.q, td.o  {background-color: #302F25!important; color: #7C7A60;}
blockquote, blockquote.q {background-color: #3D3B2F!important;margin-left:0px!important;}
div.c {background-color: #302F25!important; color: #7C7A60!important; border-color:#7C7A60!important;}
tt {    background-color: #7C7A60!important; color:  wheat!important;}
/* code */
pre {color:#BCB891;}
span.kw {color:wheat!important;}
span.str {color:wheat!important;}
code.c {color:#BCB891!important; background-color:#302F25!important; border: 1px solid #7C7A60!important;}

/* [1] 2 3 .. */
span.page-num.cur {color:#3D3B2F; background-color: #302F25!important}
a.page-num {background-color: #302F25!important; border:none; color:wheat;}
/* --- end of flat ---- */



/* msg editor */
table#tbl.nm, table#tbl.nm * { background-color: #302F25!important; color:#BCB891!important;}
textarea.nm {border-color: wheat!important;}

div.search-results {width:100%; overflow:scroll;}


/* ---- articles ---- */
h1, h2, h3, h4 {color:wheat!important;}
span.title, span.value {color:wheat!important;}

span.KEYWORD {color:wheat!important;}
span.STRING {color:wheat!important;}
font[color="black"] { color:wheat!important;} 

/* ----- search ----- */
div.found-count {color:wheat!important;}
div.search-doc-url {color:wheat!important;}
div.paging-control {color:#7C7A60!important;}
i.pager-next {color:wheat!important;}

/* ---- Search (Google) ----- */
span#info-page-title {color:wheat!important;}
div.footer, div.search-box-footer  {color:wheat!important;}
div.gs-result, div.gs-title, div.gs-result, div.gs-title *
{
  color:wheat!important;
}

div#search-form
{
  border-color:wheat!important;
  background-color:#302F25!important; 
}

button#search-btn
{
  color:wheat!important;
  border-color:wheat!important;
  background-color:#302F25!important; 

}

/* --- google search ---- */
div.gs-webResult, div.gs-snippet, div.gs-imageResult, div.gs-snippet, div.gs-fileFormatType
{
  color:#BCB891!important;
}

div.gsc-tabsArea
{
	border-color:wheat!important;
}

div.gsc-control-cse
{
  border-color:wheat!important;
  background-color:#302F25!important;
}

div.gsc-tabHeader.gsc-tabhActive
{
  background-color:#302F25!important;
  border-color:wheat!important;
  color:wheat!important;

}

div.gsc-tabHeader.gsc-tabhInactive
{
  background-color:#BCB891!important;
  border-color:wheat!important;
  color:#302F25!important;
}

div.gsc-result-info
{
	color:wheat!important;
}

div.gsc-orderby-label
{
  color:#BCB891!important;
}

div.gsc-selected-option-container
{
  color:wheat!important;
}

div.cse, div.gsc-results, div.gsc-results
{
	background-color:#302F25!important;
}

div.gsc-webResult.gsc-result, div.gsc-results div.gsc-imageResult
{
    border-color:wheat!important;
	background-color:#302F25!important;
}

div.gsc-results, div.gsc-cursor-box, div.gsc-cursor-current-page, div.gsc-cursor-page
{
  color:wheat!important;
  border-color:wheat!important;
  background-color:#302F25!important;
}


/* ---- pop ups ----- */
div.short-profile, div.avatar, h3
{
 
  background-color:#302F25;
}


h3.cluetip-jtip, h3.ui-cluetip-header
{
 /* border-color:wheat!important; */
  background-color:wheat!important;
}


div.ui-widget-content, h3.ui-widget-content, div.cluetip-outer
{
  /* border-top-color:wheat; */
  background-color:#302F25!important;
}

/* ---- my profile ----- */
*.ui-tabs, *.ui-tabs-nav
{
  background-color:#302F25!important;
  border-color:wheat!important;
  
}

*.ui-widget-header
{
  color:red!important;
  border: 1px solid #302F25!important;
  background:#7C7A60!important; 
}

*.cluetip-inner.ui-widget-content.ui-cluetip-content,
*.cluetip-title.ui-widget-header.ui-cluetip-header, *.cluetip-outer
{
  border: 1px solid #BCB891!important;
}

*.ui-tabs, *.ui-tabs-nav, li.ui-tabs-active, *.ui-tabs-anchor, *.ui-widget-content
{
	background-color:#302F25!important;
	border-color:wheat!important;
	color:wheat!important;
  	border: 1px solid #302F25!important;
  	background:none;

}

span.label
{
  	color:wheat!important;
}

span.paging-control
{
  color:#BCB891!important;
}
}
