css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gog.com") {
  
  /* GOOD OLD LOGO */
  .top-nav__logo {
    background-image: url("https://i.imgur.com/lh0M7YB.png");
    background-position: 6px 3px;
    background-repeat: no-repeat;
    background-color: #333;
  }
  .top-nav__logo:before {
    background-image: none;
  }
  
  /* MENU BAR → thin */
  .top-nav {
    height: initial;
    line-height: 41px;
  }
  .top-nav__logo, .top-nav__home, .top-nav__extras, .top-nav-extras-search-holder, .top-nav__cart-social, .top-nav__cart, .top-nav__social, .top-nav__item, .top-nav__login-inner {
    height: 100%;
    line-height:inherit;
  }
  .nav-spacer {
    padding-top: 36px;
  }
  .top-nav__item:first-child {
    margin-left: -17px;
  }
  
  /* HEADER → thin */
  .productcard-wrapper {
    background-position: 0 -44px;
  }
  .productcard-wrapper::before {
    background-image: linear-gradient(rgba(0, 0, 0, 0.3), rgba(0, 0, 0, 0), rgba(0, 0, 0, 0));
    content: "Wisdom will save us all";
    height: 280px;
    position: absolute;
    width: 100%;
  }
  .header {
    color: white;
    height: 20px;
    width: 100%
  }
  .header__title {
    text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.32);
  }
  .header__info {
    text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.42);
    font-weight: bold;
  }
  .header__rating {
    color: #FFE230;
  }
  .screens__big {
    box-shadow: 0 3px 5px rgba(0, 0, 0, 0.47);
  }
  
  /* BACKGROUNDS → stop floating in grey soup, you text */
  .product--game .module--buy, .product--game .social-btn, .product--movie .module--buy, .product--movie .social-btn {
    background-color: rgba(227, 227, 227, 0.8);
  }
  .product--game .description, .product--game .reviews, .product--game .product-details, .product--game .sysreq, .product--movie .description, .product--movie .reviews, .product--movie .product-details, .product--movie .sysreq {
    background-color: rgba(227, 227, 227, 0.8);
    box-shadow: 1px 3px 6px rgba(0, 0, 0, 0.2);
    padding: 15px;
  }
  .product--game .description, .product--game .reviews, .product--movie .description, .product--movie .reviews {
    margin-top: 40px;
  }
  
  /* FIXES */
  .page_account .header{
    height: 38px;
  }
  body.page_account{
    background-position: 0 -112px;
  }
}
