@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("vk.com") {.fans_idol_row{position:relative!important;display:inline-block!important;height:112px!important;width:112px!important;padding:0!important;border:1px solid #d3d3d3!important;margin:6px 0 0 6px !important;overflow:hidden}.fans_idolph_wrap,.fans_idol_img{width:112px!important;height:112px!important}.fans_fanph{display:none!important}.fans_idol_info{width:112px!important;overflow:hidden!important;line-height:1.5!important;margin-top:-32px!important;font-family:arial,tahoma,verdana!important;font-size:10px!important;-webkit-transition:margin-top 300ms ease!important;-webkit-transition:margin-top 300ms ease!important;-moz-transition:margin-top 300ms ease!important;-o-transition:margin-top 300ms ease!important;transition:margin-top 300ms ease!important}.fans_idol_row:hover .fans_idol_info{margin-top:-64px!important;height:64px!important}.fans_idol_info:hover{margin-top:-64px!important}.fans_idol_lnk{color:#eee!important;font-size:11px;max-height: 32px;font-weight:700;display:block;position:relative;top:50%;transform:translateY(-50%)}.fans_idol_lnk:hover{color:#fff!important;text-decoration:none!important}.fans_idol_size,.fans_idol_status,.fans_idol_name{width:108px!important;color:#D6D6D6!important;height:16px!important;padding:0 2px!important;background:rgba(0,0,0,0.5)!important}.fans_idol_name{white-space:normal!important;text-overflow:ellipsis!important;width:108px!important;height:32px!important;overflow:hidden!important;text-overflow:ellipsis!important;-o-text-overflow:ellipsis!important;font-weight:400!important;color:#eee!important}.fans_fanph_wrap,.fans_idolph_wrap{position:static!important}.fans_idol_sub,.fans_idol_unsub{position:absolute!important;top:2px!important;right:2px!important;width:13px!important;height:13px!important;padding:3px 2px 3px 4px !important;background-color:rgba(0,0,0,0.4)!important;-webkit-border-radius:2px!important;-moz-border-radius:2px!important;border-radius:2px!important;margin:0!important}.fans_idol_sub.button_blue button{cursor:pointer!important;padding:0!important;width:13px!important;height:13px!important;font-size:0!important;opacity:.5!important;background:url(http://vk.com/images/icons/photo_icons.png?2) 0 -11px no-repeat!important;-webkit-transition:all 300ms ease!important;-webkit-transition:all 300ms ease!important;-moz-transition:all 300ms ease!important;-o-transition:all 300ms ease!important;transition:all 300ms ease!important}.fans_idol_sub.button_blue button:hover,.fans_idol_unsub a:hover{opacity:1!important}.fans_idol_unsub a{display:block!important;width:13px!important;height:13px!important;font-size:0!important;opacity:.5!important;background:url(http://vk.com/images/icons/photo_icons.png?2) 0 -77px no-repeat!important;-webkit-transition:all 300ms ease!important;-webkit-transition:all 300ms ease!important;-moz-transition:all 300ms ease!important;-o-transition:all 300ms ease!important;transition:all 300ms ease!important}.fans_rows{text-align:center;padding:16px 10px!important;text-shadow:0 0 2px rgba(0,0,0,1)}.fans_search_focused .fans_row_highlight{background:#565757!important;border-bottom:#565757!important}
}
