scrollbar[orient="vertical"] {
    padding-left: 3px ;
    min-width: 12px ;
    max-width: 12px ;
}

scrollbar[orient="horizontal"] {
    padding-top: 3px ;
    min-height: 12px ;
    max-height: 12px ;
