css:
@-moz-document url-prefix("https://todoist.com/"), url-prefix("https://todoist.com/")  {
	* {
		color: #93a1a1 !important;
	}

	body, table, tr, td, ul, header, footer, blockquote, form, fieldset, a, .main_content, top_bar, left_menu, .invisible_bg, #td_logo, #top_bar, .input_due_date, .due_date_holder, .dp_container, .dp_cal, .AmiMenu, .scheduler_holder, .menu_buttons, #project_list li {
		background-color: #002b36 !important;
	}
#project_list li.current td.name {
		color: #2AA198  !important;
	  text-decoration: underline;
	}
	div {
		background-color: #002b36;
	}
  .cmp_search {
		padding: 0 0 0 0 !important;
  }
	.input_q {
		padding: 0 0 2px 2px !important;
		margin-left: 10px;
	}
	.td_color div {
		border-radius: 12px;
		height: 10px;
		width: 10px;
		margin-top: -2px;
		margin-left: 6px;
	}
		.td_color div img {
			display:none;
		}
	a, a span, h1, h2, h3, h4, h5 {
		color: #2aa198 !important;
	}
	.label {
    display: inline-block;
    margin: 2px 0px 0px;
    font-size: 10px;
    cursor: pointer;
    float: right;
    position: relative;
    top: -19px;
    height: 0px;
	}
	.task_item .task_content_item {
    border: none;
}
	.task_item td {
    padding-top: 3px;
    padding-bottom: 3px;
}
.task_item td.menu {
    padding-top: 1px !important;
    padding-bottom: 1px !important;
}
	#agenda_view .project {
    padding-top: 1px;
}
	 
	h1,h2,h3,h4,h5 {
		border-bottom: 1px solid #073642 !important;
		border-top: 1px solid #073642 !important;
	}

	a:hover {
		background-color: #073642 !important;
	}

	a:visited, a:visited span {
		color: #b58900 !important;
	}


	li a {
		border-bottom: 1px solid #073642 !important;
	}

	.leaflet-control a {
		background-color: #ddd !important;
	}
}
