css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {


html, body {
         background:#fffaf0 !important;
         color:#8b7765 !important;
	 font-family:Verdana !important;
}
hr {	border: 0 !important;
	color:#eee0e5 !important;
	background-color:#eee0e5 !important;
	height: 3px !important;
}
a {
        color:#cd6889 !important;
}
a:visited {
        color:#cd6889 !important;
}
a:hover {
        color:#000000 !important;
}
.adminbar {
        text-align:right !important;
        clear:both !important;
        float:none !important;
}

.logo {
        clear:both !important;
        text-align:center !important;
        font-size:2em !important;
/*        color:#D82E37 !important;*/
        color:#8b5f65 !important;
        width:100% !important;
        height:122px !important;
 /*       background-image: url(../eye.jpg) !important;*/
        background-repeat: no-repeat !important;
        background-position: center center !important;

}

.theader {
        background:#fff0f5 !important;
        text-align:center !important;
        padding:2px !important;
        color:#8b5f65 !important;
        width:100% !important;
}
.postarea {
}
.rules {
        font-size:0.7em !important;
}
.postblock {
        background:#fff0f5 !important;
        color:#8b5f65 !important;
        font-weight:800 !important;
	border: solid 1px #eee0e5 !important;
	padding: 0 !important;
	margin: 0 !important;
}
.footer {
        text-align:right !important;
        font-size:12px !important;
        font-family:serif !important;
}
.passvalid {
	background:#000000 !important;
	text-align:center !important;
	width:100% !important;
	color:#8b7765 !important;
}
.dellist {
	font-weight: bold !important;
	text-align:center !important;
}
.delbuttons {
	text-align:center !important;
	padding-bottom:4px !important;

}
.managehead {
	background:#000000 !important;
	color:#8b7765 !important;
	padding:0px !important;
}
.postlists {
	background:#000000 !important;
	width:100% !important;
	padding:0px !important;
	color:#8b7765 !important;
}
.row1 {
	background:#000000 !important;
	color:#8b7765 !important;
}
.row2 {
        background:#000000 !important;
        color:#8b7765 !important;
}
.unkfunc {
	background:inherit !important;
	color:#8b7765 !important;
}
.filesize {
	text-decoration:none !important;
}
.filetitle {
	background:inherit !important;
	font-size:0.9em !important;
	color:#8b7765 !important;
	font-weight:800 !important;
}
.postername {
	color:#cd6889 !important;
	font-weight:bold !important;
}
.postertrip {
	color:#8b7765 !important;
}
.oldpost {
        color:#8b7765 !important;
        font-weight:800 !important;
}
.omittedposts {
        color:#8b7765 !important;
}
.reply {
	background:#fff0f5 !important;
	color:#8b5f65 !important;
	border: solid 1px #eee0e5 !important;
	padding: 0 !important;
	margin: 0 !important;
}
.doubledash {
 	float: left !important;
 	vertical-align:top !important;
	clear:both !important;
	
}
.replytitle {
	font-size: 0.9em !important;
        color:#8b7765 !important;
        font-weight:800 !important;
}
.commentpostername {
        color:#cd6889 !important;
	font-weight:800 !important;
}
.thumbnailmsg {
	font-size: small !important;
	color:#8b7765 !important;
}



.abbrev {
        color:#8b7765 !important;
}
.highlight {
	background:#000000 !important;
	color:#000000 !important;
	border: 2px dashed #8b7765 !important;
}
.unkfunc {
	color: #789922 !important;
}
th {
	background-color:#eee0e5 !important;

}


}
