css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.pspgunz.org") {
body {
  background:url('http://hostmonkey.fr.nf/images/2014/08/28/index.png');  
}
#punwrap {
    background: none;
}  
.pun {
    background:none;
}
#brdmenu {
    background:#348EE6;
    color: #FFF;
    font-weight: bold;
}
DIV.box {
    border-color: #2A2A2A;
}  
DIV.box, DIV.codebox, #adminconsole FIELDSET TH, .rowodd, .roweven {
    background-color: #272727;
}  
.pun H2 {
    background:#348EE6;
    border-left: 1px solid #348EE6;
    border-right: 1px solid #348EE6;
} 
table{
  border-collapse: collapse;
  border:none;
}
.pun TD {
padding: 6px 3px;
border: 1px solid rgba(30, 30, 30, 1);
line-height: 20px;
}
TD.tc2, TD.tc3, TD.tc4, TD.tc5, TD.tcmod, #postpreview, #viewprofile DD, DIV.forminfo, #adminconsole FIELDSET TD, DIV.blockmenu DIV.box, #adstats DD,#box {
    background-color:#222222;
  
}  
.pun {
    color: #FFF;
}  
.pun TH {
    background-color: #494949;
}
.pun .tcl {
    border-left:solid 1px rgba(30, 30, 30, 1);
}  
.pun A:link, .pun A:visited {
    color: #4183C4;
    text-decoration: none;
}
.pun A:hover {
    color: #FFF;
}  
#brdfooter {
    background:transparent;
}
  DIV.box, .pun TD, .pun TH, .pun BLOCKQUOTE, DIV.codebox, DIV.forminfo, DIV.blockpost LABEL {
    border-style: none;
    border-width: 1px;
}
#brdwelcome{width:550px;margin-top:20px;margin-left:auto;margin-right:auto;}
#brdheader .box b,#brdheader .box img{display:none;}
  
input[type="submit"]{background-color:#348EE6;border:none;padding:5px 10px 5px 10px;color:white;}
.inbox{padding-bottom:10px;}

    DIV.blockpost DIV.box {
    border: none;
}
 DIV.blockpost DIV.box, DIV.postright, DIV.postfootright {
    background-color: transparent;
}   
DIV.postleft, DIV.postfootleft, DIV.blockpost LABEL {
    background-color: #222222;
}    
  DIV.box, DIV.codebox, #adminconsole FIELDSET TH, .rowodd, .roweven {
    background-color: #222222;
}  
    
DIV.blockpost H2 {
    background-color: #348EE6;
}
.pun BLOCKQUOTE {
    background-color: rgba(0, 0, 0, 0.22);
}    
#brdfooter .box .inbox .conr img{
    width:40px;
}   
#brdfooter .box .inbox .conr img:hover{
    width:40px;
    opacity: 0.5;
}     

DIV.icon {
    margin-left: 0.4em;
    width: 10px;
    float: left;
    display: block;
    height: 10px;
    margin-bottom: auto;
    margin-top: 7px;
}
DIV.inew {
    background: none repeat scroll 0% 0% rgba(62, 142, 168, 1);
}
DIV.icon {
    background: none repeat scroll 0% 0% rgba(17, 18, 27, 1);
} 
TR.isticky DIV.icon{
    background: none repeat scroll 0% 0% rgba(17,18,27,1);
}   
TR.isticky DIV.inew {
    background: none repeat scroll 0% 0% rgba(62, 142, 168, 1);
} 
DIV.postright, DIV.postfootright {
    border-left-color: #212121;
}
#punindex #brdstats div.inbox {
    background: url('http://img11.hostingpics.net/pics/436433iconstatistics.png') no-repeat scroll 0% 50% transparent;
    padding-left: 42px;
    margin-left: 10px;
}
.pun {
    padding-top: 10px;
    padding-left: 40px;
    padding-right: 40px;
    background-repeat:repeat;
}    
#qjump SELECT {
    width: 50%;
}
}
