css:
@-moz-document domain("dev.classmethod.jp") {

#contentsNavs, aside, .single_article_share_buttons, #globalFooter
{ display: none !important; }

.multiple_section .section.main {
  margin: 2em auto;
}

.multiple_section .section {
  float: none;
}

.single_article {
  float: none;
  margin: 2em auto;
}

.single_article_title {
  font-size: 175%;
}

.single_article_contents {
  padding: 20px 60px;
}

.editable_contents h2:first-child {
  margin: 0 -60px 0.5em -60px;
}

.editable_contents h2 {
  margin: 2.2em -60px 0.5em -60px;
  box-shadow: 0 0 1px #ddd;
  font-size: 140%;
}

.editable_contents p {
  font-size: 15px;
}

.syntaxhighlighter {
  margin: 0 -60px !important;
}
.syntaxhighlighter .line,
.syntaxhighlighter .line code {
  font-family: monospace !important;
  font-size: 13px !important;
}

}
