css:
@-moz-document domain("lenta.ru") {
body 
{
    background-color: #222 !important;
    color: #DDDDDD !important;
}

/* adFox section */

section.b-banner.b-banner-bottom.js-advert {
    display: none !important;
}

/* end of adFox section */

h1,h2,h3,h4,h5,h6
{
    color: #EEEEEE !important;
}

a.dark { 
    color: #CCCCCC !important; 
}

a { 
    color: #CCCCCC; 
}

div.decorator.icons-sprite.icons-active { background: none !important; }

.g-application {
    background-color: #222 !important;
    box-shadow: 0 0 15px #111111 !important;
}

.g-picture {
    border: none !important;
}

.b-tabloid .b-tabloid__more a:visited {
    color: #656565 !important;
}

h2.bordered-title a:visited {
    color: #656565 !important;
}

.b-yellow-box {
    background-color: #222 !important;
}

.b-yellow-box, .b-yellow-box__wrap {
    border: none !important;
}

.b-tags-list {
    background: none !important;
    border-bottom: 1px solid #ac2f2f !important;
}

.archive a { border-left: 1px solid #151515 !important; }

.b-yellow-box h2 {
    background-color: #222 !important;
}

.b-tabloid {
    background: none !important;
}

.b-footer { 
    background-color: #222222 !important; 
    box-shadow: 0 0 15px #111111 !important;
}

.b-topic__content {
    border-bottom: 1px solid #ac2f2f !important;
}

.b-topic__relation {
    background-color: #2f2f2f !important;
    border: 1px solid #ac2f2f !important;
}

.b-feature {
    border-bottom: 1px solid #ac2f2f !important;
}

.b-feature_article {
    background-color: #2f2f2f !important;
}

.b-tabloid__topic {
    border-bottom: 1px solid #353535 !important;
}

h2.bordered-title {
    border-bottom: 1px solid #ac2f2f !important;
}

.b-inline-topics-box h2 {
    background-color: #3f3f3f !important;
}

.b-inline-topics-box .b-inline-topics-box__wrap {
    border-left: 1px solid #3f3f3f !important;
    border-right: 1px solid #3f3f3f !important;
    border-bottom: 1px solid #3f3f3f !important;
}

.b-banner-sidebar-decorator {
    border: 1px solid #2f2f2f !important;
}

.item { 
   border-bottom: 1px solid #353535 !important; 
}

.b-tags-list .items .item {
   border-bottom: none !important;
}

.news { 
   border-bottom: 1px solid #353535 !important; 
}

.news-list { 
   border-top: 1px solid #ac2f2f !important; 
}

.b-tabloid {
    border-top: 1px solid #2f2f2f !important;
}

.b-button_more {
    border: 1px solid #6f6f6f !important;
}

.b-button_more:hover {
    -webkit-box-shadow: 0 0 5px #111111;
    box-shadow: 0 0 5px #111111;
    background-image: none !important;
    background-color: #4f4f4f !important;
}

.b-socials .comments {
    background: #444 !important;
    background-image: #444 !important;
    background-color: #444 !important;
    color: #ccc;
    border: 1px solid #656565;
}

.b-socials .comments :hover {
    color: #ac2f2f;
}

.b-ears .b-ears__item__wrap {
    border-left: 1px solid #333 !important;
}

.b-orphus {
    background-color: #333 !important;
    border: 1px solid #444 !important;
}

.b-orphus .header {
    color: #ddd !important;
    background-color: #555 !important;
}

.b-orphus form input[type="text"] {
    border: 1px solid #444;
    background: #222 !important;
    color: #ccc !important;
}

.b-orphus .buttons input {
    color: #ddd;
    background-color: #666 !important;
    border: 1px solid #666;
}

.b-orphus .buttons input[type="submit"]:hover {
    color: #000;
    background-color: #666 !important;
    border: 1px solid #666;
}

.icons-lenta_small {
      background: none !important;
      background-image: url(data:image/png;base64,) !important;
}
}
