css:
@-moz-document url-prefix("http://d.hatena.ne.jp/zariganitosh/") {

#pager-top
{ display: none !important; }

body {
  width: 1000px;
  margin: 0 auto;
  background: #fff;
  font-family: monospace;
  font-size: 16px;
}
h1 {
  width: auto;
}
div.main {
  margin: 0;
}
div.sidebar, div.sidebar2 {
  position: static;
  width: auto;
}
div.section {
  background-color: #fff;
}
div.body {
  border: 0;
}
div.body pre {
  margin: 0;
  padding: 1em;
  width: 98%;
  background: #f8fefc;
  border: 1px solid #e7edeb;
  border-left: 0;
  border-right: 0;
  font-family: monospace !important;
  font-size: 14px !important;
  line-height: 1.4;
}
div.day {
  border: 1px solid #ddd;
}
div.section li {
  font-size: inherit;
}
span[style="color:#999"] {
  text-shadow: 0 1px #fff;
}
h3 {
  margin: 2em 1em;
  padding: 0 0 1.5em;
  font: normal bold 2em sans-serif;
  background: none;
  color: #333;
  border: 0;
  text-indent: 0;
  text-align: center;
  border-bottom: 1px solid #ddd;
}
.body h3.title a {
  color: #333;
  padding-right: 0.5em;
}
h4 {
  margin: 3em 0 1.5em;
  padding: 10px 27px;
  font-size: 130%;
  color: #333;
  border: 0;
  border-left: solid 0.6em #ff9900;
  background: #f5f5f5;
}
h5 {
  margin: 2em 0 1em;
  padding: .5em;
  font-size: 120%;
  border-left: solid 0.4em #6699cc;
}

}
