css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("utdirect.utexas.edu") { 
    body {
      background-image: url('https://raw.githubusercontent.com/analytalica/SuperUTDirect/master/assets/whiteutblur4.png') !important;
      background-position: right bottom !important;
      background-color: #F7F7F7 !important;
      background-repeat:no-repeat !important;
      height: 100% !important;
    }
    #columnA {
      padding-left: 16% !important;
    }
    #columnB {
      padding-left: 2% !important;
      padding-right: 10% !important;
      border-left: 1px solid #A24000 !important;
    }
    #channelFloat {
      border: none !important;
    }
    #customize {
      margin-right: 9% !important;
      text-shadow: 2px 2px 2px #CCC !important;
      text-transform: lowercase !important;
    }
    #fullpage {
      margin: 20px 0px !important;
      width: 100% !important;
    }
    #mainnavtext {
      height: 44px !important;
    }
    #mainnavtext li {
      text-transform: uppercase !important;
    }
    #mainnavtext a {
      font-weight: normal !important;
    }
    .menu ul li {
      margin-top: 2px !important;
    }
    #navbar_ut_direct_logo {
      top: 135px !important;
    }
    #navbarFloat {
      width: 195px !important;
    }
    #navbarContainer {
      width: 195px !important;
      /*background-image: none !important;*/
      background: rgb(190,210,224); /* Old browsers */
      background: -moz-linear-gradient(top, rgba(190,210,224,1) 0%, rgba(247,247,247,1) 100%) !important; /* FF3.6+ */
      background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(190,210,224,1)), color-stop(100%,rgba(247,247,247,1))) !important; /* Chrome,Safari4+ */
      background: -webkit-linear-gradient(top, rgba(190,210,224,1) 0%,rgba(247,247,247,1) 100%) !important; /* Chrome10+,Safari5.1+ */
      background: -o-linear-gradient(top, rgba(190,210,224,1) 0%,rgba(247,247,247,1) 100%) !important; /* Opera 11.10+ */
      background: -ms-linear-gradient(top, rgba(190,210,224,1) 0%,rgba(247,247,247,1) 100%) !important; /* IE10+ */
      background: linear-gradient(to bottom, rgba(190,210,224,1) 0%,rgba(247,247,247,1) 100%) !important; /* W3C */
      background-size: 93% 300px !important;
      background-repeat: no-repeat !important;
    }
    #navbar_menu {
      padding: 40px 0px 10px 10px !important;
      text-align: center !important;
    }
    #navbar_menu a {
      font-size: 1em !important;
    }
    #navbar2 {
      margin-top: 20px !important;
    }
    #navbar1 {
      margin-top: 20px !important;
    }
    #navbar_bottom {
      visibility: hidden !important
    }
    #navbar_ut_direct_logo {
      background-image: url('https://raw.githubusercontent.com/analytalica/SuperUTDirect/master/assets/super_utdirect.png') !important;
    }
    .menu {
      background-image: url('https://raw.githubusercontent.com/analytalica/SuperUTDirect/master/assets/sidebar_divider.png') !important;
    }
    #breadcrumbs {
      font-size: 120% !important;
    }
    /* Custom Breadcrumb Styling */
    #breadcrumbs li.path_item, #breadcrumbs li.last_item{
      padding: 0px 2px 0px 0px !important;
    }
    /* Original breadcrumbs had >> trailing each item. */
    #breadcrumbs li.path_item:after {
      content: ">" !important;
      color: gray !important;
      font-weight: bold !important;
      font-family: "Arial Black", "Arial Bold", Gadget, sans-serif;
      /* padding: 0px 0px 4px 0px !important; */
    }
    h1 {
      margin-top: 0px !important;
    }
    #service_content img {
      margin: 0 3px 3px 0 !important;
    }
    #news_heading {
      font-size: 100% !important;
    }
    #news_body {
      padding: 1px 3px 3px 8px !important;
      font-size: 90%;
    }
    #contents {
      margin-left: 0% !important;
    }
    #utilities {
      margin-bottom: 0px !important;
    }
    table {
      border-collapse: collapse !important;
    }
    th {
      padding: 3px 8px 3px 8px !important;
      text-transform: uppercase !important;
    }
    td {
      padding: 3px 8px 3px 8px !important;
      vertical-align: middle !important;
      line-height: 20px !important;
    }
    #header_row {
      background-color: #FBBC95 !important;
    }
    #pgTitle {
      line-height: 200% !important;
    }
    div#eproxy {
      top: 12px !important;
      right: 13px !important;
    }
    .fineprnt {
      font-size: 100% !important;
    }
    hr {
      border: 0 !important;
      height: 1px !important;
      background: #333 !important;
      background-image: -webkit-linear-gradient(left, #ccc, #333, #ccc) !important;
      background-image: -moz-linear-gradient(left, #ccc, #333, #ccc) !important;
      background-image: -ms-linear-gradient(left, #ccc, #333, #ccc) !important;
      background-image: -o-linear-gradient(left, #ccc, #333, #ccc) !important;
    }
    #site_map span{
      height: 1px !important;
      background-color: #A13F00 !important;
      margin-left: 2% !important;
    }
}

@-moz-document url-prefix("https://utdirect.utexas.edu/registration/"),
         url-prefix("https://utdirect.utexas.edu/acct/rec/autopay/"),
         url-prefix("https://utdirect.utexas.edu/acct/rec/wio/") { 
    th {
      background-color: #FBBC95 !important;
    }
    form {
      /*margin-top: -20px !important;*/
    }
    tbody tr:nth-child(even) {
      background-color: rgba(255, 255, 255, 0.8) !important;
    }
    tbody tr:nth-child(odd) {
      background-color: rgba(230, 230, 230, 0.8) !important;
    }
    br {
      line-height: 5px !important;
    }
}

@-moz-document url-prefix("https://utdirect.utexas.edu/bevobucks/"),
         url-prefix("https://utdirect.utexas.edu/hfis/proxyMenu.WBX"),
         url-prefix("https://utdirect.utexas.edu/acct/rec/weft/"),
         url-prefix("https://utdirect.utexas.edu/acct/checks/"){
    #breadcrumbs {
      font-size: 120% !important;
      padding-bottom: 0px !important;
      margin-bottom: 28px !important;
    }
}

@-moz-document url("https://utdirect.utexas.edu/utdirect/index.WBX"){ 
    #pgTitle {
      margin-top: 2px;
      margin-left: -105px;
      margin-bottom: 12px;
    }
    th {
      padding: 0px 0px 0px 0px !important;
      text-transform: uppercase !important;
    }
    td {
      padding: 0px 0px 0px 0px !important;
      vertical-align: middle !important;
      line-height: 20px !important;
    }
    tbody tr:nth-child(even) {
      background-color: rgba(255, 255, 255, 0.8) !important;
    }
    tbody tr:nth-child(odd) {
      background-color: rgba(230, 230, 230, 0.8) !important;
    }
}
