css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("livejournal.com") {
body.logged-in {
       margin-left: -37px !important;
       color: #f0f0f0 !important;
       background: #3F4467 !important; }
#big-content-wrapper { 
       width: 95% !important; 
       margin-top: -110px !important;
       margin-bottom: -225px !important; 
       margin-left: auto !important;
       margin-right: auto !important;
       padding-left: 30px !important; }
#big-content-wrapper,
#content, TD, #options, #submitbar {
       background: #3F4467 !important;
       color: #f0f0f0 !important;
       font-family: "Trebuchet MS", Trebuchet, Arial,
               Helvetica, sans-serif !important; }
#content img {
       border: 0 !important;
       opacity: 0.80 !important; }
#content img:hover {
       opacity: 0.95 !important; }
#content a:link,
#content a:active,
#content a:visited {
       color: #a3bdc8 !important;
       text-decoration: none !important; }
#content a:hover {
       color: #ffffff !important; }
.container {
       border: 2px solid #1D2245 !important;
       background: #4D5480 !important;
       padding: 0 !important; }
#content-wrapper > P:first-child + TABLE + DIV > TABLE > TBODY > TR > TD {
       padding: 0 !important; }
#content-wrapper > DIV > FONT > I > B {
       color: #dbdbdb !important;
       font-weight: bold !important;
       letter-spacing: 1.5px !important;
       text-transform: lowercase !important;
       display: block !important; }
#content-wrapper > P:first-child + TABLE + DIV + DIV {
       padding: 1em 1em 1em 0 !important; }
#content-wrapper,
#Comments {
       background: #3F4467
               url(http://timepiece.nfshost.com/images/bluegradient.jpg)
               repeat-x !important;
       border-top: 2px solid #1D2245 !important;
       color: #f0f0f0 !important;
       margin-bottom: 1em !important;
       margin-top: 2em !important;
       min-height: 300px !important;
       padding: 0 !important ;
       width: 100% !important; }
blockquote {
       background: #4D5480 !important;
       color: #f0f0f0 !important;
       text-indent: 0 !important;
       width: 80% !important;
       margin-left: auto !important;
       margin-right: auto !important;
       padding: 1em !important; }
table, tbody, tr, td {
       background-color: transparent !important; }
#Comments * TABLE > TBODY > TR:last-child > TD > P {
       border-top: thin solid #808080 !important; }
HR,
.push,
#navigation,
#footer, 
#Logo { display: none !important; }
}
