@-moz-document domain("neopets.com") {
/********************************************************************/
/* HTML Tag Styles **************************************************/
/********************************************************************/

	BODY {
		background-color: #D2CCC4;
	}

	A:Link, A:Visited {
		color: #A72323;
	}

	A:Hover {
		color: #D61111;
	}

/********************************************************************/
/* Foundation IDs ***************************************************/
/********************************************************************/

	#main {
		border-left: 2px solid #747474;
		border-right: 2px solid #747474;
	}

	#header {
		background-color: #7D7664;
		background-image: url("http://images.neopets.com/themes/033_swy_82090/header_bg.png");
	}

	#footer {
		background-color: #7D7664;
		background-image: url("http://images.neopets.com/themes/033_swy_82090/footer_bg.png");
	}


/********************************************************************/
/* Header Stuff *****************************************************/
/********************************************************************/

/* Navigation.  Duh.  */
	ul.dropdown {
		border: 1px solid #635949;
		background-color: #262127;
	}

	LI:hover UL.dropdown A, LI.over UL.dropdown A {
		color: #FFFFFF;
	}

	LI:hover UL.dropdown A:Hover, LI.over UL.dropdown A:hover {
		color: #FDE224;
	}

/* Event Icons */
	.eventIcon, .user {
		color: #FFFFFF;
	}

	.eventIcon A:Link, .eventIcon A:Hover, .eventIcon A:Visited,
	.user A:Link, .user A:Hover, .user A:Visited {
		color: #fde224;
	}

/* Clock */
	#nst {
		color: #FFFFFF;
	}


/********************************************************************/
/* Sidebar Classes **************************************************/
/********************************************************************/

	.sidebarModule {
		border: 2px solid #E4E4E4;
	}

	.sidebarTable {
		border: 2px solid #C9C9C9;
	}

	.sidebarHeader {
		background-color: #8E7F92;
		border-bottom: 2px solid #C9C9C9;
	}

	.sidebarHeader A:Link, .sidebarHeader A:Hover, .sidebarHeader A:Visited {
		color: #FDE224;
	}


/********************************************************************/
/* Content Classes **************************************************/
/********************************************************************/

/* We need a few styles to make a module work */
	.contentModuleHeader {
		background-color: #000000;
		color: #FFFFFF;
	}

	.contentModuleHeader A:Link, .contentModuleHeader A:Hover, .contentModuleHeader A:Visited {
		color: #C5C5C5;
	}

	.moreLink {
		border: 1px solid #c9c9c9;
	}

	.contentModuleHeaderAlt {
		background-color: #262127;
		color: #FFFFFF;
	}

	.contentModuleHeaderAlt A:Link, .contentModuleHeaderAlt A:Hover, .contentModuleHeaderAlt A:Visited {
		color: #ebe7e3;
	}

	.moreLinkAlt {
		border: 1px solid #988974;
	}

/********************************************************************/
/* Footer Classes ***************************************************/
/********************************************************************/

	.footerForm {
		color: #FFFFFF;
	}

	.copyright, .copyright A:Link, .copyright A:Hover, .copyright A:Visited {
		color: #FFFFFF;
	}


/********************************************************************/
/* Misc Classes of Doom *********************************************/
/********************************************************************/

/* Ad banners.  No comment */
	#ban {
		background-color: #7D7664;
		background-image: url("http://images.neopets.com/themes/033_swy_82090/banner_bg.png");
	}

	.marqueeSlot, .marqueeSlot A:Link, .marqueeSlot A:Hover, .marqueeSlot A:Visited {
		background-color: #423A44;
		color: #FFFFFF;
	}

	.marqueeSlotOn, .marqueeSlotOn A:Link, .marqueeSlotOn A:Hover, .marqueeSlotOn A:Visited {
		background-color: #262127;
		color: #FFFFFF;
	}
	
/********************************************************************/
/* MAMABAR Classes **************************************************/
/********************************************************************/

ul.brand-mamabar-list a {
	display: block;
	line-height: 18px;
	color: #9a9a9a;
	font-size: 11px;
	text-decoration: none;
	font-weight: bold;
	font-family: Arial, Helvetica, sans-serif;
}

ul.brand-mamabar-list a:hover { text-decoration: underline; }

/* ACTIVE STATE */
ul.brand-mamabar-list a.brand-mamabar-active {
	color: #5D516D;
	cursor: default;
}

.footerNifty {	visibility: hidden }
}
