css:
@-moz-document domain("www.trademe.co.nz") {
body,
.page-body,
.sat-nav,
.header-container-background,
.mega-stripe.with-grey-bg,
.dealer-profile-yellow-box,
.mp-listing .seller-box,
.j-seller-details {
    background: #000;
}
a:visited {
    color: #b7359d;
}

/* -- Home Page -- */
.recent-searches-header,
.carousel-header,
.logged-in,
.carousel-header-left.drop-container .drop-label {
    color: #ccc;
}

/* -- Register Page -- */
.heading.registration-heading,
.rego-business-account-text {
    color: #ccc;
}

/* -- My Trade Me Page -- */
.mtm-accounts-row,
.j-create-profile-my-trade-me .j-create-profile-my-trade-me__title,
.j-create-profile-my-trade-me__details {
    color: #ccc;
}
.account-container .balance {
    color: #fff;
}

/* -- Listings -- */
.title-content,
.property-title-container .property-title h1,
.listingBreadCrumbs,
.ListingAttributes,
.ListingDescription,
.mp-listing,
.mp-listing .listing-attribute-header,
.key-details-attribute-value {
    color: #ccc;
}
#ClassifiedActions_Expires,
#DetailsFooterOptions {
    color: #777 !important;
}

/* -- Motors Listings -- */
.motors-listing .listing-attribute-header,
.motors-attribute-value {
    color: #ccc;
}

/* -- Property Listings -- */
.property-schools-list .title,
.property-schools-list .school-item .school-name,
.property-schools-list .school-item .school-distance {
    color: #999 !important;
}
.pi-content,
.mortgage-calculator-container h3,
.property-area-map-text,
.property-schools-list,
#QVSalesBox_QvSalesAndReports,
#QVSalesBox_QvSalesAndReports h2 {
    color: #777 !important;
}

/* -- Job Listings -- */
.j-title h1,
.j-attributes {
    color: #888 !important;
}
.attribute-label.attribute-font {
    color: #555 !important;
}
}
