css:
@-moz-document url-prefix(http://www.tumblr.com/dashboard), url-prefix(https://www.tumblr.com/dashboard){

body { 
  background:#333 url(http://31.media.tumblr.com/tumblr_madb6gLnJt1qh5mm9o1_500.jpg) left top repeat !important; 
  font:normal 13px/1.3, "#FFFFFF","Open Sans","Helvetica Neue","HelveticaNeue",Helvetica,Arial,sans-serif; 
}

/* Remove rounded border around content and blue backgrounds */
.l-container.l-container--two-column .l-content, 
.l-container.l-container--two-column-dashboard .l-content,
.l-container.l-container--two-column-dashboard .left_column,
.l-container.l-container--two-column .right_column, 
.l-container.l-container--two-column-dashboard .right_column { 
  background:none; 
}

/* Search box */
.search_form_row {
  background:none;
  border:1px solid #555;
  border:2px solid rgba(255,255,255,.13);
}

/* Hide Primary Nav Nipple */
.l-header .selected .selection_nipple {
  display:none;
}

/* Link colours */
#dashboard_controls a,
#dashboard_controls .control,
.footer_links a,
#pagination a {
  color:#FFFFFF;
  color:rgba(255,255,255,.6);
}

#dashboard_controls a:hover,
#dashboard_controls .control:hover,
.footer_links a:hover,
#pagination a:hover {
  color:#fff;
  color:rgba(255,255,255,1);
}

/* Post: Hover Top-Right Corner for Source */
.post_full .post_permalink:before {
  border-right:16px solid #333;
}

/* Hide Recommended Blogs and Radar */
.recommended_tumblelogs,
.controls_section_radar,
#tumblr_radar {
  display:none;

}
#popover_blogs .tab_notice:after{display:none}div.l-header .tab.iconic > a {background-image:url('http://i47.tinypic.com/2s7tll2.png') !important;}

}
