css:
@-moz-document domain("twitch.tv") {
  
.colpick{
	font-family:"Comic Sans MS", cursive, sans-serif;
}

.colpick_field input{
	font-family:"Comic Sans MS", cursive, sans-serif;
}

.colpick_hex_field input{
	font-family:"Comic Sans MS", cursive, sans-serif;
}

body{
	font:12px "Comic Sans MS", cursive, sans-serif;
}

code,samp,kbd{
	font-family:"Comic Sans MS", cursive, sans-serif;
}

*:first-child + html button[type].button{
	font-family:"Comic Sans MS", cursive, sans-serif;
}

.no-login-contain h3{
	font-family:"Comic Sans MS", cursive, sans-serif;
}

#broadcast_meta h1#channel_login,#main_col .content #broadcast_meta .info #channel_login.title,#broadcast_meta #channel .player-column #broadcast-meta .info #channel_login.title,#channel .player-column #broadcast-meta .info #broadcast_meta #channel_login.title{
